1269 
1270                 } else {
1271                     Connector.ReturnMessages.Add("Unable to Execute the DocumentFiles Request : " + Common.GetErrorInfo(ex));
1272                     Connector.hasError = true;  //throws exception
1273                     return false;
1274                 }
1275             } else {
1276                 Connector.ReturnMessages.Add("Unable to Execute the DocumentFiles Request : " + Common.GetErrorInfo(ex));
1277                 Connector.hasError = true;  //throws exception
1278                 return false;
1279             }
1280         }
1281     }
