1283     protected internal bool ExecuteAllDocumentsRequest(string WebServiceURL, string searchField, string searchValue, string formula, string unids, DatabaseObject dbObj) {
1284         HttpWebRequest request = null;
1285 
1286         try {
1287             if (!isInitialized) {
1288                 Connector.ReturnMessages.Add("Unable to execute the All Documents Request - Not Initialized/Connected To Server");
1289                 Connector.hasError = true;
1290                 return false;
1291             }
1292 
1293             //  ServicePointManager.Expect100Continue = false;
1294 
1295             //create an HTTP request
1296 
1297             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllDocuments");
1298             //request.Timeout = 300000; // set timeout to 5minutes
1299             request.Timeout = 1800000; //half an hour!
1300             request.CookieContainer = this.AuthenticationCookie;
1301             //  request.KeepAlive = True
1302             request.AllowAutoRedirect = true;
1303 
1304             request.Method = WebRequestMethods.Http.Post;   //"POST"
1305             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1306                                                                     // Set the request stream
1307             string result;
1308 
1309             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1310             if (!AddIdentityHeader(ref request)) {
1311                 Connector.ReturnMessages.Add("Unable to Execute All Documents Request : Identity Header could not be added!");
1312                 Connector.hasError = true;  //throws error
1313                 return false;
1314             }
1315 
1316             //test with encryption
1317             StringBuilder sb = new StringBuilder();
1318 
1319             sb.AppendLine("UserName: " + Connection.UserName);
1320             sb.AppendLine("ServerName: " + dbObj.ServerName);
1321             sb.AppendLine("FilePath: " + dbObj.FilePath);
1322             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
1323             sb.AppendLine("SearchField: " + searchField);
1324             sb.AppendLine("SearchValue: " + searchValue);
1325             sb.AppendLine("Formula: " + formula);
1326             sb.AppendLine("Unids: " + unids);
1327             sb.AppendLine("Connecting via XPagesAPI");
1328 
1329             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1330             string myString = Encoding.UTF8.GetString(b);
1331             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1332             if (EncodedEncryptedContent.Initialize()) {
1333                 Stream requestStream = null;
1334                 try {
1335                     requestStream = request.GetRequestStream();
1336                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
1337                         writer.Write(EncodedEncryptedContent.EncodedContent);
1338                     }
1339                 } catch (Exception e) {
1340                     Connector.ReturnMessages.Add("Unable to execute the All Documents Request : " + Common.GetErrorInfo(e));
1341                     Connector.hasError = true;  //throws exception
1342                     return false;
1343                 } finally {
1344                     //if (requestStream != null) {
1345                     //    requestStream.Dispose();
1346                     //}
1347                 }
1348             } else {
1349                 Connector.ReturnMessages.Add("Executing All Documents Request - Unable to encode/encrypt the content of the request!");
1350                 Connector.hasError = true; //throws exception
1351                 return false;
1352             }
1353 
1354             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1355 
1356             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
1357                 Stream responseStream = null;
1358                 try {
1359                     responseStream = request.GetResponse().GetResponseStream();
1360                     using (StreamReader reader = new StreamReader(responseStream, true)) {
1361                         result = reader.ReadToEnd();
1362                     }
1363                 } catch (Exception e) {
1364                     Connector.ReturnMessages.Add("Unable to execute the All Documents Request : " + Common.GetErrorInfo(e));
1365                     Connector.hasError = true;  //throws exception
1366                     return false;
1367                 } finally {
1368                     //if (responseStream != null) {
1369                     //    responseStream.Dispose();
1370                     //}
1371                 }
1372 
1373                 if (result != null) {
1374                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1375                     if (DecodeDecryptedContent.Initialize()) {
1376                         ArrayList arList = new ArrayList();
1377 
1378                         string[] ar = null;
1379                         bool isError = false;
1380 
1381                         string message = "";
1382                         string details = "";
1383                         string value = "";
1384                         string[] sep = { "||" };
1385                         string[] NoteIds = { };
1386                         string[] Unids = { };
1387                         string[] Forms = { };
1388                         string[] Sizes = { };
1389                         string[] URLs = { };
1390                         string[] Created = { };
1391                         string[] Modified = { };
1392 
1393                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1394                         arList.AddRange(ar);
1395                         foreach (string str in arList) {
1396                             //YES/NO
1397                             if (str.StartsWith("Error: ")) {
1398                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
1399                                     isError = true;
1400                                 }
1401                             }
1402                             if (str.StartsWith("Message: ")) {
1403                                 message = str.Replace("Message: ", "");
1404                             }
1405                             if (str.StartsWith("Details: ")) {
1406                                 details = str.Replace("Details: ", "");
1407                             }
1408 
1409                             if (str.StartsWith("Unids: ")) {
1410                                 value = str.Replace("Unids: ", "");
1411                                 if (!string.IsNullOrEmpty(value)) {
1412                                     Unids = value.Split(sep, StringSplitOptions.None);
1413                                 }
1414                             }
1415 
1416                             if (str.StartsWith("NoteIDs: ")) {
1417                                 value = str.Replace("NoteIDs: ", "");
1418                                 if (!string.IsNullOrEmpty(value)) {
1419                                     NoteIds = value.Split(sep, StringSplitOptions.None);
1420                                 }
1421                             }
1422 
1423                             if (str.StartsWith("Forms: ")) {
1424                                 value = str.Replace("Forms: ", "");
1425                                 if (!string.IsNullOrEmpty(value)) {
1426                                     Forms = value.Split(sep, StringSplitOptions.None);
1427                                 }
1428                             }
1429 
1430                             if (str.StartsWith("Sizes: ")) {
1431                                 value = str.Replace("Sizes: ", "");
1432                                 if (!string.IsNullOrEmpty(value)) {
1433                                     Sizes = value.Split(sep, StringSplitOptions.None);
1434                                 }
1435                             }
1436 
1437                             if (str.StartsWith("URLs: ")) {
1438                                 value = str.Replace("URLs: ", "");
1439                                 if (!string.IsNullOrEmpty(value)) {
1440                                     URLs = value.Split(sep, StringSplitOptions.None);
1441                                 }
1442                             }
1443 
1444 
1445                             if (str.StartsWith("Created: ")) {
1446                                 value = str.Replace("Created: ", "");
1447                                 if (!string.IsNullOrEmpty(value)) {
1448                                     Created = value.Split(sep, StringSplitOptions.None);
1449                                 }
1450                             }
1451 
1452 
1453                             if (str.StartsWith("Modified: ")) {
1454                                 value = str.Replace("Modified: ", "");
1455                                 if (!string.IsNullOrEmpty(value)) {
1456                                     Modified = value.Split(sep, StringSplitOptions.None);
1457                                 }
1458                             }
1459                         }
1460 
1461                         //loop through unids, notesids, forms, sizes, urls and create document objects
1462                         // check if all have the same number of items!
1463                         int count = 0;
1464                         if (Unids != null && Unids.Length > 0) {
1465                             count = Unids.Length;
1466                             if (NoteIds != null && NoteIds.Length > 0) {
1467                                 if (count != NoteIds.Length) {
1468                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> NoteIds");
1469                                     Connector.hasError = true;  //throws exception
1470                                     return false;
1471                                 } else {
1472                                     // form can be empty!!
1473                                     //if (Forms != null && Forms.Length > 0) {
1474                                     //    if (count != Forms.Length) {
1475                                     //        Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Forms");
1476                                     //        Connector.hasError = true;  //throws exception
1477                                     //        return false;
1478                                     //    } else {
1479                                     if (Sizes != null && Sizes.Length > 0) {
1480                                         if (count != Sizes.Length) {
1481                                             Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Sizes");
1482                                             Connector.hasError = true;  //throws exception
1483                                             return false;
1484                                         } else {
1485                                             if (URLs != null && URLs.Length > 0) {
1486                                                 if (count != URLs.Length) {
1487                                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> URLs");
1488                                                     Connector.hasError = true;  //throws exception
1489                                                     return false;
1490                                                 }
1491                                             }
1492                                         }
1493                                     }
1494                                     //   }
1495                                     // }
1496                                 }
1497                             }
1498                             // loop through and create doc obj
1499                             DocumentObject docObj = null;
1500                             if (dbObj.Documents == null) {
1501                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
1502                             }
1503                             for (int i = 0; i < Unids.Length; i++) {
1504                                 docObj = new DocumentObject(dbObj, Unids[i]);
1505                                 if (NoteIds.Length - 1 >= i) {
1506                                     docObj.NoteID = NoteIds[i];
1507                                 }
1508                                 if (Forms.Length - 1 >= i) {
1509                                     docObj.Form = Forms[i];
1510                                 }
1511                                 if (Sizes.Length - 1 >= i) {
1512                                     docObj.Size = Sizes[i];
1513                                 }
1514                                 if (URLs.Length - 1 >= i) {
1515                                     docObj.Url = URLs[i];
1516                                 }
1517                                 if (Created.Length - 1 >= i) {
1518                                     docObj.DateCreated = Created[i];
1519                                 }
1520                                 if (Modified.Length - 1 >= i) {
1521                                     docObj.DateModified = Modified[i];
1522                                 }
1523                                 if (dbObj.Documents.ContainsKey(Unids[i])) {
1524                                     dbObj.Documents.Remove(Unids[i]);
1525                                 }
1526                                 docObj.IsInitialized = true;
1527                                 dbObj.Documents.Add(Unids[i], docObj);
1528                             }
1529                         }
1530 
1531 
1532 
1533                         if (isError) {
1534                             Connector.ReturnMessages.Add(message);
1535                             Connector.ReturnMessages.Add(details);
1536                             Connector.hasError = true; //throws exception
1537                             return false;
1538                         } else {
1539                             Connector.ReturnMessages.Add(message);
1540                             Connector.ReturnMessages.Add(details);
1541                             Connector.hasError = false; //SUCCESS
1542                         }
1543 
1544                     } else {
1545                         Connector.ReturnMessages.Add("Executing All Documents Request - Unable to decode/decrypt the content of the response!");
1546                         Connector.hasError = true;  //throws exception
1547                         return false;
1548                     }
1549                 } else {
1550                     //no response from server
1551                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get response from XPages!");
1552                     Connector.hasError = true;  //throws exception
1553                     return false;
1554 
1555                 }
1556                 return true;
1557             } else {
1558                 Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Document - Authentication Issue");
1559                 Connector.hasError = true;  //throws exception
1560                 return false;
1561             }
1562 
1563         } catch (Exception ex) {
1564             if (ex.GetType() == typeof(WebException)) {
1565                 WebException webex;
1566                 webex = (WebException)ex;
1567                 if (webex.Response != null) {
1568                     Stream stream = webex.Response.GetResponseStream();
1569                     using (StreamReader reader = new StreamReader(stream)) {
1570                         string errorResp = reader.ReadToEnd();
1571                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1572                         Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1573                         Connector.hasError = true;  //throws exception
1574                         return false;
1575                     }
1576 
1577                 } else {
1578                     Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1579                     Connector.hasError = true;  //throws exception
1580                     return false;
1581                 }
1582             } else {
1583                 Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1584                 Connector.hasError = true;  //throws exception
1585                 return false;
1586             }
1587         }
1588     }
