1846                                         if (!string.IsNullOrEmpty(str) && !str.Contains("<NO_FILES_ATTACHED>")) {
1847                                             //can be valid file here split on  and count
1848                                             //fObj.Application + "" + fObj.Creator + "" + fObj.DateCreated + "" + fObj.DateModfied + "$" + fObj.FieldName + "" + fObj.FileExtension + "" + fObj.FileName + "" + fObj.FileSize + "" + fObj.LinkToFile + "" + fObj.Other + "" + fObj.SoftClass);
1849                                             fObj = new FileObject(docObj);
1850                                             if (fObj.Initialize(str)) {
1851                                                 docObj.Files.Add(fObj.Name, fObj);
1852                                             }
1853                                         }
1854                                     }
1855                                 }
1856                                 if (dbObj.Documents.ContainsKey(Unids[i])) {
1857                                     dbObj.Documents.Remove(Unids[i]);
1858                                 }
1859                                 docObj.IsInitialized = true;
1860                                 dbObj.Documents.Add(Unids[i], docObj);
1861                             }
1862                         }
1863 
1864 
1865 
1866                         if (isError) {
1867                             Connector.ReturnMessages.Add(message);
1868                             Connector.ReturnMessages.Add(details);
1869                             Connector.hasError = true; //throws exception
1870                             return false;
1871                         } else {
1872                             Connector.ReturnMessages.Add(message);
1873                             Connector.ReturnMessages.Add(details);
1874                             Connector.hasError = false; //SUCCESS
1875                         }
1876 
1877                     } else {
1878                         Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request - Unable to decode/decrypt the content of the response!");
1879                         Connector.hasError = true;  //throws exception
1880                         return false;
1881                     }
1882                 } else {
1883                     //no response from server
1884                     Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get response from XPages!");
1885                     Connector.hasError = true;  //throws exception
1886                     return false;
1887 
1888                 }
1889                 return true;
1890             } else {
1891                 Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get the Document - Authentication Issue");
1892                 Connector.hasError = true;  //throws exception
1893                 return false;
1894             }
1895 
1896         } catch (Exception ex) {
1897             if (ex.GetType() == typeof(WebException)) {
1898                 WebException webex;
1899                 webex = (WebException)ex;
1900                 if (webex.Response != null) {
1901                     Stream stream = webex.Response.GetResponseStream();
1902                     using (StreamReader reader = new StreamReader(stream)) {
1903                         string errorResp = reader.ReadToEnd();
1904                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1905                         Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1906                         Connector.hasError = true;  //throws exception
1907                         return false;
1908                     }
1909 
1910                 } else {
1911                     Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
1912                     Connector.hasError = true;  //throws exception
1913                     return false;
1914                 }
1915             } else {
1916                 Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
1917                 Connector.hasError = true;  //throws exception
1918                 return false;
1919             }
1920         }
