1923     protected internal bool ExecuteFieldsRequest(string WebServiceURL, DocumentObject docObj, string fields) {
1924         HttpWebRequest request = null;
1925 
1926         try {
1927             if (!isInitialized) {
1928                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
1929                 Connector.hasError = true;
1930                 return false;
1931             }
1932 
1933             // ServicePointManager.Expect100Continue = false;
1934 
1935             //create an HTTP request
1936 
1937             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Fields");
1938 
1939             request.CookieContainer = this.AuthenticationCookie;
1940             //  request.KeepAlive = True
1941             request.AllowAutoRedirect = true;
1942 
1943             request.Method = WebRequestMethods.Http.Post;   //"POST"
1944             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1945                                                                     // Set the request stream
1946             string result;
1947 
1948             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1949             if (!AddIdentityHeader(ref request)) {
1950                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
1951                 Connector.hasError = true;  //throws error
1952                 return false;
1953             }
1954 
1955             //test with encryption
1956             StringBuilder sb = new StringBuilder();
1957 
1958             sb.AppendLine("UserName: " + Connection.UserName);
1959             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
1960             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
1961             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
1962             sb.AppendLine("UniversalID: " + docObj.UniversalID);
1963             sb.AppendLine("Fields: " + fields);
1964             sb.AppendLine("Connecting via XPagesAPI");
1965 
1966             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1967             string myString = Encoding.UTF8.GetString(b);
1968             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1969             if (EncodedEncryptedContent.Initialize()) {
1970                 Stream requestStream = null;
1971                 try {
1972                     requestStream = request.GetRequestStream();
1973                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
1974                         writer.Write(EncodedEncryptedContent.EncodedContent);
1975                     }
1976                 } catch (Exception e) {
1977                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
1978                     Connector.hasError = true;  //throws exception
1979                     return false;
1980                 } finally {
1981                     //if (requestStream != null) {
1982                     //    requestStream.Dispose();
1983                     //}
1984                 }
1985             } else {
1986                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
1987                 Connector.hasError = true; //throws exception
1988                 return false;
1989             }
1990 
1991             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1992 
1993             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
1994 
1995                 Stream responseStream = null;
1996                 try {
1997                     responseStream = request.GetResponse().GetResponseStream();
1998                     using (StreamReader reader = new StreamReader(responseStream, true)) {
1999                         result = reader.ReadToEnd();
2000                     }
2001                 } catch (Exception e) {
2002                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2003                     Connector.hasError = true;  //throws exception
2004                     return false;
2005                 } finally {
2006                     //if (responseStream != null) {
2007                     //    responseStream.Dispose();
2008                     //}
2009                 }
2010 
2011                 if (result != null) {
2012                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2013                     if (DecodeDecryptedContent.Initialize()) {
2014                         ArrayList arList = new ArrayList();
2015 
2016                         string[] ar = null;
2017                         bool isError = false;
2018 
2019                         string message = "";
2020                         string details = "";
2021                         string value = "";
2022                         string[] fieldnames = null;
2023                         string[] fieldvalues = null;
2024                         string[] fieldtypes = null;
2025 
2026                         string[] sep = { "||" };
2027 
2028                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2029                         arList.AddRange(ar);
2030                         foreach (string str in arList) {
2031                             //YES/NO
2032                             if (str.StartsWith("Error: ")) {
2033                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
2034                                     isError = true;
2035                                 }
2036                             }
2037                             if (str.StartsWith("Message: ")) {
2038                                 message = str.Replace("Message: ", "");
2039                             }
2040                             if (str.StartsWith("Details: ")) {
2041                                 details = str.Replace("Details: ", "");
2042                             }
2043 
2044                             if (str.StartsWith("FieldNames: ")) {
2045                                 value = str.Replace("FieldNames: ", "");
2046                                 if (!string.IsNullOrEmpty(value)) {
2047                                     fieldnames = value.Split(sep, StringSplitOptions.None);
2048                                 }
2049                             }
2050 
2051                             if (str.StartsWith("FieldValues: ")) {
2052                                 value = str.Replace("FieldValues: ", "");
2053                                 if (!string.IsNullOrEmpty(value)) {
2054                                     fieldvalues = value.Split(sep, StringSplitOptions.None);
2055                                 }
2056                             }
2057 
2058                             if (str.StartsWith("FieldTypes: ")) {
2059                                 value = str.Replace("FieldTypes: ", "");
2060                                 if (!string.IsNullOrEmpty(value)) {
2061                                     fieldtypes = value.Split(sep, StringSplitOptions.None);
2062                                 }
2063                             }
2064 
2065                         }
2066                         FieldObject fObj = null;
2067                         if (docObj.Fields == null) {
2068                             docObj.Fields = new SortedDictionary<string, FieldObject>();
2069                         }
2070                         for (int i = 0; i < fieldnames.Length; i++) {
2071                             fObj = new FieldObject(fieldnames[i]);
2072                             if (fieldvalues.Length - 1 >= i) {
2073                                 fObj.Value = fieldvalues[i];
2074                             }
2075                             if (fieldtypes.Length - 1 >= i) {
2076                                 fObj.Type = fieldtypes[i];
2077                             }
2078 
2079                             if (docObj.Fields.ContainsKey(fObj.Name)) {
2080                                 docObj.Fields.Remove(fObj.Name);
2081                             }
2082                             docObj.Fields.Add(fObj.Name, fObj);
2083 
2084                         }
2085 
2086                         if (isError) {
2087                             Connector.ReturnMessages.Add(message);
2088                             Connector.ReturnMessages.Add(details);
2089                             Connector.hasError = true; //throws exception
2090                             return false;
2091                         } else {
2092                             Connector.ReturnMessages.Add(message);
2093                             Connector.ReturnMessages.Add(details);
2094                             Connector.hasError = false; //SUCCESS
2095                         }
2096 
2097                     } else {
2098                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
2099                         Connector.hasError = true;  //throws exception
2100                         return false;
2101                     }
2102                 } else {
2103                     //no response from server
2104                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
2105                     Connector.hasError = true;  //throws exception
2106                     return false;
2107 
2108                 }
2109                 return true;
2110             } else {
2111                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
2112                 Connector.hasError = true;  //throws exception
2113                 return false;
2114             }
2115 
2116         } catch (Exception ex) {
2117             if (ex.GetType() == typeof(WebException)) {
2118                 WebException webex;
2119                 webex = (WebException)ex;
2120                 if (webex.Response != null) {
2121                     Stream stream = webex.Response.GetResponseStream();
2122                     using (StreamReader reader = new StreamReader(stream)) {
2123                         string errorResp = reader.ReadToEnd();
2124                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2125                         Connector.ReturnMessages.Add("Unable to Execute the Fields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2126                         Connector.hasError = true;  //throws exception
2127                         return false;
2128                     }
2129 
2130                 } else {
2131                     Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2132                     Connector.hasError = true;  //throws exception
2133                     return false;
2134                 }
2135             } else {
2136                 Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2137                 Connector.hasError = true;  //throws exception
2138                 return false;
2139             }
2140         }
