2429                         } else {
2430                             Connector.ReturnMessages.Add(message);
2431                             Connector.ReturnMessages.Add(details);
2432                             Connector.hasError = false; //SUCCESS
2433                         }
2434 
2435                     } else {
2436                         Connector.ReturnMessages.Add("Executing All Files Request - Unable to decode/decrypt the content of the response!");
2437                         Connector.hasError = true;  //throws exception
2438                         return false;
2439                     }
2440                 } else {
2441                     //no response from server
2442                     Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get response from XPages!");
2443                     Connector.hasError = true;  //throws exception
2444                     return false;
2445 
2446                 }
2447                 return true;
2448             } else {
2449                 Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get the Document - Authentication Issue");
2450                 Connector.hasError = true;  //throws exception
2451                 return false;
2452             }
2453 
2454         } catch (Exception ex) {
2455             if (ex.GetType() == typeof(WebException)) {
2456                 WebException webex;
2457                 webex = (WebException)ex;
2458                 if (webex.Response != null) {
2459                     Stream stream = webex.Response.GetResponseStream();
2460                     using (StreamReader reader = new StreamReader(stream)) {
2461                         string errorResp = reader.ReadToEnd();
2462                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2463                         Connector.ReturnMessages.Add("Unable to Execute the All Files Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2464                         Connector.hasError = true;  //throws exception
2465                         return false;
2466                     }
2467 
2468                 } else {
2469                     Connector.ReturnMessages.Add("Unable to Execute the All Files Request : " + Common.GetErrorInfo(ex));
2470                     Connector.hasError = true;  //throws exception
2471                     return false;
2472                 }
2473             } else {
2474                 Connector.ReturnMessages.Add("Unable to Execute the All Files Request : " + Common.GetErrorInfo(ex));
2475                 Connector.hasError = true;  //throws exception
2476                 return false;
2477             }
2478         }
2479     }
