2481     protected internal bool ExecuteAllFieldsRequest(string WebServiceURL, DatabaseObject dbObj, string fields) {
2482         HttpWebRequest request = null;
2483 
2484         try {
2485             if (!isInitialized) {
2486                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
2487                 Connector.hasError = true;
2488                 return false;
2489             }
2490 
2491             // ServicePointManager.Expect100Continue = false;
2492 
2493             //create an HTTP request
2494 
2495             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFields");
2496             // request.Timeout = 300000; // set timeout to 5minutes
2497             request.Timeout = 1800000; //half an hour!
2498             request.CookieContainer = this.AuthenticationCookie;
2499             //  request.KeepAlive = True
2500             request.AllowAutoRedirect = true;
2501 
2502             request.Method = WebRequestMethods.Http.Post;   //"POST"
2503             request.ContentType = "application/jpi; charset=utf-8"; //application/json
2504                                                                     // Set the request stream
2505             string result;
2506 
2507             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
2508             if (!AddIdentityHeader(ref request)) {
2509                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
2510                 Connector.hasError = true;  //throws error
2511                 return false;
2512             }
2513 
2514             //test with encryption
2515             StringBuilder sb = new StringBuilder();
2516 
2517             sb.AppendLine("UserName: " + Connection.UserName);
2518             sb.AppendLine("ServerName: " + dbObj.ServerName);
2519             sb.AppendLine("FilePath: " + dbObj.FilePath);
2520             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
2521             string listUnids = String.Join(";", dbObj.Documents.Keys.ToArray());
2522 
2523             //foreach (KeyValuePair<string, DocumentObject> kvp in dbObj.Documents) {
2524             //    listUnids = listUnids + kvp.Key + ";";
2525             //}
2526             //listUnids = listUnids.Substring(0, listUnids.Length - 1); // remove ; at the end
2527 
2528             sb.AppendLine("UniversalIDs: " + listUnids);
2529             sb.AppendLine("Fields: " + fields);
2530 
2531             sb.AppendLine("Connecting via XPagesAPI");
2532 
2533             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
2534             string myString = Encoding.UTF8.GetString(b);
2535             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
2536             if (EncodedEncryptedContent.Initialize()) {
2537                 Stream requestStream = null;
2538                 try {
2539                     requestStream = request.GetRequestStream();
2540                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
2541                         writer.Write(EncodedEncryptedContent.EncodedContent);
2542                     }
2543                 } catch (Exception e) {
2544                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2545                     Connector.hasError = true;  //throws exception
2546                     return false;
2547                 } finally {
2548                     //if (requestStream != null) {
2549                     //    requestStream.Dispose();
2550                     //}
2551                 }
2552             } else {
2553                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
2554                 Connector.hasError = true; //throws exception
2555                 return false;
2556             }
2557 
2558             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
2559 
2560             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
2561                 Stream responseStream = null;
2562                 try {
2563                     responseStream = request.GetResponse().GetResponseStream();
2564                     using (StreamReader reader = new StreamReader(responseStream, true)) {
2565                         result = reader.ReadToEnd();
2566                     }
2567                 } catch (Exception e) {
2568                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2569                     Connector.hasError = true;  //throws exception
2570                     return false;
2571                 } finally {
2572                     //if (responseStream != null) {
2573                     //    responseStream.Dispose();
2574                     //}
2575                 }
2576 
2577                 if (result != null) {
2578                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2579                     if (DecodeDecryptedContent.Initialize()) {
2580                         ArrayList arList = new ArrayList();
2581 
2582                         string[] ar = null;
2583                         bool isError = false;
2584 
2585                         string message = "";
2586                         string details = "";
2587                         string value = "";
2588                         string unid = "";
2589                         Dictionary<string, List<string[]>> dict = new Dictionary<string, List<string[]>>();
2590                         string[] fieldnames = null;
2591                         string[] fieldvalues = null;
2592                         string[] fieldtypes = null;
2593 
2594                         string[] sep = { "||" };
2595 
2596                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2597                         arList.AddRange(ar);
2598                         foreach (string str in arList) {
2599                             //YES/NO
2600                             if (str.StartsWith("Error: ")) {
2601                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
2602                                     isError = true;
2603                                 }
2604                             }
2605                             if (str.StartsWith("Message: ")) {
2606                                 message = str.Replace("Message: ", "");
2607                             }
2608                             if (str.StartsWith("Details: ")) {
2609                                 details = str.Replace("Details: ", "");
2610                             }
2611 
2612                             // 'FieldNames: Unid: '
2613                             if (str.StartsWith("FieldNames: ")) {
2614                                 value = str.Replace("FieldNames: ", "");
2615                                 //get the unid C125760F0054EEFBC1257356002F192D
2616                                 if (value.Length > 34) { //Unid is 32 + ': '
2617                                     unid = value.Substring(0, 32);
2618                                     //remove unid
2619                                     value = value.Remove(0, 34);
2620                                     if (!string.IsNullOrEmpty(value)) {
2621                                         fieldnames = value.Split(sep, StringSplitOptions.None);
2622                                         // check if item is already in dict
2623                                         if (!dict.ContainsKey(unid)) {
2624                                             dict.Add(unid, new List<string[]>());
2625                                         }
2626                                         dict[unid].Add(fieldnames);
2627                                     }
2628 
2629                                 }// else something is wrong
2630 
2631 
2632                             }
2633 
2634                             if (str.StartsWith("FieldValues: ")) {
2635                                 value = str.Replace("FieldValues: ", "");
2636                                 if (value.Length > 34) { //Unid is 32 + ': '
2637                                     unid = value.Substring(0, 32);
2638                                     //remove unid
2639                                     value = value.Remove(0, 34);
2640                                     if (!string.IsNullOrEmpty(value)) {
2641                                         fieldvalues = value.Split(sep, StringSplitOptions.None);
2642                                         // check if item is already in dict
2643                                         if (!dict.ContainsKey(unid)) {
2644                                             dict.Add(unid, new List<string[]>());
2645                                         }
2646                                         dict[unid].Add(fieldvalues);
2647                                     }
2648 
2649                                 }
2650                             }
2651 
2652 
2653                             if (str.StartsWith("FieldTypes: ")) {
2654                                 value = str.Replace("FieldTypes: ", "");
2655                                 if (value.Length > 34) { //Unid is 32 + ': '
2656                                     unid = value.Substring(0, 32);
2657                                     //remove unid
2658                                     value = value.Remove(0, 34);
2659                                     if (!string.IsNullOrEmpty(value)) {
2660                                         fieldtypes = value.Split(sep, StringSplitOptions.None);
2661                                         // check if item is already in dict
2662                                         if (!dict.ContainsKey(unid)) {
2663                                             dict.Add(unid, new List<string[]>());
2664                                         }
2665                                         dict[unid].Add(fieldtypes);
2666                                     }
2667 
2668                                 }
2669                             }
2670                         }
2671 
2672                         DocumentObject docObj = null;
2673                         FieldObject fObj = null;
2674 
2675                         foreach (KeyValuePair<string, List<string[]>> kvp in dict) {
2676                             if (dbObj.Documents.ContainsKey(kvp.Key)) {
2677                                 docObj = dbObj.Documents[kvp.Key];
2678                                 if (docObj.Fields == null) {
2679                                     docObj.Fields = new SortedDictionary<string, FieldObject>();
2680                                 }
2681                                 //reset
2682                                 fieldnames = null;
2683                                 fieldvalues = null;
2684                                 fieldtypes = null;
2685                                 // first array in list should be fieldnames, then values then types
2686                                 fieldnames = kvp.Value[0];
2687                                 fieldvalues = kvp.Value[1];
2688                                 fieldtypes = kvp.Value[2];
2689 
2690                                 for (int i = 0; i < fieldnames.Length; i++) {//fieldnames
2691                                     fObj = new FieldObject(fieldnames[i]);
2692                                     if (fieldvalues.Length - 1 >= i) {
2693                                         fObj.Value = fieldvalues[i];
2694                                     }
2695                                     if (fieldtypes.Length - 1 >= i) {
2696                                         fObj.Type = fieldtypes[i];
2697                                     }
2698 
2699                                     if (docObj.Fields.ContainsKey(fObj.Name)) {
2700                                         docObj.Fields.Remove(fObj.Name);
2701                                     }
2702                                     docObj.Fields.Add(fObj.Name, fObj);
2703 
2704                                 }
2705                             } else {
2706                                 Connector.ReturnMessages.Add("Unable to find the document in the databaseobject :" + kvp.Key);
2707                                 isError = true;
2708                             }
2709 
2710                         }
2711 
2712                         if (isError) {
2713                             Connector.ReturnMessages.Add(message);
2714                             Connector.ReturnMessages.Add(details);
2715                             Connector.hasError = true; //throws exception
2716                             return false;
2717                         } else {
2718                             Connector.ReturnMessages.Add(message);
2719                             Connector.ReturnMessages.Add(details);
2720                             Connector.hasError = false; //SUCCESS
2721                         }
2722 
2723                     } else {
2724                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
2725                         Connector.hasError = true;  //throws exception
2726                         return false;
2727                     }
2728                 } else {
2729                     //no response from server
2730                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
2731                     Connector.hasError = true;  //throws exception
2732                     return false;
2733 
2734                 }
2735                 return true;
2736             } else {
2737                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
2738                 Connector.hasError = true;  //throws exception
2739                 return false;
2740             }
2741 
2742         } catch (Exception ex) {
2743             if (ex.GetType() == typeof(WebException)) {
2744                 WebException webex;
2745                 webex = (WebException)ex;
2746                 if (webex.Response != null) {
2747                     Stream stream = webex.Response.GetResponseStream();
2748                     using (StreamReader reader = new StreamReader(stream)) {
2749                         string errorResp = reader.ReadToEnd();
2750                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2751                         Connector.ReturnMessages.Add("Unable to Execute the All Fields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2752                         Connector.hasError = true;  //throws exception
2753                         return false;
2754                     }
2755 
2756                 } else {
2757                     Connector.ReturnMessages.Add("Unable to Execute the All Fields Request : " + Common.GetErrorInfo(ex));
2758                     Connector.hasError = true;  //throws exception
2759                     return false;
2760                 }
2761             } else {
2762                 Connector.ReturnMessages.Add("Unable to Execute the All Fields Request : " + Common.GetErrorInfo(ex));
2763                 Connector.hasError = true;  //throws exception
2764                 return false;
2765             }
2766         }
2767     }
