2769     protected internal bool ExecuteGetFileRequest(string FileURL, string LocalFilePath) {
2770         HttpWebRequest request = null;
2771         try {
2772 
2773             if (!isInitialized) {
2774                 Connector.ReturnMessages.Add("Unable to execute the Get File Request - Not Initialized/Connected To Server");
2775                 Connector.hasError = true;
2776                 return false;
2777             }
2778 
2779             request = request = (HttpWebRequest)WebRequest.Create(FileURL);
2780 
2781             request.CookieContainer = AuthenticationCookie;
2782             request.AllowAutoRedirect = true;
2783             request.Method = WebRequestMethods.Http.Get;
2784             const int BUFFER_SIZE = 16 * 1024;
2785             byte[] buffer = new byte[BUFFER_SIZE - 1];
2786             int bytesRead;
2787 
2788             using (var outputFileStream = File.Create(LocalFilePath, BUFFER_SIZE)) {
2789                 using (var response2 = request.GetResponse()) {
2790                     using (var responseStream = response2.GetResponseStream()) {
2791                         while ((bytesRead = responseStream.Read(buffer, 0, buffer.Length)) > 0) {
2792                             outputFileStream.Write(buffer, 0, bytesRead);
2793                         }
2794                     }
2795                 }
2796             }
2797             return true;
2798         } catch (Exception ex) {
2799             if (ex.GetType() == typeof(WebException)) {
2800                 WebException webex;
2801                 webex = (WebException)ex;
2802                 if (webex.Response != null) {
2803                     Stream stream = webex.Response.GetResponseStream();
2804                     using (StreamReader reader = new StreamReader(stream)) {
2805                         string errorResp = reader.ReadToEnd();
2806                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2807                         Connector.ReturnMessages.Add("Unable to Execute the Get File Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2808                         Connector.hasError = true;  //throws exception
2809                         return false;
2810                     }
2811 
2812                 } else {
2813                     Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
2814                     Connector.hasError = true;  //throws exception
2815                     return false;
2816                 }
2817             } else {
2818                 Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
2819                 Connector.hasError = true;  //throws exception
2820                 return false;
2821             }
2822         }
2823     }
