1209                                 if (!string.IsNullOrEmpty(value))
1210                                 {
1211                                     modified = value;
1212                                 }
1213                             }
1214 
1215                             if (str.StartsWith("FileObject: "))
1216                             {
1217                                 value = str.Replace("FileObject: ", "");
1218                                 if (!string.IsNullOrEmpty(value))
1219                                 {
1220                                     // files = value;
1221                                     fList.Add(value);
1222                                 }
1223                             }
1224                         }
1225 
1226                         if (isError)
1227                         {
1228                             Connector.ReturnMessages.Add(message);
