1244                                 docObj.UniversalID = unid;
1245                                 docObj.NoteID = noteId;
1246                                 docObj.Size = size;
1247                                 docObj.Url = url;
1248                                 docObj.Form = form;
1249                                 docObj.DateCreated = created;
1250                                 docObj.DateModified = modified;
1251                                 //create file objects
1252                                 SortedDictionary<string, FileObject> files = new SortedDictionary<string, FileObject>();
1253                                 FileObject fObj = null;
