1294                     }
1295                 }
1296                 else
1297                 {
1298                     //no response from server
1299                     Connector.ReturnMessages.Add("Executing DocumentFiles Request -  Unable to get response from XPages!");
1300                     Connector.hasError = true;  //throws exception
1301                     return false;
