1440                     using (StreamReader reader = new StreamReader(responseStream, true))
1441                     {
1442                         result = reader.ReadToEnd();
1443                     }
1444                 }
1445                 catch (Exception e)
1446                 {
1447                     Connector.ReturnMessages.Add("Unable to execute the All Documents Request : " + Common.GetErrorInfo(e));
1448                     Connector.hasError = true;  //throws exception
1449                     return false;
1450                 }
1451                 finally
1452                 {
1453                     //if (responseStream != null) {
1454                     //    responseStream.Dispose();
1455                     //}
1456                 }
1457 
1458                 if (result != null)
1459                 {
1460                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1461                     if (DecodeDecryptedContent.Initialize())
1462                     {
1463                         ArrayList arList = new ArrayList();
1464 
1465                         string[] ar = null;
1466                         bool isError = false;
1467 
1468                         string message = "";
1469                         string details = "";
1470                         string value = "";
1471                         string[] sep = { "||" };
1472                         string[] NoteIds = { };
1473                         string[] Unids = { };
1474                         string[] Forms = { };
1475                         string[] Sizes = { };
1476                         string[] URLs = { };
1477                         string[] Created = { };
1478                         string[] Modified = { };
1479 
1480                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1481                         arList.AddRange(ar);
1482                         foreach (string str in arList)
1483                         {
1484                             //YES/NO
1485                             if (str.StartsWith("Error: "))
1486                             {
1487                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
1488                                 {
1489                                     isError = true;
1490                                 }
1491                             }
1492                             if (str.StartsWith("Message: "))
1493                             {
1494                                 message = str.Replace("Message: ", "");
1495                             }
1496                             if (str.StartsWith("Details: "))
1497                             {
1498                                 details = str.Replace("Details: ", "");
1499                             }
1500 
1501                             if (str.StartsWith("Unids: "))
1502                             {
1503                                 value = str.Replace("Unids: ", "");
1504                                 if (!string.IsNullOrEmpty(value))
1505                                 {
1506                                     Unids = value.Split(sep, StringSplitOptions.None);
1507                                 }
1508                             }
1509 
1510                             if (str.StartsWith("NoteIDs: "))
1511                             {
1512                                 value = str.Replace("NoteIDs: ", "");
1513                                 if (!string.IsNullOrEmpty(value))
1514                                 {
1515                                     NoteIds = value.Split(sep, StringSplitOptions.None);
1516                                 }
1517                             }
1518 
1519                             if (str.StartsWith("Forms: "))
1520                             {
1521                                 value = str.Replace("Forms: ", "");
1522                                 if (!string.IsNullOrEmpty(value))
1523                                 {
1524                                     Forms = value.Split(sep, StringSplitOptions.None);
1525                                 }
1526                             }
1527 
1528                             if (str.StartsWith("Sizes: "))
1529                             {
1530                                 value = str.Replace("Sizes: ", "");
1531                                 if (!string.IsNullOrEmpty(value))
1532                                 {
1533                                     Sizes = value.Split(sep, StringSplitOptions.None);
1534                                 }
1535                             }
1536 
1537                             if (str.StartsWith("URLs: "))
1538                             {
1539                                 value = str.Replace("URLs: ", "");
1540                                 if (!string.IsNullOrEmpty(value))
1541                                 {
1542                                     URLs = value.Split(sep, StringSplitOptions.None);
1543                                 }
1544                             }
1545 
1546 
1547                             if (str.StartsWith("Created: "))
1548                             {
1549                                 value = str.Replace("Created: ", "");
1550                                 if (!string.IsNullOrEmpty(value))
1551                                 {
1552                                     Created = value.Split(sep, StringSplitOptions.None);
1553                                 }
1554                             }
1555 
1556 
1557                             if (str.StartsWith("Modified: "))
1558                             {
1559                                 value = str.Replace("Modified: ", "");
1560                                 if (!string.IsNullOrEmpty(value))
1561                                 {
1562                                     Modified = value.Split(sep, StringSplitOptions.None);
1563                                 }
1564                             }
1565                         }
1566 
1567                         //loop through unids, notesids, forms, sizes, urls and create document objects
1568                         // check if all have the same number of items!
1569                         int count = 0;
1570                         if (Unids != null && Unids.Length > 0)
1571                         {
1572                             count = Unids.Length;
1573                             if (NoteIds != null && NoteIds.Length > 0)
1574                             {
1575                                 if (count != NoteIds.Length)
1576                                 {
1577                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> NoteIds");
1578                                     Connector.hasError = true;  //throws exception
1579                                     return false;
1580                                 }
1581                                 else
1582                                 {
1583                                     // form can be empty!!
1584                                     //if (Forms != null && Forms.Length > 0) {
1585                                     //    if (count != Forms.Length) {
