1591                                     {
1592                                         if (count != Sizes.Length)
1593                                         {
1594                                             Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Sizes");
1595                                             Connector.hasError = true;  //throws exception
1596                                             return false;
1597                                         }
1598                                         else
1599                                         {
1600                                             if (URLs != null && URLs.Length > 0)
1601                                             {
1602                                                 if (count != URLs.Length)
1603                                                 {
1604                                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> URLs");
1605                                                     Connector.hasError = true;  //throws exception
1606                                                     return false;
1607                                                 }
1608                                             }
1609                                         }
1610                                     }
1611                                     //   }
1612                                     // }
1613                                 }
1614                             }
1615                             // loop through and create doc obj
1616                             DocumentObject docObj = null;
1617                             if (dbObj.Documents == null)
1618                             {
1619                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
1620                             }
1621                             for (int i = 0; i < Unids.Length; i++)
1622                             {
1623                                 docObj = new DocumentObject(dbObj, Unids[i]);
1624                                 if (NoteIds.Length - 1 >= i)
1625                                 {
1626                                     docObj.NoteID = NoteIds[i];
1627                                 }
1628                                 if (Forms.Length - 1 >= i)
1629                                 {
1630                                     docObj.Form = Forms[i];
1631                                 }
1632                                 if (Sizes.Length - 1 >= i)
1633                                 {
1634                                     docObj.Size = Sizes[i];
1635                                 }
1636                                 if (URLs.Length - 1 >= i)
1637                                 {
1638                                     docObj.Url = URLs[i];
1639                                 }
1640                                 if (Created.Length - 1 >= i)
1641                                 {
1642                                     docObj.DateCreated = Created[i];
1643                                 }
1644                                 if (Modified.Length - 1 >= i)
1645                                 {
1646                                     docObj.DateModified = Modified[i];
1647                                 }
1648                                 if (dbObj.Documents.ContainsKey(Unids[i]))
1649                                 {
1650                                     dbObj.Documents.Remove(Unids[i]);
1651                                 }
1652                                 docObj.IsInitialized = true;
1653                                 dbObj.Documents.Add(Unids[i], docObj);
1654                             }
1655                         }
1656 
1657 
1658 
1659                         if (isError)
1660                         {
1661                             Connector.ReturnMessages.Add(message);
1662                             Connector.ReturnMessages.Add(details);
1663                             Connector.hasError = true; //throws exception
1664                             return false;
1665                         }
1666                         else
1667                         {
1668                             Connector.ReturnMessages.Add(message);
1669                             Connector.ReturnMessages.Add(details);
1670                             Connector.hasError = false; //SUCCESS
1671                         }
1672 
1673                     }
1674                     else
1675                     {
1676                         Connector.ReturnMessages.Add("Executing All Documents Request - Unable to decode/decrypt the content of the response!");
1677                         Connector.hasError = true;  //throws exception
1678                         return false;
1679                     }
1680                 }
1681                 else
1682                 {
1683                     //no response from server
1684                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get response from XPages!");
1685                     Connector.hasError = true;  //throws exception
1686                     return false;
1687 
1688                 }
1689                 return true;
1690             }
1691             else
1692             {
1693                 Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Document - Authentication Issue");
1694                 Connector.hasError = true;  //throws exception
1695                 return false;
1696             }
1697 
1698         }
1699         catch (Exception ex)
1700         {
1701             if (ex.GetType() == typeof(WebException))
1702             {
1703                 WebException webex;
1704                 webex = (WebException)ex;
1705                 if (webex.Response != null)
1706                 {
1707                     Stream stream = webex.Response.GetResponseStream();
1708                     using (StreamReader reader = new StreamReader(stream))
1709                     {
1710                         string errorResp = reader.ReadToEnd();
1711                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1712                         Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1713                         Connector.hasError = true;  //throws exception
1714                         return false;
1715                     }
1716 
1717                 }
1718                 else
1719                 {
1720                     Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1721                     Connector.hasError = true;  //throws exception
1722                     return false;
1723                 }
1724             }
1725             else
1726             {
1727                 Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1728                 Connector.hasError = true;  //throws exception
1729                 return false;
1730             }
1731         }
1732     }
