1734     protected internal bool ExecuteAllDocumentsFilesRequest(string WebServiceURL, string searchField, string searchValue, string formula, string unids, DatabaseObject dbObj)
1735     {
1736         HttpWebRequest request = null;
1737 
1738         try
1739         {
1740             if (!isInitialized)
1741             {
1742                 Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request - Not Initialized/Connected To Server");
1743                 Connector.hasError = true;
1744                 return false;
1745             }
1746 
1747             ServicePointManager.Expect100Continue = false;
1748 
1749             //create an HTTP request
1750 
1751             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFiles");
1752             request.Timeout = 300000; // set timeout to 5minutes
1753             request.CookieContainer = this.AuthenticationCookie;
1754             //  request.KeepAlive = True
1755             request.AllowAutoRedirect = true;
1756 
1757             request.Method = WebRequestMethods.Http.Post;   //"POST"
1758             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1759                                                                     // Set the request stream
1760             string result;
1761 
1762             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1763             if (!AddIdentityHeader(ref request))
1764             {
1765                 Connector.ReturnMessages.Add("Unable to Execute AllDocumentsFiles Request : Identity Header could not be added!");
1766                 Connector.hasError = true;  //throws error
1767                 return false;
1768             }
1769 
1770             //test with encryption
1771             StringBuilder sb = new StringBuilder();
1772 
1773             sb.AppendLine("UserName: " + Connection.UserName);
1774             sb.AppendLine("ServerName: " + dbObj.ServerName);
1775             sb.AppendLine("FilePath: " + dbObj.FilePath);
1776             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
1777             sb.AppendLine("SearchField: " + searchField);
1778             sb.AppendLine("SearchValue: " + searchValue);
1779             sb.AppendLine("Formula: " + formula);
1780             sb.AppendLine("Unids: " + unids);
1781             sb.AppendLine("Connecting via XPagesAPI");
1782 
1783             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1784             string myString = Encoding.UTF8.GetString(b);
1785             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1786             if (EncodedEncryptedContent.Initialize())
1787             {
1788                 Stream requestStream = null;
1789                 try
1790                 {
1791                     requestStream = request.GetRequestStream();
1792                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
1793                     {
1794                         writer.Write(EncodedEncryptedContent.EncodedContent);
1795                     }
1796                 }
1797                 catch (Exception e)
1798                 {
1799                     Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(e));
1800                     Connector.hasError = true;  //throws exception
1801                     return false;
1802                 }
1803                 finally
1804                 {
1805                     //if (requestStream != null) {
1806                     //    requestStream.Dispose();
1807                     //}
1808                 }
1809             }
1810             else
1811             {
1812                 Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request - Unable to encode/encrypt the content of the request!");
1813                 Connector.hasError = true; //throws exception
1814                 return false;
1815             }
1816 
1817             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1818 
1819             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
1820             {
1821                 Stream responseStream = null;
1822                 try
1823                 {
1824                     responseStream = request.GetResponse().GetResponseStream();
1825                     using (StreamReader reader = new StreamReader(responseStream, true))
1826                     {
1827                         result = reader.ReadToEnd();
1828                     }
1829                 }
1830                 catch (Exception e)
1831                 {
1832                     Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(e));
1833                     Connector.hasError = true;  //throws exception
1834                     return false;
1835                 }
1836                 finally
1837                 {
1838                     //if (responseStream != null) {
1839                     //    responseStream.Dispose();
1840                     //}
1841                 }
1842 
1843                 if (result != null)
1844                 {
1845                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1846                     if (DecodeDecryptedContent.Initialize())
1847                     {
1848                         ArrayList arList = new ArrayList();
1849 
1850                         string[] ar = null;
1851                         bool isError = false;
1852 
1853                         string message = "";
1854                         string details = "";
1855                         string value = "";
