2020                                 {
2021                                     docObj.NoteID = NoteIds[i];
2022                                 }
2023                                 if (Forms.Length - 1 >= i)
2024                                 {
2025                                     docObj.Form = Forms[i];
2026                                 }
2027                                 if (Sizes.Length - 1 >= i)
2028                                 {
