2116         catch (Exception ex)
2117         {
2118             if (ex.GetType() == typeof(WebException))
2119             {
2120                 WebException webex;
2121                 webex = (WebException)ex;
2122                 if (webex.Response != null)
2123                 {
2124                     Stream stream = webex.Response.GetResponseStream();
2125                     using (StreamReader reader = new StreamReader(stream))
2126                     {
2127                         string errorResp = reader.ReadToEnd();
2128                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2129                         Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2130                         Connector.hasError = true;  //throws exception
2131                         return false;
2132                     }
2133 
2134                 }
2135                 else
2136                 {
2137                     Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
2138                     Connector.hasError = true;  //throws exception
2139                     return false;
2140                 }
2141             }
2142             else
2143             {
2144                 Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
2145                 Connector.hasError = true;  //throws exception
2146                 return false;
2147             }
2148         }
