2151     protected internal bool ExecuteFieldsRequest(string WebServiceURL, DocumentObject docObj, string fields)
2152     {
2153         HttpWebRequest request = null;
2154 
2155         try
2156         {
2157             if (!isInitialized)
2158             {
2159                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
2160                 Connector.hasError = true;
2161                 return false;
2162             }
2163 
2164             ServicePointManager.Expect100Continue = false;
2165 
2166             //create an HTTP request
2167 
2168             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Fields");
2169 
2170             request.CookieContainer = this.AuthenticationCookie;
2171             //  request.KeepAlive = True
2172             request.AllowAutoRedirect = true;
2173 
2174             request.Method = WebRequestMethods.Http.Post;   //"POST"
2175             request.ContentType = "application/jpi; charset=utf-8"; //application/json
2176                                                                     // Set the request stream
2177             string result;
2178 
2179             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
2180             if (!AddIdentityHeader(ref request))
2181             {
2182                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
2183                 Connector.hasError = true;  //throws error
2184                 return false;
2185             }
2186 
2187             //test with encryption
2188             StringBuilder sb = new StringBuilder();
2189 
2190             sb.AppendLine("UserName: " + Connection.UserName);
2191             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
2192             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
2193             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
2194             sb.AppendLine("UniversalID: " + docObj.UniversalID);
2195             sb.AppendLine("Fields: " + fields);
2196             sb.AppendLine("Connecting via XPagesAPI");
2197 
2198             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
2199             string myString = Encoding.UTF8.GetString(b);
2200             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
2201             if (EncodedEncryptedContent.Initialize())
2202             {
2203                 Stream requestStream = null;
2204                 try
2205                 {
2206                     requestStream = request.GetRequestStream();
2207                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
2208                     {
2209                         writer.Write(EncodedEncryptedContent.EncodedContent);
2210                     }
2211                 }
2212                 catch (Exception e)
2213                 {
2214                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2215                     Connector.hasError = true;  //throws exception
2216                     return false;
2217                 }
2218                 finally
2219                 {
2220                     //if (requestStream != null) {
2221                     //    requestStream.Dispose();
2222                     //}
2223                 }
2224             }
2225             else
2226             {
2227                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
2228                 Connector.hasError = true; //throws exception
2229                 return false;
2230             }
2231 
2232             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
2233 
2234             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
2235             {
2236 
2237                 Stream responseStream = null;
2238                 try
2239                 {
2240                     responseStream = request.GetResponse().GetResponseStream();
2241                     using (StreamReader reader = new StreamReader(responseStream, true))
2242                     {
2243                         result = reader.ReadToEnd();
2244                     }
2245                 }
2246                 catch (Exception e)
2247                 {
2248                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2249                     Connector.hasError = true;  //throws exception
2250                     return false;
2251                 }
2252                 finally
2253                 {
2254                     //if (responseStream != null) {
2255                     //    responseStream.Dispose();
2256                     //}
2257                 }
2258 
2259                 if (result != null)
2260                 {
2261                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2262                     if (DecodeDecryptedContent.Initialize())
2263                     {
2264                         ArrayList arList = new ArrayList();
2265 
2266                         string[] ar = null;
2267                         bool isError = false;
2268 
2269                         string message = "";
2270                         string details = "";
2271                         string value = "";
2272                         string[] fieldnames = null;
2273                         string[] fieldvalues = null;
2274                         string[] fieldtypes = null;
2275 
2276                         string[] sep = { "||" };
2277 
2278                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2279                         arList.AddRange(ar);
2280                         foreach (string str in arList)
2281                         {
2282                             //YES/NO
2283                             if (str.StartsWith("Error: "))
2284                             {
2285                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
2286                                 {
2287                                     isError = true;
2288                                 }
2289                             }
2290                             if (str.StartsWith("Message: "))
2291                             {
2292                                 message = str.Replace("Message: ", "");
2293                             }
2294                             if (str.StartsWith("Details: "))
2295                             {
2296                                 details = str.Replace("Details: ", "");
2297                             }
2298 
2299                             if (str.StartsWith("FieldNames: "))
2300                             {
2301                                 value = str.Replace("FieldNames: ", "");
2302                                 if (!string.IsNullOrEmpty(value))
2303                                 {
2304                                     fieldnames = value.Split(sep, StringSplitOptions.None);
2305                                 }
2306                             }
2307 
2308                             if (str.StartsWith("FieldValues: "))
2309                             {
2310                                 value = str.Replace("FieldValues: ", "");
2311                                 if (!string.IsNullOrEmpty(value))
2312                                 {
2313                                     fieldvalues = value.Split(sep, StringSplitOptions.None);
2314                                 }
2315                             }
2316 
2317                             if (str.StartsWith("FieldTypes: "))
2318                             {
2319                                 value = str.Replace("FieldTypes: ", "");
2320                                 if (!string.IsNullOrEmpty(value))
2321                                 {
2322                                     fieldtypes = value.Split(sep, StringSplitOptions.None);
2323                                 }
2324                             }
2325 
2326                         }
2327                         FieldObject fObj = null;
2328                         if (docObj.Fields == null)
2329                         {
2330                             docObj.Fields = new SortedDictionary<string, FieldObject>();
2331                         }
2332                         for (int i = 0; i < fieldnames.Length; i++)
2333                         {
2334                             fObj = new FieldObject(fieldnames[i]);
2335                             if (fieldvalues.Length - 1 >= i)
2336                             {
2337                                 fObj.Value = fieldvalues[i];
2338                             }
2339                             if (fieldtypes.Length - 1 >= i)
2340                             {
2341                                 fObj.Type = fieldtypes[i];
2342                             }
2343 
2344                             if (docObj.Fields.ContainsKey(fObj.Name))
2345                             {
2346                                 docObj.Fields.Remove(fObj.Name);
2347                             }
2348                             docObj.Fields.Add(fObj.Name, fObj);
2349 
2350                         }
2351 
2352                         if (isError)
2353                         {
2354                             Connector.ReturnMessages.Add(message);
2355                             Connector.ReturnMessages.Add(details);
2356                             Connector.hasError = true; //throws exception
2357                             return false;
2358                         }
2359                         else
2360                         {
2361                             Connector.ReturnMessages.Add(message);
2362                             Connector.ReturnMessages.Add(details);
2363                             Connector.hasError = false; //SUCCESS
2364                         }
2365 
2366                     }
2367                     else
2368                     {
2369                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
2370                         Connector.hasError = true;  //throws exception
2371                         return false;
2372                     }
2373                 }
2374                 else
2375                 {
2376                     //no response from server
2377                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
2378                     Connector.hasError = true;  //throws exception
2379                     return false;
2380 
2381                 }
2382                 return true;
2383             }
2384             else
2385             {
2386                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
2387                 Connector.hasError = true;  //throws exception
2388                 return false;
2389             }
2390 
2391         }
2392         catch (Exception ex)
2393         {
2394             if (ex.GetType() == typeof(WebException))
2395             {
2396                 WebException webex;
2397                 webex = (WebException)ex;
2398                 if (webex.Response != null)
2399                 {
2400                     Stream stream = webex.Response.GetResponseStream();
2401                     using (StreamReader reader = new StreamReader(stream))
2402                     {
2403                         string errorResp = reader.ReadToEnd();
2404                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2405                         Connector.ReturnMessages.Add("Unable to Execute the Fields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2406                         Connector.hasError = true;  //throws exception
2407                         return false;
2408                     }
2409 
2410                 }
2411                 else
2412                 {
2413                     Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2414                     Connector.hasError = true;  //throws exception
2415                     return false;
2416                 }
2417             }
2418             else
2419             {
2420                 Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2421                 Connector.hasError = true;  //throws exception
2422                 return false;
2423             }
2424         }
2425     }
