2849     protected internal bool ExecuteAllFieldsRequest(string WebServiceURL, DatabaseObject dbObj, string fields)
2850     {
2851         HttpWebRequest request = null;
2852 
2853         try
2854         {
2855             if (!isInitialized)
2856             {
2857                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
2858                 Connector.hasError = true;
2859                 return false;
2860             }
2861 
2862             ServicePointManager.Expect100Continue = false;
2863 
2864             //create an HTTP request
2865 
2866             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFields");
2867             request.Timeout = 300000; // set timeout to 5minutes
2868             request.CookieContainer = this.AuthenticationCookie;
2869             //  request.KeepAlive = True
2870             request.AllowAutoRedirect = true;
2871 
2872             request.Method = WebRequestMethods.Http.Post;   //"POST"
2873             request.ContentType = "application/jpi; charset=utf-8"; //application/json
2874                                                                     // Set the request stream
2875             string result;
2876 
2877             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
2878             if (!AddIdentityHeader(ref request))
2879             {
2880                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
2881                 Connector.hasError = true;  //throws error
2882                 return false;
2883             }
2884 
2885             //test with encryption
2886             StringBuilder sb = new StringBuilder();
2887 
2888             sb.AppendLine("UserName: " + Connection.UserName);
2889             sb.AppendLine("ServerName: " + dbObj.ServerName);
2890             sb.AppendLine("FilePath: " + dbObj.FilePath);
2891             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
