2900             sb.AppendLine("Fields: " + fields);
2901 
2902             sb.AppendLine("Connecting via XPagesAPI");
2903 
2904             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
2905             string myString = Encoding.UTF8.GetString(b);
2906             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
2907             if (EncodedEncryptedContent.Initialize())
2908             {
2909                 Stream requestStream = null;
2910                 try
2911                 {
2912                     requestStream = request.GetRequestStream();
2913                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
2914                     {
2915                         writer.Write(EncodedEncryptedContent.EncodedContent);
2916                     }
2917                 }
2918                 catch (Exception e)
2919                 {
2920                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2921                     Connector.hasError = true;  //throws exception
2922                     return false;
2923                 }
2924                 finally
2925                 {
2926                     //if (requestStream != null) {
2927                     //    requestStream.Dispose();
2928                     //}
2929                 }
2930             }
2931             else
2932             {
2933                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
2934                 Connector.hasError = true; //throws exception
2935                 return false;
2936             }
2937 
2938             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
2939 
2940             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
2941             {
2942                 Stream responseStream = null;
2943                 try
2944                 {
2945                     responseStream = request.GetResponse().GetResponseStream();
2946                     using (StreamReader reader = new StreamReader(responseStream, true))
2947                     {
2948                         result = reader.ReadToEnd();
2949                     }
2950                 }
2951                 catch (Exception e)
2952                 {
2953                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2954                     Connector.hasError = true;  //throws exception
2955                     return false;
2956                 }
2957                 finally
2958                 {
2959                     //if (responseStream != null) {
2960                     //    responseStream.Dispose();
2961                     //}
2962                 }
2963 
2964                 if (result != null)
2965                 {
2966                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2967                     if (DecodeDecryptedContent.Initialize())
2968                     {
2969                         ArrayList arList = new ArrayList();
2970 
2971                         string[] ar = null;
2972                         bool isError = false;
2973 
2974                         string message = "";
2975                         string details = "";
2976                         string value = "";
2977                         string unid = "";
