2979                         string[] fieldnames = null;
2980                         string[] fieldvalues = null;
2981                         string[] fieldtypes = null;
2982 
2983                         string[] sep = { "||" };
2984 
2985                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2986                         arList.AddRange(ar);
2987                         foreach (string str in arList)
2988                         {
2989                             //YES/NO
2990                             if (str.StartsWith("Error: "))
2991                             {
2992                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
2993                                 {
2994                                     isError = true;
2995                                 }
2996                             }
2997                             if (str.StartsWith("Message: "))
2998                             {
2999                                 message = str.Replace("Message: ", "");
3000                             }
3001                             if (str.StartsWith("Details: "))
3002                             {
3003                                 details = str.Replace("Details: ", "");
3004                             }
3005 
3006                             // 'FieldNames: Unid: '
3007                             if (str.StartsWith("FieldNames: "))
3008                             {
3009                                 value = str.Replace("FieldNames: ", "");
3010                                 //get the unid C125760F0054EEFBC1257356002F192D
3011                                 if (value.Length > 34)
3012                                 { //Unid is 32 + ': '
3013                                     unid = value.Substring(0, 32);
3014                                     //remove unid
3015                                     value = value.Remove(0, 34);
3016                                     if (!string.IsNullOrEmpty(value))
3017                                     {
3018                                         fieldnames = value.Split(sep, StringSplitOptions.None);
3019                                         // check if item is already in dict
3020                                         if (!dict.ContainsKey(unid))
3021                                         {
3022                                             dict.Add(unid, new List<string[]>());
3023                                         }
3024                                         dict[unid].Add(fieldnames);
3025                                     }
3026 
3027                                 }// else something is wrong
3028 
3029 
3030                             }
3031 
3032                             if (str.StartsWith("FieldValues: "))
3033                             {
3034                                 value = str.Replace("FieldValues: ", "");
3035                                 if (value.Length > 34)
3036                                 { //Unid is 32 + ': '
3037                                     unid = value.Substring(0, 32);
3038                                     //remove unid
3039                                     value = value.Remove(0, 34);
3040                                     if (!string.IsNullOrEmpty(value))
3041                                     {
3042                                         fieldvalues = value.Split(sep, StringSplitOptions.None);
3043                                         // check if item is already in dict
3044                                         if (!dict.ContainsKey(unid))
3045                                         {
3046                                             dict.Add(unid, new List<string[]>());
3047                                         }
3048                                         dict[unid].Add(fieldvalues);
3049                                     }
3050 
3051                                 }
3052                             }
3053 
3054 
3055                             if (str.StartsWith("FieldTypes: "))
3056                             {
3057                                 value = str.Replace("FieldTypes: ", "");
3058                                 if (value.Length > 34)
3059                                 { //Unid is 32 + ': '
3060                                     unid = value.Substring(0, 32);
3061                                     //remove unid
3062                                     value = value.Remove(0, 34);
3063                                     if (!string.IsNullOrEmpty(value))
3064                                     {
3065                                         fieldtypes = value.Split(sep, StringSplitOptions.None);
3066                                         // check if item is already in dict
3067                                         if (!dict.ContainsKey(unid))
3068                                         {
3069                                             dict.Add(unid, new List<string[]>());
3070                                         }
3071                                         dict[unid].Add(fieldtypes);
3072                                     }
3073 
3074                                 }
3075                             }
3076                         }
3077 
3078                         DocumentObject docObj = null;
3079                         FieldObject fObj = null;
3080 
3081                         foreach (KeyValuePair<string, List<string[]>> kvp in dict)
3082                         {
3083                             if (dbObj.Documents.ContainsKey(kvp.Key))
3084                             {
3085                                 docObj = dbObj.Documents[kvp.Key];
3086                                 if (docObj.Fields == null)
3087                                 {
3088                                     docObj.Fields = new SortedDictionary<string, FieldObject>();
3089                                 }
3090                                 //reset
3091                                 fieldnames = null;
3092                                 fieldvalues = null;
3093                                 fieldtypes = null;
3094                                 // first array in list should be fieldnames, then values then types
3095                                 fieldnames = kvp.Value[0];
3096                                 fieldvalues = kvp.Value[1];
3097                                 fieldtypes = kvp.Value[2];
3098 
3099                                 for (int i = 0; i < fieldnames.Length; i++)
3100                                 {//fieldnames
3101                                     fObj = new FieldObject(fieldnames[i]);
3102                                     if (fieldvalues.Length - 1 >= i)
3103                                     {
3104                                         fObj.Value = fieldvalues[i];
3105                                     }
3106                                     if (fieldtypes.Length - 1 >= i)
3107                                     {
3108                                         fObj.Type = fieldtypes[i];
3109                                     }
3110 
3111                                     if (docObj.Fields.ContainsKey(fObj.Name))
3112                                     {
3113                                         docObj.Fields.Remove(fObj.Name);
3114                                     }
3115                                     docObj.Fields.Add(fObj.Name, fObj);
3116 
3117                                 }
3118                             }
3119                             else
3120                             {
3121                                 Connector.ReturnMessages.Add("Unable to find the document in the databaseobject :" + kvp.Key);
3122                                 isError = true;
3123                             }
3124 
3125                         }
3126 
3127                         if (isError)
3128                         {
3129                             Connector.ReturnMessages.Add(message);
3130                             Connector.ReturnMessages.Add(details);
3131                             Connector.hasError = true; //throws exception
3132                             return false;
3133                         }
3134                         else
3135                         {
3136                             Connector.ReturnMessages.Add(message);
3137                             Connector.ReturnMessages.Add(details);
3138                             Connector.hasError = false; //SUCCESS
3139                         }
3140 
3141                     }
3142                     else
3143                     {
3144                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
3145                         Connector.hasError = true;  //throws exception
3146                         return false;
3147                     }
3148                 }
3149                 else
3150                 {
3151                     //no response from server
3152                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
3153                     Connector.hasError = true;  //throws exception
3154                     return false;
3155 
3156                 }
3157                 return true;
3158             }
3159             else
3160             {
3161                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
3162                 Connector.hasError = true;  //throws exception
3163                 return false;
3164             }
3165 
3166         }
3167         catch (Exception ex)
3168         {
3169             if (ex.GetType() == typeof(WebException))
3170             {
3171                 WebException webex;
3172                 webex = (WebException)ex;
3173                 if (webex.Response != null)
3174                 {
3175                     Stream stream = webex.Response.GetResponseStream();
3176                     using (StreamReader reader = new StreamReader(stream))
