3202     protected internal bool ExecuteGetFileRequest(string FileURL, string LocalFilePath)
3203     {
3204         HttpWebRequest request = null;  
3205         try
3206         {
3207 
3208             if (!isInitialized)
3209             {
3210                 Connector.ReturnMessages.Add("Unable to execute the Get File Request - Not Initialized/Connected To Server");
3211                 Connector.hasError = true;
3212                 return false;
3213             }
3214 
3215             request = request = (HttpWebRequest)WebRequest.Create(FileURL);
3216         
3217             request.CookieContainer = AuthenticationCookie;
3218             request.AllowAutoRedirect = true;
3219             request.Method = WebRequestMethods.Http.Get;
3220             const int BUFFER_SIZE = 16 * 1024;
3221            byte[] buffer = new byte[BUFFER_SIZE - 1];
3222             int bytesRead;
3223 
3224             using (var outputFileStream = File.Create(LocalFilePath, BUFFER_SIZE))
3225             {
3226                 using (var response2 = request.GetResponse())
3227                 {
3228                     using (var responseStream = response2.GetResponseStream())
3229                     {
3230                         while ((bytesRead = responseStream.Read(buffer, 0, buffer.Length)) > 0)
3231                         {
3232                             outputFileStream.Write(buffer, 0, bytesRead);
3233                         }
3234                     }
3235                 }
3236             }
3237             return true;
3238         }
3239         catch (Exception ex)
3240         {
3241             if (ex.GetType() == typeof(WebException))
3242             {
3243                 WebException webex;
3244                 webex = (WebException)ex;
3245                 if (webex.Response != null)
3246                 {
3247                     Stream stream = webex.Response.GetResponseStream();
3248                     using (StreamReader reader = new StreamReader(stream))
3249                     {
3250                         string errorResp = reader.ReadToEnd();
3251                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
3252                         Connector.ReturnMessages.Add("Unable to Execute the Get File Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
3253                         Connector.hasError = true;  //throws exception
3254                         return false;
3255                     }
3256 
3257                 }
3258                 else
3259                 {
3260                     Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
3261                     Connector.hasError = true;  //throws exception
3262                     return false;
3263                 }
3264             }
3265             else
3266             {
3267                 Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
3268                 Connector.hasError = true;  //throws exception
3269                 return false;
3270             }
3271         }
3272     }
