28     #region Private Methods
29 
30     private bool AddIdentityHeader(ref HttpWebRequest request)
31     {
32         try
33         {
34             if (request != null)
35             {
36                 string XPIdentity = "JPI$XP@ges!C0nn3nt0r|Id3nTity@Request";
37                 if (!string.IsNullOrEmpty(Connection.XPIdentity))
38                 {
39                     XPIdentity = Connection.XPIdentity;
40                 }
41                 //  Const XPIdentity As String = "JPI$XP@ges!C0nn3nt0r|Id3nTity@Request"
42 
43                 Encryptor EncodedEncryptedContent = new Encryptor(XPIdentity, true, ref Connection);
44                 if (EncodedEncryptedContent.Initialize())
45                 {
46                     request.Headers.Add("Identity", EncodedEncryptedContent.EncodedContent);
47                     return true;
48                 }
49             }
50             else
51             {
52                 //request is nothing ' error added in calling function
53             }
54             return false;
55         }
56         catch (Exception ex)
57         {
58             Connector.ReturnMessages.Add("Unable to add Identity Header to XPages Request");
59             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
60             Connector.hasError = true; //throws exception
61 
62             return false;
63         }
64     }
65 
66     #endregion
67 
68     #region Protected Internal Methods
69 
70     protected internal bool Initialize()
71     {
72 
73         HttpWebRequest request = null;
74 
75         // use the XPagesAPI ReturnMessages & hasError
76         try
77         {
78             //ServicePointManager.Expect100Continue = false;
79             ServicePointManager.Expect100Continue = true;
80             ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls;
81             ServicePointManager.DefaultConnectionLimit = 9999;
82 
83             // Cookie container to store authentication cookie
84             CookieContainer cookies = new CookieContainer();
85 
86             //create an HTTP request
87             //Login - check for "/names.nsf?Login"
88             string serverURL = "";
89             serverURL = Connection.ServerURL;
90             if (!serverURL.Contains("/names.nsf?Login"))
91             {
92                 serverURL = serverURL + "/names.nsf?Login";
93             }
94 
95             request = (HttpWebRequest)WebRequest.Create(serverURL);
96 
97             // Prepare HTTP request
98             request.Method = WebRequestMethods.Http.Post;
99             // "POST"
100             request.AllowAutoRedirect = false;
101             request.ContentType = "application/x-www-form-urlencoded";
102             request.CookieContainer = cookies;
103             //  request.UserAgent = "Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/54.0.2840.99 Safari/537.36"
104             // Prepare POST body
105             string post = "Username=" + Uri.EscapeDataString(Connection.UserName) + "&Password=" + Uri.EscapeDataString(Connection.Password);
106             byte[] bytes = Encoding.ASCII.GetBytes(post);
107 
108             // Write data to request
109             request.ContentLength = bytes.Length;
110             Stream streamOut = request.GetRequestStream();
111             streamOut.Write(bytes, 0, bytes.Length);
112             streamOut.Close();
113 
114             //' Get response
115             HttpWebResponse response = (HttpWebResponse)request.GetResponse();
116 
117             // Check if we are authenticated properly
118             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
119             {
120                 foreach (Cookie cooky in response.Cookies)
121                 {
122                     cookies.Add(cooky);
123                 }
124 
125                 this.AuthenticationCookie = cookies;
126 
127                 if (response.Cookies != null && response.Cookies.Count > 0)
128                 {
129                     Connector.ReturnMessages.Add(Connection.UserName + " Successfully Authenticated On Domino Server " + Connection.ServerURL);
130                     Connector.hasError = false;
131                 }
132                 else
133                 {
134                     Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
135                     Connector.hasError = true;
136                     //thows exception
137                 }
138             }
139             else
140             {
141                 Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
142                 Connector.hasError = true;
143                 //thows exception
144             }
145             isInitialized = true;
146             return true;
147         }
148         catch (Exception ex)
149         {
150             Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
151             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
152             Connector.hasError = true;
153             //throws exception
154             isInitialized = false;
155             return false;
156         }
157 
158     }
159 
160     protected internal bool ExecuteSessionRequest(string WebServiceURL)
161     {
162         HttpWebRequest request = null;
163 
164         try
165         {
166             if (!isInitialized)
167             {
168                 Connector.ReturnMessages.Add("Unable to execute the Session Request - Not Initialized/Connected To Server");
169                 Connector.hasError = true;
170                 return false;
171             }
172 
173             ServicePointManager.Expect100Continue = false;
174 
175             //create an HTTP request
176             // WebURL use this without ?CheckOut or ?CheckIn
177 
178             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Session");
179             // ?CheckOut
180 
181             request.CookieContainer = this.AuthenticationCookie;
182             //  request.KeepAlive = True
183             request.AllowAutoRedirect = true;
184 
185             request.Method = WebRequestMethods.Http.Post;           //"POST"
186             request.ContentType = "application/jpi; charset=utf-8";         //application/json
187                                                                             // Set the request stream
188             string result;
189 
190             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
191             if (!AddIdentityHeader(ref request))
192             {
193                 Connector.ReturnMessages.Add("Unable to Execute Session Request : Identity Header could not be added!");
194                 Connector.hasError = true;
195                 return false;
196             }
197 
198             //test with encryption
199             StringBuilder sb = new StringBuilder();
200 
201             // User Name
202             sb.AppendLine("UserName : " + Connection.UserName);
203             sb.AppendLine("Connecting via XPagesAPI");
204 
205 
206             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
207             string myString = Encoding.UTF8.GetString(b);
208             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
209             if (EncodedEncryptedContent.Initialize())
210             {
211 
212                 Stream requestStream = null;
213                 try
214                 {
215                     requestStream = request.GetRequestStream();
216                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
217                     {
218                         writer.Write(EncodedEncryptedContent.EncodedContent);
219                     }
220                 }
221                 catch (Exception e)
222                 {
223                     Connector.ReturnMessages.Add("Unable to execute the Session Request : " + Common.GetErrorInfo(e));
224                     Connector.hasError = true;  //throws exception
225                     return false;
226                 }
227                 finally
228                 {
229                     //if (requestStream != null) {
230                     //    requestStream.Dispose();
231                     //}
232                 }
233 
234             }
235             else
236             {
237                 Connector.ReturnMessages.Add("Executing Session Request - Unable to encode/encrypt the content of the request!");
238                 Connector.hasError = true; //throws exception
239                 return false;
240             }
241 
242             HttpWebResponse response = (HttpWebResponse)request.GetResponse();
243             //can give webexception error 500
244 
245             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
246             {
247                 Stream responseStream = null;
248                 try
249                 {
250                     responseStream = request.GetResponse().GetResponseStream();
251                     using (StreamReader reader = new StreamReader(responseStream, true))
252                     {
253                         result = reader.ReadToEnd();
254                     }
255                 }
256                 catch (Exception e)
257                 {
258                     Connector.ReturnMessages.Add("Unable to execute the Session Request : " + Common.GetErrorInfo(e));
259                     Connector.hasError = true;  //throws exception
260                     return false;
261                 }
262                 finally
263                 {
264                     //if (responseStream != null) {
265                     //    responseStream.Dispose();
266                     //}
267                 }
268 
269                 if (result != null)
270                 {
271                     //check if the page returned is not just the login page - we need to get encoded & encrypted content here - check if start with <! 
272                     if (result.StartsWith("<!"))
273                     {
274                         //possible wrong password !
275                         Connector.ReturnMessages.Add("Executing Session Request -  Unable to get the session - Authentication/Password Issue");
276                         Connector.hasError = true;
277                         //throws exception
278                         return false;
279                     }
280 
281                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
282                     if (DecodeDecryptedContent.Initialize())
283                     {
284                         ArrayList arList = new ArrayList();
285 
286                         string[] ar = null;
287                         bool isError = false;
288 
289                         string message = "";
290                         string details = "";
291 
292                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
293                         arList.AddRange(ar);
294                         foreach (string str in arList)
295                         {
296                             //YES/NO
297                             if (str.StartsWith("Error: "))
298                             {
299                                 if (str.Replace("Error: ", "").Equals("YES", StringComparison.OrdinalIgnoreCase))
300                                 {
301                                     isError = true;
302                                 }
303                             }
304                             if (str.StartsWith("Message: "))
305                             {
306                                 message = str.Replace("Message: ", "");
307                             }
308                             if (str.StartsWith("Details: "))
309                             {
310                                 details = str.Replace("Details: ", "");
311                             }
312 
313                         }
314                         if (isError)
315                         {
316                             Connector.ReturnMessages.Add(message);
317                             Connector.ReturnMessages.Add(details);
318                             Connector.hasError = true;
319                             //throws exception
320                             return false;
321                         }
322                         else
323                         {
324                             Connector.ReturnMessages.Add(message);
325                             Connector.ReturnMessages.Add(details);
326                             Connector.hasError = false;
327                             //SUCCESS
328                         }
329 
330                     }
331                     else
332                     {
333                         Connector.ReturnMessages.Add("Executing Session Request - Unable to decode/decrypt the content of the response!");
334                         Connector.hasError = true;
335                         //throws exception
336                         return false;
337                     }
338                 }
339                 else
340                 {
341                     //no response from server
342                     Connector.ReturnMessages.Add("Executing Session Request -  Unable to get response from XPages!");
343                     Connector.hasError = true;
344                     //throws exception
345                     return false;
346 
347                 }
348                 return true;
349             }
350             else
351             {
352                 Connector.ReturnMessages.Add("Executing Session Request -  Unable to get the session - Authentication Issue");
353                 Connector.hasError = true;
354                 //throws exception
355                 return false;
356             }
357 
358         }
359         catch (Exception ex)
360         {
361             if (ex.GetType() == typeof(WebException))
362             {
363                 WebException webex;
364                 webex = (WebException)ex;
365                 if (webex.Response != null)
366                 {
367                     Stream stream = webex.Response.GetResponseStream();
368                     using (StreamReader reader = new StreamReader(stream))
369                     {
370                         string errorResp = reader.ReadToEnd();
371                         // errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\r\\n\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\r\\n", Environment.NewLine);
372                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
373                         Connector.ReturnMessages.Add("Unable to Execute the Session Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
374                         Connector.hasError = true;
375                         //throws exception
376                         return false;
377                     }
378 
379                 }
380                 else
381                 {
382                     Connector.ReturnMessages.Add("Unable to Execute the Session Request : " + Common.GetErrorInfo(ex));
383                     Connector.hasError = true;
384                     //throws exception
385                     return false;
386                 }
387             }
388             else
389             {
390                 Connector.ReturnMessages.Add("Unable to Execute the Session Request : " + Common.GetErrorInfo(ex));
391                 Connector.hasError = true;
392                 //throws exception
393                 return false;
394             }
395         }
396     }
397 
398     protected internal bool ExecuteDatabaseRequest(string WebServiceURL, string ServerName, string dbFilePath, string dbReplicationId, DatabaseObject dbObj)
399     {
400         HttpWebRequest request = null;
401 
402         try
403         {
404             if (!isInitialized)
405             {
406                 Connector.ReturnMessages.Add("Unable to execute the Database Request - Not Initialized/Connected To Server");
407                 Connector.hasError = true;
408                 return false;
409             }
410 
411             ServicePointManager.Expect100Continue = false;
412 
413             //create an HTTP request
414 
415             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Database");
416 
417             request.CookieContainer = this.AuthenticationCookie;
418             //  request.KeepAlive = True
419             request.AllowAutoRedirect = true;
420 
421             request.Method = WebRequestMethods.Http.Post;   //"POST"
422             request.ContentType = "application/jpi; charset=utf-8"; //application/json
423                                                                     // Set the request stream
424             string result;
425 
426             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
427             if (!AddIdentityHeader(ref request))
428             {
429                 Connector.ReturnMessages.Add("Unable to Execute Database Request : Identity Header could not be added!");
430                 Connector.hasError = true;  //throws error
431                 return false;
432             }
433 
434             //test with encryption
435             StringBuilder sb = new StringBuilder();
436 
437             sb.AppendLine("UserName: " + Connection.UserName);
438             sb.AppendLine("ServerName: " + ServerName);
439             sb.AppendLine("FilePath: " + dbFilePath);
440             sb.AppendLine("ReplicationID: " + dbReplicationId);
441 
442             sb.AppendLine("Connecting via XPagesAPI");
443 
444             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
445             string myString = Encoding.UTF8.GetString(b);
446             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
447             if (EncodedEncryptedContent.Initialize())
448             {
449                 Stream requestStream = null;
450                 try
451                 {
452                     requestStream = request.GetRequestStream();
453                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
454                     {
455                         writer.Write(EncodedEncryptedContent.EncodedContent);
456                     }
457                 }
458                 catch (Exception e)
459                 {
460                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
461                     Connector.hasError = true;  //throws exception
462                     return false;
463                 }
464                 finally
465                 {
466                     //if (requestStream != null) {
467                     //    requestStream.Dispose();
468                     //}
469                 }
470             }
471             else
472             {
473                 Connector.ReturnMessages.Add("Executing Database Request - Unable to encode/encrypt the content of the request!");
474                 Connector.hasError = true; //throws exception
475                 return false;
476             }
477 
478             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
479 
480             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
481             {
482                 Stream responseStream = null;
483                 try
484                 {
485                     responseStream = request.GetResponse().GetResponseStream();
486                     using (StreamReader reader = new StreamReader(responseStream, true))
487                     {
488                         result = reader.ReadToEnd();
489                     }
490                 }
491                 catch (Exception e)
492                 {
493                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
494                     Connector.hasError = true;  //throws exception
495                     return false;
496                 }
497                 finally
498                 {
499                     //if (responseStream != null) {
500                     //    responseStream.Dispose();
501                     //}
502                 }
503 
504                 if (result != null)
505                 {
506                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
507                     if (DecodeDecryptedContent.Initialize())
508                     {
509                         ArrayList arList = new ArrayList();
510 
511                         string[] ar = null;
512                         bool isError = false;
513 
514                         string message = "";
515                         string details = "";
516                         string value = "";
517 
518                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
519                         arList.AddRange(ar);
520                         foreach (string str in arList)
521                         {
522                             //YES/NO
523                             if (str.StartsWith("Error: "))
524                             {
525                                 if (str.Replace("Error: ", "").Equals("YES", StringComparison.OrdinalIgnoreCase))
526                                 {
527                                     isError = true;
528                                 }
529                             }
530                             if (str.StartsWith("Message: "))
531                             {
532                                 message = str.Replace("Message: ", "");
533                             }
534                             if (str.StartsWith("Details: "))
535                             {
536                                 details = str.Replace("Details: ", "");
537                             }
538                             if (str.StartsWith("ServerName: "))
539                             {
540                                 value = str.Replace("ServerName: ", "");
541                                 if (!string.IsNullOrEmpty(value))
542                                 {
543                                     dbObj.ServerName = value;
544                                 }
545                             }
546                             if (str.StartsWith("FilePath: "))
547                             {
548                                 value = str.Replace("FilePath: ", "");
549                                 if (!string.IsNullOrEmpty(value))
550                                 {
551                                     dbObj.FilePath = value;
552                                 }
553                             }
554                             if (str.StartsWith("ReplicationID: "))
555                             {
556                                 value = str.Replace("ReplicationID: ", "");
557                                 if (!string.IsNullOrEmpty(value))
558                                 {
559                                     dbObj.ReplicationID = value;
560                                 }
561                             }
562 
563                             if (str.StartsWith("Title: "))
564                             {
565                                 value = str.Replace("Title: ", "");
566                                 if (!string.IsNullOrEmpty(value))
567                                 {
568                                     dbObj.Title = value;
569                                 }
570                             }
571 
572                             if (str.StartsWith("Template: "))
573                             {
574                                 value = str.Replace("Template: ", "");
575                                 if (!string.IsNullOrEmpty(value))
576                                 {
577                                     dbObj.Template = value;
578                                 }
579                             }
580 
581                             if (str.StartsWith("Size: "))
582                             {
583                                 value = str.Replace("Size: ", "");
584                                 if (!string.IsNullOrEmpty(value))
585                                 {
586                                     dbObj.Size = value;
587                                 }
588                             }
589 
590                             if (str.StartsWith("URL: "))
591                             {
592                                 value = str.Replace("URL: ", "");
593                                 if (!string.IsNullOrEmpty(value))
594                                 {
595                                     dbObj.Url = value;
596                                 }
597                             }
598 
599                         }
600 
601                         if (isError)
602                         {
603                             Connector.ReturnMessages.Add(message);
604                             Connector.ReturnMessages.Add(details);
605                             Connector.hasError = true; //throws exception
606                             return false;
607                         }
608                         else
609                         {
610                             Connector.ReturnMessages.Add(message);
611                             Connector.ReturnMessages.Add(details);
612                             Connector.hasError = false; //SUCCESS
613                         }
614 
615                     }
616                     else
617                     {
618                         Connector.ReturnMessages.Add("Executing Database Request - Unable to decode/decrypt the content of the response!");
619                         Connector.hasError = true;  //throws exception
620                         return false;
621                     }
622                 }
623                 else
624                 {
625                     //no response from server
626                     Connector.ReturnMessages.Add("Executing Database Request -  Unable to get response from XPages!");
627                     Connector.hasError = true;  //throws exception
628                     return false;
629 
630                 }
631                 return true;
632             }
633             else
634             {
635                 Connector.ReturnMessages.Add("Executing Database Request -  Unable to get the Database - Authentication Issue");
636                 Connector.hasError = true;  //throws exception
637                 return false;
638             }
639 
640         }
641         catch (Exception ex)
642         {
643             if (ex.GetType() == typeof(WebException))
644             {
645                 WebException webex;
646                 webex = (WebException)ex;
647                 if (webex.Response != null)
648                 {
649                     Stream stream = webex.Response.GetResponseStream();
650                     using (StreamReader reader = new StreamReader(stream))
651                     {
652                         string errorResp = reader.ReadToEnd();
653                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
654                         Connector.ReturnMessages.Add("Unable to Execute the Database Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
655                         Connector.hasError = true;  //throws exception
656                         return false;
657                     }
658 
659                 }
660                 else
661                 {
662                     Connector.ReturnMessages.Add("Unable to Execute the Database Request : " + Common.GetErrorInfo(ex));
663                     Connector.hasError = true;  //throws exception
664                     return false;
665                 }
666             }
667             else
668             {
669                 Connector.ReturnMessages.Add("Unable to Execute the Database Request : " + Common.GetErrorInfo(ex));
670                 Connector.hasError = true;  //throws exception
671                 return false;
672             }
673         }
674     }
