676     protected internal bool ExecuteDocumentRequest(string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj)
677     {
678         HttpWebRequest request = null;
679 
680         try
681         {
682             if (!isInitialized)
683             {
684                 Connector.ReturnMessages.Add("Unable to execute the Document Request - Not Initialized/Connected To Server");
685                 Connector.hasError = true;
686                 return false;
687             }
688 
689             ServicePointManager.Expect100Continue = false;
690 
691             //create an HTTP request
692 
693             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Document");
694 
695             request.CookieContainer = this.AuthenticationCookie;
696             //  request.KeepAlive = True
697             request.AllowAutoRedirect = true;
698 
699             request.Method = WebRequestMethods.Http.Post;   //"POST"
700             request.ContentType = "application/jpi; charset=utf-8"; //application/json
701                                                                     // Set the request stream
702             string result;
703 
704             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
705             if (!AddIdentityHeader(ref request))
706             {
707                 Connector.ReturnMessages.Add("Unable to Execute Document Request : Identity Header could not be added!");
708                 Connector.hasError = true;  //throws error
709                 return false;
710             }
711 
712             //test with encryption
713             StringBuilder sb = new StringBuilder();
714 
715             sb.AppendLine("UserName: " + Connection.UserName);
716             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
717             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
718             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
719             sb.AppendLine("UniversalID: " + Unid);
720             sb.AppendLine("SearchField: " + searchField);
721             sb.AppendLine("SearchValue: " + searchValue);
722             sb.AppendLine("Formula: " + formula);
723             sb.AppendLine("Connecting via XPagesAPI");
724 
725             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
726             string myString = Encoding.UTF8.GetString(b);
727             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
728             if (EncodedEncryptedContent.Initialize())
729             {
730                 Stream requestStream = null;
731                 try
732                 {
733                     requestStream = request.GetRequestStream();
734                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
735                     {
736                         writer.Write(EncodedEncryptedContent.EncodedContent);
737                     }
738                 }
739                 catch (Exception e)
740                 {
741                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
742                     Connector.hasError = true;  //throws exception
743                     return false;
744                 }
745                 finally
746                 {
747                     //if (requestStream != null) {
748                     //    requestStream.Dispose();
749                     //}
750                 }
751             }
752             else
753             {
754                 Connector.ReturnMessages.Add("Executing Document Request - Unable to encode/encrypt the content of the request!");
755                 Connector.hasError = true; //throws exception
756                 return false;
757             }
758 
759             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
760 
761             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
762             {
763                 Stream responseStream = null;
764                 try
765                 {
766                     responseStream = request.GetResponse().GetResponseStream();
767                     using (StreamReader reader = new StreamReader(responseStream, true))
768                     {
769                         result = reader.ReadToEnd();
770                     }
771                 }
772                 catch (Exception e)
773                 {
774                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
775                     Connector.hasError = true;  //throws exception
776                     return false;
777                 }
778                 finally
779                 {
780                     //if (responseStream != null) {
781                     //    responseStream.Dispose();
782                     //}
783                 }
784 
785                 if (result != null)
786                 {
787                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
788                     if (DecodeDecryptedContent.Initialize())
789                     {
790                         ArrayList arList = new ArrayList();
791 
792                         string[] ar = null;
793                         bool isError = false;
794 
795                         string message = "";
796                         string details = "";
797                         string value = "";
798                         string unid = "";
799                         string noteId = "";
800                         string form = "";
801                         string size = "";
802                         string url = "";
803                         string created = "";
804                         string modified = "";
805 
806                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
807                         arList.AddRange(ar);
808 
809                         foreach (string str in arList)
810                         {
811                             //YES/NO
812                             if (str.StartsWith("Error: "))
813                             {
814                                 if (str.Replace("Error: ", "").Equals("YES", StringComparison.OrdinalIgnoreCase))
815                                 {
816                                     isError = true;
817                                 }
818                             }
819                             if (str.StartsWith("Message: "))
820                             {
821                                 message = str.Replace("Message: ", "");
822                             }
823                             if (str.StartsWith("Details: "))
824                             {
825                                 details = str.Replace("Details: ", "");
826                             }
827 
828                             if (str.StartsWith("NoteID: "))
829                             {
830                                 value = str.Replace("NoteID: ", "");
831                                 if (!string.IsNullOrEmpty(value))
832                                 {
833                                     noteId = value;
834                                 }
835                             }
836                             if (str.StartsWith("Unid: "))
837                             {
838                                 value = str.Replace("Unid: ", "");
839                                 if (!string.IsNullOrEmpty(value))
840                                 {
841                                     unid = value;
842                                 }
843                             }
844                             if (str.StartsWith("Form: "))
845                             {
846                                 value = str.Replace("Form: ", "");
847                                 if (!string.IsNullOrEmpty(value))
848                                 {
849                                     form = value;
850                                 }
851                             }
852 
853                             if (str.StartsWith("Size: "))
854                             {
855                                 value = str.Replace("Size: ", "");
856                                 if (!string.IsNullOrEmpty(value))
857                                 {
858                                     size = value;
859                                 }
860                             }
861 
862                             if (str.StartsWith("URL: "))
863                             {
864                                 value = str.Replace("URL: ", "");
865                                 if (!string.IsNullOrEmpty(value))
866                                 {
867                                     url = value;
868                                 }
869                             }
870 
871 
872                             if (str.StartsWith("Created: "))
873                             {
874                                 value = str.Replace("Created: ", "");
875                                 if (!string.IsNullOrEmpty(value))
876                                 {
877                                     created = value;
878                                 }
879                             }
880 
881 
882                             if (str.StartsWith("Modified: "))
883                             {
884                                 value = str.Replace("Modified: ", "");
885                                 if (!string.IsNullOrEmpty(value))
886                                 {
887                                     modified = value;
888                                 }
889                             }
890                         }
891 
892                         if (isError)
893                         {
894                             Connector.ReturnMessages.Add(message);
895                             Connector.ReturnMessages.Add(details);
896                             Connector.hasError = true; //throws exception
897                             return false;
898                         }
899                         else
900                         {
901 
902                             // add doc to list
903                             if (dbObj.Documents == null)
904                             {
905                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
906                             }
907                             if (!string.IsNullOrEmpty(unid))
908                             {
909                                 //docObj = new DocumentObject(dbObj, unid);
910                                 docObj.UniversalID = unid;
911                                 docObj.NoteID = noteId;
912                                 docObj.Size = size;
913                                 docObj.Url = url;
914                                 docObj.Form = form;
915                                 docObj.DateCreated = created;
916                                 docObj.DateModified = modified;
917                                 if (dbObj.Documents.ContainsKey(unid))
918                                 {
919                                     dbObj.Documents.Remove(unid);
920                                 }
921                                 dbObj.Documents.Add(unid, docObj);
922                                 Connector.ReturnMessages.Add(message);
923                                 Connector.ReturnMessages.Add(details);
924                                 Connector.hasError = false; //SUCCESS
925                             }
926                             else
927                             {
928                                 // unid not found??
929                                 Connector.ReturnMessages.Add(message);
930                                 Connector.ReturnMessages.Add(details);
931                                 Connector.ReturnMessages.Add("Universal ID not found!");
932                                 Connector.hasError = true; // throws exception
933                             }
934 
935                         }
936 
937                     }
938                     else
939                     {
940                         Connector.ReturnMessages.Add("Executing Document Request - Unable to decode/decrypt the content of the response!");
941                         Connector.hasError = true;  //throws exception
942                         return false;
943                     }
944                 }
945                 else
946                 {
947                     //no response from server
948                     Connector.ReturnMessages.Add("Executing Document Request -  Unable to get response from XPages!");
949                     Connector.hasError = true;  //throws exception
950                     return false;
951 
952                 }
953                 return true;
954             }
955             else
956             {
957                 Connector.ReturnMessages.Add("Executing Document Request -  Unable to get the Document - Authentication Issue");
958                 Connector.hasError = true;  //throws exception
959                 return false;
960             }
961 
962         }
963         catch (Exception ex)
964         {
965             if (ex.GetType() == typeof(WebException))
966             {
967                 WebException webex;
968                 webex = (WebException)ex;
969                 if (webex.Response != null)
970                 {
971                     Stream stream = webex.Response.GetResponseStream();
972                     using (StreamReader reader = new StreamReader(stream))
973                     {
974                         string errorResp = reader.ReadToEnd();
975                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
976                         Connector.ReturnMessages.Add("Unable to Execute the Document Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
977                         Connector.hasError = true;  //throws exception
978                         return false;
979                     }
980 
981                 }
982                 else
983                 {
984                     Connector.ReturnMessages.Add("Unable to Execute the Document Request : " + Common.GetErrorInfo(ex));
985                     Connector.hasError = true;  //throws exception
986                     return false;
987                 }
988             }
989             else
990             {
991                 Connector.ReturnMessages.Add("Unable to Execute the Document Request : " + Common.GetErrorInfo(ex));
992                 Connector.hasError = true;  //throws exception
993                 return false;
994             }
995         }
996     }
