998     protected internal bool ExecuteDocumentFilesRequest(string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj)
999     {
1000         HttpWebRequest request = null;
1001 
1002         try
1003         {
1004             if (!isInitialized)
1005             {
1006                 Connector.ReturnMessages.Add("Unable to execute the DocumentFiles Request - Not Initialized/Connected To Server");
1007                 Connector.hasError = true;
1008                 return false;
1009             }
1010 
1011             ServicePointManager.Expect100Continue = false;
1012 
1013             //create an HTTP request
1014 
1015             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Files");
1016 
1017             request.CookieContainer = this.AuthenticationCookie;
1018             //  request.KeepAlive = True
1019             request.AllowAutoRedirect = true;
1020 
1021             request.Method = WebRequestMethods.Http.Post;   //"POST"
1022             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1023                                                                     // Set the request stream
1024             string result;
1025 
1026             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1027             if (!AddIdentityHeader(ref request))
1028             {
1029                 Connector.ReturnMessages.Add("Unable to Execute DocumentFiles Request : Identity Header could not be added!");
1030                 Connector.hasError = true;  //throws error
1031                 return false;
1032             }
1033 
1034             //test with encryption
1035             StringBuilder sb = new StringBuilder();
1036 
1037             sb.AppendLine("UserName: " + Connection.UserName);
1038             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
1039             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
1040             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
1041             sb.AppendLine("UniversalID: " + Unid);
1042             sb.AppendLine("SearchField: " + searchField);
1043             sb.AppendLine("SearchValue: " + searchValue);
1044             sb.AppendLine("Formula: " + formula);
1045             sb.AppendLine("Connecting via XPagesAPI");
1046 
1047             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1048             string myString = Encoding.UTF8.GetString(b);
1049             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1050             if (EncodedEncryptedContent.Initialize())
1051             {
1052                 Stream requestStream = null;
1053                 try
1054                 {
1055                     requestStream = request.GetRequestStream();
1056                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
1057                     {
1058                         writer.Write(EncodedEncryptedContent.EncodedContent);
1059                     }
1060                 }
1061                 catch (Exception e)
1062                 {
1063                     Connector.ReturnMessages.Add("Unable to execute the DocumentFiles Request : " + Common.GetErrorInfo(e));
1064                     Connector.hasError = true;  //throws exception
1065                     return false;
1066                 }
1067                 finally
1068                 {
1069                     //if (requestStream != null) {
1070                     //    requestStream.Dispose();
1071                     //}
1072                 }
1073             }
1074             else
1075             {
1076                 Connector.ReturnMessages.Add("Executing DocumentFiles Request - Unable to encode/encrypt the content of the request!");
1077                 Connector.hasError = true; //throws exception
1078                 return false;
1079             }
1080 
1081             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1082 
1083             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
1084             {
1085                 Stream responseStream = null;
1086                 try
1087                 {
1088                     responseStream = request.GetResponse().GetResponseStream();
1089                     using (StreamReader reader = new StreamReader(responseStream, true))
1090                     {
1091                         result = reader.ReadToEnd();
1092                     }
1093                 }
1094                 catch (Exception e)
1095                 {
1096                     Connector.ReturnMessages.Add("Unable to execute the DocumentFiles Request : " + Common.GetErrorInfo(e));
1097                     Connector.hasError = true;  //throws exception
1098                     return false;
1099                 }
1100                 finally
1101                 {
1102                     //if (responseStream != null) {
1103                     //    responseStream.Dispose();
1104                     //}
1105                 }
1106 
1107                 if (result != null)
1108                 {
1109                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1110                     if (DecodeDecryptedContent.Initialize())
1111                     {
1112                         ArrayList arList = new ArrayList();
1113 
1114                         string[] ar = null;
1115                         bool isError = false;
1116 
1117                         string message = "";
1118                         string details = "";
1119                         string value = "";
1120                         string unid = "";
1121                         string noteId = "";
1122                         string form = "";
1123                         string size = "";
1124                         string url = "";
1125                         string created = "";
1126                         string modified = "";
1127                         List<string> fList = new List<String>();
1128 
1129 
