1143                             if (str.StartsWith("Message: "))
1144                             {
1145                                 message = str.Replace("Message: ", "");
1146                             }
1147                             if (str.StartsWith("Details: "))
1148                             {
1149                                 details = str.Replace("Details: ", "");
1150                             }
1151 
1152                             if (str.StartsWith("NoteID: "))
1153                             {
1154                                 value = str.Replace("NoteID: ", "");
1155                                 if (!string.IsNullOrEmpty(value))
