1439                     using (StreamReader reader = new StreamReader(responseStream, true))
1440                     {
1441                         result = reader.ReadToEnd();
1442                     }
1443                 }
1444                 catch (Exception e)
1445                 {
1446                     Connector.ReturnMessages.Add("Unable to execute the All Documents Request : " + Common.GetErrorInfo(e));
1447                     Connector.hasError = true;  //throws exception
1448                     return false;
1449                 }
1450                 finally
1451                 {
1452                     //if (responseStream != null) {
1453                     //    responseStream.Dispose();
1454                     //}
1455                 }
1456 
1457                 if (result != null)
1458                 {
1459                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1460                     if (DecodeDecryptedContent.Initialize())
1461                     {
1462                         ArrayList arList = new ArrayList();
1463 
1464                         string[] ar = null;
1465                         bool isError = false;
1466 
1467                         string message = "";
1468                         string details = "";
1469                         string value = "";
1470                         string[] sep = { "||" };
1471                         string[] NoteIds = { };
1472                         string[] Unids = { };
1473                         string[] Forms = { };
1474                         string[] Sizes = { };
1475                         string[] URLs = { };
1476                         string[] Created = { };
1477                         string[] Modified = { };
1478 
1479                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1480                         arList.AddRange(ar);
1481                         foreach (string str in arList)
1482                         {
1483                             //YES/NO
1484                             if (str.StartsWith("Error: "))
1485                             {
1486                                 if (str.Replace("Error: ", "").Equals("YES", StringComparison.OrdinalIgnoreCase))
1487                                 {
1488                                     isError = true;
1489                                 }
1490                             }
1491                             if (str.StartsWith("Message: "))
1492                             {
1493                                 message = str.Replace("Message: ", "");
1494                             }
1495                             if (str.StartsWith("Details: "))
1496                             {
1497                                 details = str.Replace("Details: ", "");
1498                             }
1499 
1500                             if (str.StartsWith("Unids: "))
1501                             {
1502                                 value = str.Replace("Unids: ", "");
1503                                 if (!string.IsNullOrEmpty(value))
1504                                 {
1505                                     Unids = value.Split(sep, StringSplitOptions.None);
1506                                 }
1507                             }
1508 
1509                             if (str.StartsWith("NoteIDs: "))
1510                             {
1511                                 value = str.Replace("NoteIDs: ", "");
1512                                 if (!string.IsNullOrEmpty(value))
1513                                 {
1514                                     NoteIds = value.Split(sep, StringSplitOptions.None);
1515                                 }
1516                             }
1517 
1518                             if (str.StartsWith("Forms: "))
1519                             {
1520                                 value = str.Replace("Forms: ", "");
1521                                 if (!string.IsNullOrEmpty(value))
1522                                 {
1523                                     Forms = value.Split(sep, StringSplitOptions.None);
1524                                 }
1525                             }
1526 
1527                             if (str.StartsWith("Sizes: "))
1528                             {
1529                                 value = str.Replace("Sizes: ", "");
1530                                 if (!string.IsNullOrEmpty(value))
1531                                 {
1532                                     Sizes = value.Split(sep, StringSplitOptions.None);
1533                                 }
1534                             }
1535 
1536                             if (str.StartsWith("URLs: "))
1537                             {
1538                                 value = str.Replace("URLs: ", "");
1539                                 if (!string.IsNullOrEmpty(value))
1540                                 {
1541                                     URLs = value.Split(sep, StringSplitOptions.None);
1542                                 }
1543                             }
1544 
1545 
1546                             if (str.StartsWith("Created: "))
1547                             {
1548                                 value = str.Replace("Created: ", "");
1549                                 if (!string.IsNullOrEmpty(value))
1550                                 {
1551                                     Created = value.Split(sep, StringSplitOptions.None);
1552                                 }
1553                             }
1554 
1555 
1556                             if (str.StartsWith("Modified: "))
1557                             {
1558                                 value = str.Replace("Modified: ", "");
1559                                 if (!string.IsNullOrEmpty(value))
1560                                 {
1561                                     Modified = value.Split(sep, StringSplitOptions.None);
1562                                 }
1563                             }
1564                         }
1565 
1566                         //loop through unids, notesids, forms, sizes, urls and create document objects
1567                         // check if all have the same number of items!
1568                         int count = 0;
1569                         if (Unids != null && Unids.Length > 0)
1570                         {
1571                             count = Unids.Length;
1572                             if (NoteIds != null && NoteIds.Length > 0)
1573                             {
1574                                 if (count != NoteIds.Length)
1575                                 {
1576                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> NoteIds");
1577                                     Connector.hasError = true;  //throws exception
1578                                     return false;
1579                                 }
1580                                 else
1581                                 {
1582                                     // form can be empty!!
