1593                                             Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Sizes");
1594                                             Connector.hasError = true;  //throws exception
1595                                             return false;
1596                                         }
1597                                         else
1598                                         {
1599                                             if (URLs != null && URLs.Length > 0)
1600                                             {
1601                                                 if (count != URLs.Length)
1602                                                 {
1603                                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> URLs");
1604                                                     Connector.hasError = true;  //throws exception
1605                                                     return false;
1606                                                 }
1607                                             }
1608                                         }
1609                                     }
1610                                     //   }
1611                                     // }
1612                                 }
1613                             }
1614                             // loop through and create doc obj
1615                             DocumentObject docObj = null;
1616                             if (dbObj.Documents == null)
1617                             {
1618                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
1619                             }
1620                             for (int i = 0; i < Unids.Length; i++)
1621                             {
1622                                 docObj = new DocumentObject(dbObj, Unids[i]);
1623                                 if (NoteIds.Length - 1 >= i)
1624                                 {
1625                                     docObj.NoteID = NoteIds[i];
1626                                 }
1627                                 if (Forms.Length - 1 >= i)
1628                                 {
1629                                     docObj.Form = Forms[i];
1630                                 }
1631                                 if (Sizes.Length - 1 >= i)
1632                                 {
1633                                     docObj.Size = Sizes[i];
1634                                 }
1635                                 if (URLs.Length - 1 >= i)
1636                                 {
1637                                     docObj.Url = URLs[i];
1638                                 }
1639                                 if (Created.Length - 1 >= i)
1640                                 {
1641                                     docObj.DateCreated = Created[i];
1642                                 }
1643                                 if (Modified.Length - 1 >= i)
1644                                 {
1645                                     docObj.DateModified = Modified[i];
1646                                 }
1647                                 if (dbObj.Documents.ContainsKey(Unids[i]))
1648                                 {
1649                                     dbObj.Documents.Remove(Unids[i]);
1650                                 }
1651                                 docObj.IsInitialized = true;
1652                                 dbObj.Documents.Add(Unids[i], docObj);
1653                             }
1654                         }
1655 
1656 
1657 
1658                         if (isError)
1659                         {
1660                             Connector.ReturnMessages.Add(message);
1661                             Connector.ReturnMessages.Add(details);
1662                             Connector.hasError = true; //throws exception
1663                             return false;
1664                         }
1665                         else
1666                         {
1667                             Connector.ReturnMessages.Add(message);
1668                             Connector.ReturnMessages.Add(details);
1669                             Connector.hasError = false; //SUCCESS
1670                         }
1671 
1672                     }
1673                     else
1674                     {
1675                         Connector.ReturnMessages.Add("Executing All Documents Request - Unable to decode/decrypt the content of the response!");
1676                         Connector.hasError = true;  //throws exception
1677                         return false;
1678                     }
1679                 }
1680                 else
1681                 {
1682                     //no response from server
1683                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get response from XPages!");
1684                     Connector.hasError = true;  //throws exception
1685                     return false;
1686 
1687                 }
1688                 return true;
1689             }
1690             else
1691             {
1692                 Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Document - Authentication Issue");
1693                 Connector.hasError = true;  //throws exception
1694                 return false;
1695             }
1696 
1697         }
1698         catch (Exception ex)
1699         {
1700             if (ex.GetType() == typeof(WebException))
1701             {
1702                 WebException webex;
1703                 webex = (WebException)ex;
1704                 if (webex.Response != null)
1705                 {
1706                     Stream stream = webex.Response.GetResponseStream();
1707                     using (StreamReader reader = new StreamReader(stream))
1708                     {
1709                         string errorResp = reader.ReadToEnd();
1710                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1711                         Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1712                         Connector.hasError = true;  //throws exception
1713                         return false;
1714                     }
1715 
1716                 }
1717                 else
1718                 {
1719                     Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1720                     Connector.hasError = true;  //throws exception
1721                     return false;
1722                 }
1723             }
1724             else
1725             {
1726                 Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1727                 Connector.hasError = true;  //throws exception
1728                 return false;
1729             }
1730         }
1731     }
