1733     protected internal bool ExecuteAllDocumentsFilesRequest(string WebServiceURL, string searchField, string searchValue, string formula, string unids, DatabaseObject dbObj)
1734     {
1735         HttpWebRequest request = null;
1736 
1737         try
1738         {
1739             if (!isInitialized)
1740             {
1741                 Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request - Not Initialized/Connected To Server");
1742                 Connector.hasError = true;
1743                 return false;
1744             }
1745 
1746             ServicePointManager.Expect100Continue = false;
1747 
1748             //create an HTTP request
1749 
1750             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFiles");
1751             request.Timeout = 300000; // set timeout to 5minutes
1752             request.CookieContainer = this.AuthenticationCookie;
1753             //  request.KeepAlive = True
1754             request.AllowAutoRedirect = true;
1755 
1756             request.Method = WebRequestMethods.Http.Post;   //"POST"
1757             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1758                                                                     // Set the request stream
1759             string result;
1760 
1761             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1762             if (!AddIdentityHeader(ref request))
1763             {
1764                 Connector.ReturnMessages.Add("Unable to Execute AllDocumentsFiles Request : Identity Header could not be added!");
1765                 Connector.hasError = true;  //throws error
1766                 return false;
1767             }
1768 
1769             //test with encryption
1770             StringBuilder sb = new StringBuilder();
1771 
1772             sb.AppendLine("UserName: " + Connection.UserName);
1773             sb.AppendLine("ServerName: " + dbObj.ServerName);
1774             sb.AppendLine("FilePath: " + dbObj.FilePath);
1775             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
1776             sb.AppendLine("SearchField: " + searchField);
1777             sb.AppendLine("SearchValue: " + searchValue);
1778             sb.AppendLine("Formula: " + formula);
1779             sb.AppendLine("Unids: " + unids);
1780             sb.AppendLine("Connecting via XPagesAPI");
1781 
1782             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1783             string myString = Encoding.UTF8.GetString(b);
1784             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1785             if (EncodedEncryptedContent.Initialize())
1786             {
1787                 Stream requestStream = null;
1788                 try
1789                 {
1790                     requestStream = request.GetRequestStream();
1791                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
1792                     {
1793                         writer.Write(EncodedEncryptedContent.EncodedContent);
1794                     }
1795                 }
1796                 catch (Exception e)
1797                 {
1798                     Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(e));
1799                     Connector.hasError = true;  //throws exception
1800                     return false;
1801                 }
1802                 finally
1803                 {
1804                     //if (requestStream != null) {
1805                     //    requestStream.Dispose();
1806                     //}
1807                 }
1808             }
1809             else
1810             {
1811                 Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request - Unable to encode/encrypt the content of the request!");
1812                 Connector.hasError = true; //throws exception
1813                 return false;
1814             }
1815 
1816             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1817 
1818             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
1819             {
1820                 Stream responseStream = null;
1821                 try
1822                 {
1823                     responseStream = request.GetResponse().GetResponseStream();
1824                     using (StreamReader reader = new StreamReader(responseStream, true))
1825                     {
1826                         result = reader.ReadToEnd();
1827                     }
1828                 }
1829                 catch (Exception e)
1830                 {
1831                     Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(e));
1832                     Connector.hasError = true;  //throws exception
1833                     return false;
1834                 }
1835                 finally
1836                 {
1837                     //if (responseStream != null) {
1838                     //    responseStream.Dispose();
1839                     //}
1840                 }
1841 
1842                 if (result != null)
1843                 {
1844                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1845                     if (DecodeDecryptedContent.Initialize())
1846                     {
1847                         ArrayList arList = new ArrayList();
1848 
1849                         string[] ar = null;
1850                         bool isError = false;
1851 
