2115         catch (Exception ex)
2116         {
2117             if (ex.GetType() == typeof(WebException))
2118             {
2119                 WebException webex;
2120                 webex = (WebException)ex;
2121                 if (webex.Response != null)
2122                 {
2123                     Stream stream = webex.Response.GetResponseStream();
2124                     using (StreamReader reader = new StreamReader(stream))
2125                     {
2126                         string errorResp = reader.ReadToEnd();
2127                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2128                         Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2129                         Connector.hasError = true;  //throws exception
2130                         return false;
2131                     }
2132 
2133                 }
2134                 else
2135                 {
2136                     Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
2137                     Connector.hasError = true;  //throws exception
2138                     return false;
2139                 }
2140             }
2141             else
2142             {
2143                 Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
2144                 Connector.hasError = true;  //throws exception
2145                 return false;
2146             }
2147         }
