2150     protected internal bool ExecuteFieldsRequest(string WebServiceURL, DocumentObject docObj, string fields)
2151     {
2152         HttpWebRequest request = null;
2153 
2154         try
2155         {
2156             if (!isInitialized)
2157             {
2158                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
2159                 Connector.hasError = true;
2160                 return false;
2161             }
2162 
2163             ServicePointManager.Expect100Continue = false;
2164 
2165             //create an HTTP request
2166 
2167             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Fields");
2168 
2169             request.CookieContainer = this.AuthenticationCookie;
2170             //  request.KeepAlive = True
2171             request.AllowAutoRedirect = true;
2172 
2173             request.Method = WebRequestMethods.Http.Post;   //"POST"
2174             request.ContentType = "application/jpi; charset=utf-8"; //application/json
2175                                                                     // Set the request stream
2176             string result;
2177 
2178             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
2179             if (!AddIdentityHeader(ref request))
2180             {
2181                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
2182                 Connector.hasError = true;  //throws error
2183                 return false;
2184             }
2185 
2186             //test with encryption
2187             StringBuilder sb = new StringBuilder();
2188 
2189             sb.AppendLine("UserName: " + Connection.UserName);
2190             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
2191             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
2192             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
2193             sb.AppendLine("UniversalID: " + docObj.UniversalID);
2194             sb.AppendLine("Fields: " + fields);
2195             sb.AppendLine("Connecting via XPagesAPI");
2196 
2197             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
2198             string myString = Encoding.UTF8.GetString(b);
2199             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
2200             if (EncodedEncryptedContent.Initialize())
2201             {
2202                 Stream requestStream = null;
2203                 try
2204                 {
2205                     requestStream = request.GetRequestStream();
2206                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
2207                     {
2208                         writer.Write(EncodedEncryptedContent.EncodedContent);
2209                     }
2210                 }
2211                 catch (Exception e)
2212                 {
2213                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2214                     Connector.hasError = true;  //throws exception
2215                     return false;
2216                 }
2217                 finally
2218                 {
2219                     //if (requestStream != null) {
2220                     //    requestStream.Dispose();
2221                     //}
2222                 }
2223             }
2224             else
2225             {
2226                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
2227                 Connector.hasError = true; //throws exception
2228                 return false;
2229             }
2230 
2231             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
2232 
2233             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
2234             {
2235 
2236                 Stream responseStream = null;
2237                 try
2238                 {
2239                     responseStream = request.GetResponse().GetResponseStream();
2240                     using (StreamReader reader = new StreamReader(responseStream, true))
2241                     {
2242                         result = reader.ReadToEnd();
2243                     }
2244                 }
2245                 catch (Exception e)
2246                 {
2247                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2248                     Connector.hasError = true;  //throws exception
2249                     return false;
2250                 }
2251                 finally
2252                 {
2253                     //if (responseStream != null) {
2254                     //    responseStream.Dispose();
2255                     //}
2256                 }
2257 
2258                 if (result != null)
2259                 {
2260                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2261                     if (DecodeDecryptedContent.Initialize())
2262                     {
2263                         ArrayList arList = new ArrayList();
2264 
2265                         string[] ar = null;
2266                         bool isError = false;
2267 
2268                         string message = "";
2269                         string details = "";
2270                         string value = "";
2271                         string[] fieldnames = null;
2272                         string[] fieldvalues = null;
2273                         string[] fieldtypes = null;
2274 
2275                         string[] sep = { "||" };
2276 
2277                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2278                         arList.AddRange(ar);
2279                         foreach (string str in arList)
2280                         {
2281                             //YES/NO
2282                             if (str.StartsWith("Error: "))
2283                             {
2284                                 if (str.Replace("Error: ", "").Equals("YES", StringComparison.OrdinalIgnoreCase))
2285                                 {
2286                                     isError = true;
2287                                 }
2288                             }
2289                             if (str.StartsWith("Message: "))
2290                             {
2291                                 message = str.Replace("Message: ", "");
2292                             }
2293                             if (str.StartsWith("Details: "))
2294                             {
2295                                 details = str.Replace("Details: ", "");
2296                             }
2297 
2298                             if (str.StartsWith("FieldNames: "))
2299                             {
2300                                 value = str.Replace("FieldNames: ", "");
2301                                 if (!string.IsNullOrEmpty(value))
2302                                 {
2303                                     fieldnames = value.Split(sep, StringSplitOptions.None);
2304                                 }
2305                             }
2306 
2307                             if (str.StartsWith("FieldValues: "))
2308                             {
2309                                 value = str.Replace("FieldValues: ", "");
2310                                 if (!string.IsNullOrEmpty(value))
2311                                 {
2312                                     fieldvalues = value.Split(sep, StringSplitOptions.None);
2313                                 }
2314                             }
2315 
2316                             if (str.StartsWith("FieldTypes: "))
2317                             {
2318                                 value = str.Replace("FieldTypes: ", "");
2319                                 if (!string.IsNullOrEmpty(value))
2320                                 {
2321                                     fieldtypes = value.Split(sep, StringSplitOptions.None);
2322                                 }
2323                             }
2324 
2325                         }
2326                         FieldObject fObj = null;
2327                         if (docObj.Fields == null)
2328                         {
2329                             docObj.Fields = new SortedDictionary<string, FieldObject>();
2330                         }
2331                         for (int i = 0; i < fieldnames.Length; i++)
2332                         {
2333                             fObj = new FieldObject(fieldnames[i]);
2334                             if (fieldvalues.Length - 1 >= i)
2335                             {
2336                                 fObj.Value = fieldvalues[i];
2337                             }
2338                             if (fieldtypes.Length - 1 >= i)
2339                             {
2340                                 fObj.Type = fieldtypes[i];
2341                             }
2342 
2343                             if (docObj.Fields.ContainsKey(fObj.Name))
2344                             {
2345                                 docObj.Fields.Remove(fObj.Name);
2346                             }
2347                             docObj.Fields.Add(fObj.Name, fObj);
2348 
2349                         }
2350 
2351                         if (isError)
2352                         {
2353                             Connector.ReturnMessages.Add(message);
2354                             Connector.ReturnMessages.Add(details);
2355                             Connector.hasError = true; //throws exception
2356                             return false;
2357                         }
2358                         else
2359                         {
2360                             Connector.ReturnMessages.Add(message);
2361                             Connector.ReturnMessages.Add(details);
2362                             Connector.hasError = false; //SUCCESS
2363                         }
2364 
2365                     }
2366                     else
2367                     {
2368                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
2369                         Connector.hasError = true;  //throws exception
2370                         return false;
2371                     }
2372                 }
2373                 else
2374                 {
2375                     //no response from server
2376                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
2377                     Connector.hasError = true;  //throws exception
2378                     return false;
2379 
2380                 }
2381                 return true;
2382             }
2383             else
2384             {
2385                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
2386                 Connector.hasError = true;  //throws exception
2387                 return false;
2388             }
2389 
2390         }
2391         catch (Exception ex)
2392         {
2393             if (ex.GetType() == typeof(WebException))
2394             {
2395                 WebException webex;
2396                 webex = (WebException)ex;
2397                 if (webex.Response != null)
2398                 {
2399                     Stream stream = webex.Response.GetResponseStream();
2400                     using (StreamReader reader = new StreamReader(stream))
2401                     {
2402                         string errorResp = reader.ReadToEnd();
2403                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2404                         Connector.ReturnMessages.Add("Unable to Execute the Fields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2405                         Connector.hasError = true;  //throws exception
2406                         return false;
2407                     }
2408 
2409                 }
2410                 else
2411                 {
2412                     Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2413                     Connector.hasError = true;  //throws exception
2414                     return false;
2415                 }
2416             }
2417             else
2418             {
2419                 Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2420                 Connector.hasError = true;  //throws exception
2421                 return false;
2422             }
2423         }
2424     }
