2835                     Connector.hasError = true;  //throws exception
2836                     return false;
2837                 }
2838             }
2839             else
2840             {
2841                 Connector.ReturnMessages.Add("Unable to Execute the All Files Request : " + Common.GetErrorInfo(ex));
2842                 Connector.hasError = true;  //throws exception
