2848     protected internal bool ExecuteAllFieldsRequest(string WebServiceURL, DatabaseObject dbObj, string fields)
2849     {
2850         HttpWebRequest request = null;
2851 
2852         try
2853         {
2854             if (!isInitialized)
2855             {
2856                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
2857                 Connector.hasError = true;
2858                 return false;
2859             }
2860 
2861             ServicePointManager.Expect100Continue = false;
2862 
2863             //create an HTTP request
2864 
2865             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFields");
2866             request.Timeout = 300000; // set timeout to 5minutes
2867             request.CookieContainer = this.AuthenticationCookie;
2868             //  request.KeepAlive = True
2869             request.AllowAutoRedirect = true;
2870 
2871             request.Method = WebRequestMethods.Http.Post;   //"POST"
2872             request.ContentType = "application/jpi; charset=utf-8"; //application/json
2873                                                                     // Set the request stream
2874             string result;
2875 
2876             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
2877             if (!AddIdentityHeader(ref request))
2878             {
2879                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
2880                 Connector.hasError = true;  //throws error
2881                 return false;
2882             }
2883 
2884             //test with encryption
2885             StringBuilder sb = new StringBuilder();
2886 
2887             sb.AppendLine("UserName: " + Connection.UserName);
2888             sb.AppendLine("ServerName: " + dbObj.ServerName);
2889             sb.AppendLine("FilePath: " + dbObj.FilePath);
2890             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
