2899             sb.AppendLine("Fields: " + fields);
2900 
2901             sb.AppendLine("Connecting via XPagesAPI");
2902 
2903             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
2904             string myString = Encoding.UTF8.GetString(b);
2905             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
2906             if (EncodedEncryptedContent.Initialize())
2907             {
2908                 Stream requestStream = null;
2909                 try
2910                 {
2911                     requestStream = request.GetRequestStream();
2912                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
2913                     {
2914                         writer.Write(EncodedEncryptedContent.EncodedContent);
2915                     }
2916                 }
2917                 catch (Exception e)
2918                 {
2919                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2920                     Connector.hasError = true;  //throws exception
2921                     return false;
2922                 }
2923                 finally
2924                 {
2925                     //if (requestStream != null) {
2926                     //    requestStream.Dispose();
2927                     //}
2928                 }
2929             }
2930             else
2931             {
2932                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
2933                 Connector.hasError = true; //throws exception
2934                 return false;
2935             }
2936 
2937             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
2938 
2939             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
2940             {
2941                 Stream responseStream = null;
2942                 try
2943                 {
2944                     responseStream = request.GetResponse().GetResponseStream();
2945                     using (StreamReader reader = new StreamReader(responseStream, true))
2946                     {
2947                         result = reader.ReadToEnd();
2948                     }
2949                 }
2950                 catch (Exception e)
2951                 {
2952                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2953                     Connector.hasError = true;  //throws exception
2954                     return false;
2955                 }
2956                 finally
2957                 {
2958                     //if (responseStream != null) {
2959                     //    responseStream.Dispose();
2960                     //}
2961                 }
2962 
2963                 if (result != null)
2964                 {
2965                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2966                     if (DecodeDecryptedContent.Initialize())
2967                     {
2968                         ArrayList arList = new ArrayList();
2969 
2970                         string[] ar = null;
2971                         bool isError = false;
2972 
2973                         string message = "";
2974                         string details = "";
2975                         string value = "";
2976                         string unid = "";
