2978                         string[] fieldnames = null;
2979                         string[] fieldvalues = null;
2980                         string[] fieldtypes = null;
2981 
2982                         string[] sep = { "||" };
2983 
2984                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2985                         arList.AddRange(ar);
2986                         foreach (string str in arList)
2987                         {
2988                             //YES/NO
2989                             if (str.StartsWith("Error: "))
2990                             {
2991                                 if (str.Replace("Error: ", "").Equals("YES", StringComparison.OrdinalIgnoreCase))
2992                                 {
2993                                     isError = true;
2994                                 }
2995                             }
2996                             if (str.StartsWith("Message: "))
2997                             {
2998                                 message = str.Replace("Message: ", "");
2999                             }
3000                             if (str.StartsWith("Details: "))
3001                             {
3002                                 details = str.Replace("Details: ", "");
3003                             }
3004 
3005                             // 'FieldNames: Unid: '
3006                             if (str.StartsWith("FieldNames: "))
3007                             {
3008                                 value = str.Replace("FieldNames: ", "");
3009                                 //get the unid C125760F0054EEFBC1257356002F192D
3010                                 if (value.Length > 34)
3011                                 { //Unid is 32 + ': '
3012                                     unid = value.Substring(0, 32);
3013                                     //remove unid
3014                                     value = value.Remove(0, 34);
3015                                     if (!string.IsNullOrEmpty(value))
3016                                     {
3017                                         fieldnames = value.Split(sep, StringSplitOptions.None);
3018                                         // check if item is already in dict
3019                                         if (!dict.ContainsKey(unid))
3020                                         {
3021                                             dict.Add(unid, new List<string[]>());
3022                                         }
3023                                         dict[unid].Add(fieldnames);
3024                                     }
3025 
3026                                 }// else something is wrong
3027 
3028 
3029                             }
3030 
3031                             if (str.StartsWith("FieldValues: "))
3032                             {
3033                                 value = str.Replace("FieldValues: ", "");
3034                                 if (value.Length > 34)
3035                                 { //Unid is 32 + ': '
3036                                     unid = value.Substring(0, 32);
3037                                     //remove unid
3038                                     value = value.Remove(0, 34);
3039                                     if (!string.IsNullOrEmpty(value))
3040                                     {
3041                                         fieldvalues = value.Split(sep, StringSplitOptions.None);
3042                                         // check if item is already in dict
3043                                         if (!dict.ContainsKey(unid))
3044                                         {
3045                                             dict.Add(unid, new List<string[]>());
3046                                         }
3047                                         dict[unid].Add(fieldvalues);
3048                                     }
3049 
3050                                 }
3051                             }
3052 
3053 
3054                             if (str.StartsWith("FieldTypes: "))
3055                             {
3056                                 value = str.Replace("FieldTypes: ", "");
3057                                 if (value.Length > 34)
3058                                 { //Unid is 32 + ': '
3059                                     unid = value.Substring(0, 32);
3060                                     //remove unid
3061                                     value = value.Remove(0, 34);
3062                                     if (!string.IsNullOrEmpty(value))
3063                                     {
3064                                         fieldtypes = value.Split(sep, StringSplitOptions.None);
3065                                         // check if item is already in dict
3066                                         if (!dict.ContainsKey(unid))
3067                                         {
3068                                             dict.Add(unid, new List<string[]>());
3069                                         }
3070                                         dict[unid].Add(fieldtypes);
3071                                     }
3072 
3073                                 }
3074                             }
3075                         }
3076 
3077                         DocumentObject docObj = null;
3078                         FieldObject fObj = null;
3079 
3080                         foreach (KeyValuePair<string, List<string[]>> kvp in dict)
3081                         {
3082                             if (dbObj.Documents.ContainsKey(kvp.Key))
3083                             {
3084                                 docObj = dbObj.Documents[kvp.Key];
3085                                 if (docObj.Fields == null)
3086                                 {
3087                                     docObj.Fields = new SortedDictionary<string, FieldObject>();
3088                                 }
3089                                 //reset
3090                                 fieldnames = null;
3091                                 fieldvalues = null;
3092                                 fieldtypes = null;
3093                                 // first array in list should be fieldnames, then values then types
3094                                 fieldnames = kvp.Value[0];
3095                                 fieldvalues = kvp.Value[1];
3096                                 fieldtypes = kvp.Value[2];
3097 
3098                                 for (int i = 0; i < fieldnames.Length; i++)
3099                                 {//fieldnames
3100                                     fObj = new FieldObject(fieldnames[i]);
3101                                     if (fieldvalues.Length - 1 >= i)
3102                                     {
3103                                         fObj.Value = fieldvalues[i];
3104                                     }
3105                                     if (fieldtypes.Length - 1 >= i)
3106                                     {
3107                                         fObj.Type = fieldtypes[i];
3108                                     }
3109 
3110                                     if (docObj.Fields.ContainsKey(fObj.Name))
3111                                     {
3112                                         docObj.Fields.Remove(fObj.Name);
3113                                     }
3114                                     docObj.Fields.Add(fObj.Name, fObj);
3115 
3116                                 }
3117                             }
3118                             else
3119                             {
3120                                 Connector.ReturnMessages.Add("Unable to find the document in the databaseobject :" + kvp.Key);
3121                                 isError = true;
3122                             }
3123 
3124                         }
3125 
3126                         if (isError)
3127                         {
3128                             Connector.ReturnMessages.Add(message);
3129                             Connector.ReturnMessages.Add(details);
3130                             Connector.hasError = true; //throws exception
3131                             return false;
3132                         }
3133                         else
3134                         {
3135                             Connector.ReturnMessages.Add(message);
3136                             Connector.ReturnMessages.Add(details);
3137                             Connector.hasError = false; //SUCCESS
3138                         }
3139 
3140                     }
3141                     else
3142                     {
3143                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
3144                         Connector.hasError = true;  //throws exception
3145                         return false;
3146                     }
3147                 }
3148                 else
3149                 {
3150                     //no response from server
3151                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
3152                     Connector.hasError = true;  //throws exception
3153                     return false;
3154 
3155                 }
3156                 return true;
3157             }
3158             else
3159             {
3160                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
3161                 Connector.hasError = true;  //throws exception
3162                 return false;
3163             }
3164 
3165         }
3166         catch (Exception ex)
3167         {
3168             if (ex.GetType() == typeof(WebException))
3169             {
3170                 WebException webex;
3171                 webex = (WebException)ex;
3172                 if (webex.Response != null)
3173                 {
3174                     Stream stream = webex.Response.GetResponseStream();
3175                     using (StreamReader reader = new StreamReader(stream))
3176                     {
3177                         string errorResp = reader.ReadToEnd();
3178                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
3179                         Connector.ReturnMessages.Add("Unable to Execute the All Fields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
