3201     protected internal bool ExecuteGetFileRequest(string FileURL, string LocalFilePath)
3202     {
3203         HttpWebRequest request = null;  
3204         try
3205         {
3206 
3207             if (!isInitialized)
3208             {
3209                 Connector.ReturnMessages.Add("Unable to execute the Get File Request - Not Initialized/Connected To Server");
3210                 Connector.hasError = true;
3211                 return false;
3212             }
3213 
3214             request = request = (HttpWebRequest)WebRequest.Create(FileURL);
3215         
3216             request.CookieContainer = AuthenticationCookie;
3217             request.AllowAutoRedirect = true;
3218             request.Method = WebRequestMethods.Http.Get;
3219             const int BUFFER_SIZE = 16 * 1024;
3220            byte[] buffer = new byte[BUFFER_SIZE - 1];
3221             int bytesRead;
3222 
3223             using (var outputFileStream = File.Create(LocalFilePath, BUFFER_SIZE))
3224             {
3225                 using (var response2 = request.GetResponse())
3226                 {
3227                     using (var responseStream = response2.GetResponseStream())
3228                     {
3229                         while ((bytesRead = responseStream.Read(buffer, 0, buffer.Length)) > 0)
3230                         {
3231                             outputFileStream.Write(buffer, 0, bytesRead);
3232                         }
3233                     }
3234                 }
3235             }
3236             return true;
3237         }
3238         catch (Exception ex)
3239         {
3240             if (ex.GetType() == typeof(WebException))
3241             {
3242                 WebException webex;
3243                 webex = (WebException)ex;
3244                 if (webex.Response != null)
3245                 {
3246                     Stream stream = webex.Response.GetResponseStream();
3247                     using (StreamReader reader = new StreamReader(stream))
3248                     {
3249                         string errorResp = reader.ReadToEnd();
3250                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
3251                         Connector.ReturnMessages.Add("Unable to Execute the Get File Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
3252                         Connector.hasError = true;  //throws exception
3253                         return false;
3254                     }
3255 
3256                 }
3257                 else
3258                 {
3259                     Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
3260                     Connector.hasError = true;  //throws exception
3261                     return false;
3262                 }
3263             }
3264             else
3265             {
3266                 Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
3267                 Connector.hasError = true;  //throws exception
3268                 return false;
3269             }
3270         }
3271     }
