9 internal class Requestor
10 {
11 
12     #region Variables
13 
14     private Connector Connection;
15     protected internal bool isInitialized = false;
16     private CookieContainer AuthenticationCookie;
17 
18     #endregion
19 
20     #region Constructor
21 
22     public Requestor(Connector Connector)
23     {
24         Connection = Connector;
25     }
26     #endregion
