28     #region Private Methods
29 
30     private bool AddIdentityHeader(ref HttpWebRequest request)
31     {
32         try
33         {
34             if (request != null)
35             {
36                 string XPIdentity = "JPI$XP@ges!C0nn3nt0r|Id3nTity@Request";
37                 if (!string.IsNullOrEmpty(Connection.XPIdentity))
38                 {
39                     XPIdentity = Connection.XPIdentity;
40                 }
41                 //  Const XPIdentity As String = "JPI$XP@ges!C0nn3nt0r|Id3nTity@Request"
42 
43                 Encryptor EncodedEncryptedContent = new Encryptor(XPIdentity, true, ref Connection);
44                 if (EncodedEncryptedContent.Initialize())
45                 {
46                     request.Headers.Add("Identity", EncodedEncryptedContent.EncodedContent);
47                     return true;
48                 }
49             }
50             else
51             {
52                 //request is nothing ' error added in calling function
53             }
54             return false;
55         }
56         catch (Exception ex)
57         {
58             Connector.ReturnMessages.Add("Unable to add Identity Header to XPages Request");
59             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
60             Connector.hasError = true; //throws exception
61 
62             return false;
63         }
64     }
65 
66     #endregion
67 
68     #region Protected Internal Methods
69 
70     protected internal bool Initialize()
71     {
72 
73         HttpWebRequest request = null;
74 
75         // use the XPagesAPI ReturnMessages & hasError
76         try
77         {
78             //ServicePointManager.Expect100Continue = false;
79             ServicePointManager.Expect100Continue = true;
80           //  ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls;
81             ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12 | SecurityProtocolType.Tls11 | SecurityProtocolType.Tls;
82             ServicePointManager.DefaultConnectionLimit = 9999;
83 
84            
85             // Cookie container to store authentication cookie
86             CookieContainer cookies = new CookieContainer();
87 
88             //create an HTTP request
89             //Login - check for "/names.nsf?Login"
90             string serverURL = "";
91             serverURL = Connection.ServerURL;
92             if (!serverURL.Contains("/names.nsf?Login"))
93             {
94                 serverURL = serverURL + "/names.nsf?Login";
95             }
96 
97             request = (HttpWebRequest)WebRequest.Create(serverURL);
98 
99             // Prepare HTTP request
100             request.Method = WebRequestMethods.Http.Post;
101             // "POST"
102             request.AllowAutoRedirect = false;
103             request.ContentType = "application/x-www-form-urlencoded";
104             request.CookieContainer = cookies;
105             //  request.UserAgent = "Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/54.0.2840.99 Safari/537.36"
106             // Prepare POST body
107             string post = "Username=" + Uri.EscapeDataString(Connection.UserName) + "&Password=" + Uri.EscapeDataString(Connection.Password);
108             byte[] bytes = Encoding.ASCII.GetBytes(post);
109 
110             // Write data to request
111             request.ContentLength = bytes.Length;
112             Stream streamOut = request.GetRequestStream();
113             streamOut.Write(bytes, 0, bytes.Length);
114             streamOut.Close();
115 
116             //' Get response
117             HttpWebResponse response = (HttpWebResponse)request.GetResponse();
118 
119             // Check if we are authenticated properly
120             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
121             {
122                 foreach (Cookie cooky in response.Cookies)
123                 {
124                     cookies.Add(cooky);
125                 }
126 
127                 this.AuthenticationCookie = cookies;
128 
129                 if (response.Cookies != null && response.Cookies.Count > 0)
130                 {
131                     Connector.ReturnMessages.Add(Connection.UserName + " Successfully Authenticated On Domino Server " + Connection.ServerURL);
132                     Connector.hasError = false;
133                 }
134                 else
135                 {
136                     Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
137                     Connector.hasError = true;
138                     //thows exception
139                 }
140             }
141             else
142             {
143                 Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
144                 Connector.hasError = true;
145                 //thows exception
146             }
147             isInitialized = true;
148             return true;
149         }
150         catch (Exception ex)
151         {
152             Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
153             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
154             Connector.hasError = true;
155             //throws exception
156             isInitialized = false;
157             return false;
158         }
159 
160     }
161 
162     protected internal bool ExecuteSessionRequest(string WebServiceURL)
163     {
164         HttpWebRequest request = null;
165 
166         try
167         {
168             if (!isInitialized)
169             {
170                 Connector.ReturnMessages.Add("Unable to execute the Session Request - Not Initialized/Connected To Server");
171                 Connector.hasError = true;
172                 return false;
173             }
174 
175             //ServicePointManager.Expect100Continue = false;
176 
177             //create an HTTP request
178             // WebURL use this without ?CheckOut or ?CheckIn
179 
180             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Session");
181             // ?CheckOut
182 
183             request.CookieContainer = this.AuthenticationCookie;
184             //  request.KeepAlive = True
185             request.AllowAutoRedirect = true;
186 
187             request.Method = WebRequestMethods.Http.Post;           //"POST"
188             request.ContentType = "application/jpi; charset=utf-8";         //application/json
189                                                                             // Set the request stream
190             string result;
191 
192             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
193             if (!AddIdentityHeader(ref request))
194             {
195                 Connector.ReturnMessages.Add("Unable to Execute Session Request : Identity Header could not be added!");
196                 Connector.hasError = true;
197                 return false;
198             }
199 
200             //test with encryption
201             StringBuilder sb = new StringBuilder();
202 
203             // User Name
204             sb.AppendLine("UserName : " + Connection.UserName);
205             sb.AppendLine("Connecting via XPagesAPI");
206 
207 
208             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
209             string myString = Encoding.UTF8.GetString(b);
210             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
211             if (EncodedEncryptedContent.Initialize())
212             {
213 
214                 Stream requestStream = null;
215                 try
216                 {
217                     requestStream = request.GetRequestStream();
218                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
219                     {
220                         writer.Write(EncodedEncryptedContent.EncodedContent);
221                     }
222                 }
223                 catch (Exception e)
224                 {
225                     Connector.ReturnMessages.Add("Unable to execute the Session Request : " + Common.GetErrorInfo(e));
226                     Connector.hasError = true;  //throws exception
227                     return false;
228                 }
229                 finally
230                 {
231                     //if (requestStream != null) {
232                     //    requestStream.Dispose();
233                     //}
234                 }
235 
236             }
237             else
238             {
239                 Connector.ReturnMessages.Add("Executing Session Request - Unable to encode/encrypt the content of the request!");
240                 Connector.hasError = true; //throws exception
241                 return false;
242             }
243 
244             HttpWebResponse response = (HttpWebResponse)request.GetResponse();
245             //can give webexception error 500
246 
247             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
248             {
249                 Stream responseStream = null;
250                 try
251                 {
252                     responseStream = request.GetResponse().GetResponseStream();
253                     using (StreamReader reader = new StreamReader(responseStream, true))
254                     {
255                         result = reader.ReadToEnd();
256                     }
257                 }
258                 catch (Exception e)
259                 {
260                     Connector.ReturnMessages.Add("Unable to execute the Session Request : " + Common.GetErrorInfo(e));
261                     Connector.hasError = true;  //throws exception
262                     return false;
263                 }
264                 finally
265                 {
266                     //if (responseStream != null) {
267                     //    responseStream.Dispose();
268                     //}
269                 }
270 
271                 if (result != null)
272                 {
273                     //check if the page returned is not just the login page - we need to get encoded & encrypted content here - check if start with <! 
274                     if (result.StartsWith("<!"))
275                     {
276                         //possible wrong password !
277                         Connector.ReturnMessages.Add("Executing Session Request -  Unable to get the session - Authentication/Password Issue");
278                         Connector.hasError = true;
279                         //throws exception
280                         return false;
281                     }
282 
283                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
284                     if (DecodeDecryptedContent.Initialize())
285                     {
286                         ArrayList arList = new ArrayList();
287 
288                         string[] ar = null;
289                         bool isError = false;
290 
291                         string message = "";
292                         string details = "";
293 
294                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
295                         arList.AddRange(ar);
296                         foreach (string str in arList)
297                         {
298                             //YES/NO
299                             if (str.StartsWith("Error: "))
300                             {
301                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
302                                 {
303                                     isError = true;
304                                 }
305                             }
306                             if (str.StartsWith("Message: "))
307                             {
308                                 message = str.Replace("Message: ", "");
309                             }
310                             if (str.StartsWith("Details: "))
311                             {
312                                 details = str.Replace("Details: ", "");
313                             }
314 
315                         }
316                         if (isError)
317                         {
318                             Connector.ReturnMessages.Add(message);
319                             Connector.ReturnMessages.Add(details);
320                             Connector.hasError = true;
321                             //throws exception
322                             return false;
323                         }
324                         else
325                         {
326                             Connector.ReturnMessages.Add(message);
327                             Connector.ReturnMessages.Add(details);
328                             Connector.hasError = false;
329                             //SUCCESS
330                         }
331 
332                     }
333                     else
334                     {
335                         Connector.ReturnMessages.Add("Executing Session Request - Unable to decode/decrypt the content of the response!");
336                         Connector.hasError = true;
337                         //throws exception
338                         return false;
339                     }
340                 }
341                 else
342                 {
343                     //no response from server
344                     Connector.ReturnMessages.Add("Executing Session Request -  Unable to get response from XPages!");
345                     Connector.hasError = true;
346                     //throws exception
347                     return false;
348 
349                 }
350                 return true;
351             }
352             else
353             {
354                 Connector.ReturnMessages.Add("Executing Session Request -  Unable to get the session - Authentication Issue");
355                 Connector.hasError = true;
356                 //throws exception
357                 return false;
358             }
359 
360         }
361         catch (Exception ex)
362         {
363             if (ex.GetType() == typeof(WebException))
364             {
365                 WebException webex;
366                 webex = (WebException)ex;
367                 if (webex.Response != null)
368                 {
369                     Stream stream = webex.Response.GetResponseStream();
370                     using (StreamReader reader = new StreamReader(stream))
371                     {
372                         string errorResp = reader.ReadToEnd();
373                         // errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\r\\n\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\r\\n", Environment.NewLine);
374                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
375                         Connector.ReturnMessages.Add("Unable to Execute the Session Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
376                         Connector.hasError = true;
377                         //throws exception
378                         return false;
379                     }
380 
381                 }
382                 else
383                 {
384                     Connector.ReturnMessages.Add("Unable to Execute the Session Request : " + Common.GetErrorInfo(ex));
385                     Connector.hasError = true;
386                     //throws exception
387                     return false;
388                 }
389             }
390             else
391             {
392                 Connector.ReturnMessages.Add("Unable to Execute the Session Request : " + Common.GetErrorInfo(ex));
393                 Connector.hasError = true;
394                 //throws exception
395                 return false;
396             }
397         }
398     }
399 
400     protected internal bool ExecuteDatabaseRequest(string WebServiceURL, string ServerName, string dbFilePath, string dbReplicationId, DatabaseObject dbObj)
401     {
402         HttpWebRequest request = null;
403 
404         try
405         {
406             if (!isInitialized)
407             {
408                 Connector.ReturnMessages.Add("Unable to execute the Database Request - Not Initialized/Connected To Server");
409                 Connector.hasError = true;
410                 return false;
411             }
412 
413           //  ServicePointManager.Expect100Continue = false;
414 
415             //create an HTTP request
416 
417             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Database");
418 
419             request.CookieContainer = this.AuthenticationCookie;
420             //  request.KeepAlive = True
421             request.AllowAutoRedirect = true;
422 
423             request.Method = WebRequestMethods.Http.Post;   //"POST"
424             request.ContentType = "application/jpi; charset=utf-8"; //application/json
425                                                                     // Set the request stream
426             string result;
427 
428             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
429             if (!AddIdentityHeader(ref request))
430             {
431                 Connector.ReturnMessages.Add("Unable to Execute Database Request : Identity Header could not be added!");
432                 Connector.hasError = true;  //throws error
433                 return false;
434             }
435 
436             //test with encryption
437             StringBuilder sb = new StringBuilder();
438 
439             sb.AppendLine("UserName: " + Connection.UserName);
440             sb.AppendLine("ServerName: " + ServerName);
441             sb.AppendLine("FilePath: " + dbFilePath);
442             sb.AppendLine("ReplicationID: " + dbReplicationId);
443 
444             sb.AppendLine("Connecting via XPagesAPI");
445 
446             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
447             string myString = Encoding.UTF8.GetString(b);
448             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
449             if (EncodedEncryptedContent.Initialize())
450             {
451                 Stream requestStream = null;
452                 try
453                 {
454                     requestStream = request.GetRequestStream();
455                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
456                     {
457                         writer.Write(EncodedEncryptedContent.EncodedContent);
458                     }
459                 }
460                 catch (Exception e)
461                 {
462                     Connector.ReturnMessages.Add("Unable to execute the Database Request : " + Common.GetErrorInfo(e));
463                     Connector.hasError = true;  //throws exception
464                     return false;
465                 }
466                 finally
467                 {
468                     //if (requestStream != null) {
469                     //    requestStream.Dispose();
470                     //}
471                 }
472             }
473             else
474             {
475                 Connector.ReturnMessages.Add("Executing Database Request - Unable to encode/encrypt the content of the request!");
476                 Connector.hasError = true; //throws exception
477                 return false;
478             }
479 
480             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
481 
482             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
483             {
484                 Stream responseStream = null;
485                 try
486                 {
487                     responseStream = request.GetResponse().GetResponseStream();
488                     using (StreamReader reader = new StreamReader(responseStream, true))
489                     {
490                         result = reader.ReadToEnd();
491                     }
492                 }
493                 catch (Exception e)
494                 {
495                     Connector.ReturnMessages.Add("Unable to execute the Database Request : " + Common.GetErrorInfo(e));
496                     Connector.hasError = true;  //throws exception
497                     return false;
498                 }
499                 finally
500                 {
501                     //if (responseStream != null) {
502                     //    responseStream.Dispose();
503                     //}
504                 }
505 
506                 if (result != null)
507                 {
508                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
509                     if (DecodeDecryptedContent.Initialize())
510                     {
511                         ArrayList arList = new ArrayList();
512 
513                         string[] ar = null;
514                         bool isError = false;
515 
516                         string message = "";
517                         string details = "";
518                         string value = "";
519 
520                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
521                         arList.AddRange(ar);
522                         foreach (string str in arList)
523                         {
524                             //YES/NO
525                             if (str.StartsWith("Error: "))
526                             {
527                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
528                                 {
529                                     isError = true;
530                                 }
531                             }
532                             if (str.StartsWith("Message: "))
533                             {
534                                 message = str.Replace("Message: ", "");
535                             }
536                             if (str.StartsWith("Details: "))
537                             {
538                                 details = str.Replace("Details: ", "");
539                             }
540                             if (str.StartsWith("ServerName: "))
541                             {
542                                 value = str.Replace("ServerName: ", "");
543                                 if (!string.IsNullOrEmpty(value))
544                                 {
545                                     dbObj.ServerName = value;
546                                 }
547                             }
548                             if (str.StartsWith("FilePath: "))
549                             {
550                                 value = str.Replace("FilePath: ", "");
551                                 if (!string.IsNullOrEmpty(value))
552                                 {
553                                     dbObj.FilePath = value;
554                                 }
555                             }
556                             if (str.StartsWith("ReplicationID: "))
557                             {
558                                 value = str.Replace("ReplicationID: ", "");
559                                 if (!string.IsNullOrEmpty(value))
560                                 {
561                                     dbObj.ReplicationID = value;
562                                 }
563                             }
564 
565                             if (str.StartsWith("Title: "))
566                             {
567                                 value = str.Replace("Title: ", "");
568                                 if (!string.IsNullOrEmpty(value))
569                                 {
570                                     dbObj.Title = value;
571                                 }
572                             }
573 
574                             if (str.StartsWith("Template: "))
575                             {
576                                 value = str.Replace("Template: ", "");
577                                 if (!string.IsNullOrEmpty(value))
578                                 {
579                                     dbObj.Template = value;
580                                 }
581                             }
582 
583                             if (str.StartsWith("Size: "))
584                             {
585                                 value = str.Replace("Size: ", "");
586                                 if (!string.IsNullOrEmpty(value))
587                                 {
588                                     dbObj.Size = value;
589                                 }
590                             }
591 
592                             if (str.StartsWith("URL: "))
593                             {
594                                 value = str.Replace("URL: ", "");
595                                 if (!string.IsNullOrEmpty(value))
596                                 {
597                                     dbObj.Url = value;
598                                 }
599                             }
600 
601                         }
602 
603                         if (isError)
604                         {
605                             Connector.ReturnMessages.Add(message);
606                             Connector.ReturnMessages.Add(details);
607                             Connector.hasError = true; //throws exception
608                             return false;
609                         }
610                         else
611                         {
612                             Connector.ReturnMessages.Add(message);
613                             Connector.ReturnMessages.Add(details);
614                             Connector.hasError = false; //SUCCESS
615                         }
616 
617                     }
618                     else
619                     {
620                         Connector.ReturnMessages.Add("Executing Database Request - Unable to decode/decrypt the content of the response!");
621                         Connector.hasError = true;  //throws exception
622                         return false;
623                     }
624                 }
625                 else
626                 {
627                     //no response from server
628                     Connector.ReturnMessages.Add("Executing Database Request -  Unable to get response from XPages!");
629                     Connector.hasError = true;  //throws exception
630                     return false;
631 
632                 }
633                 return true;
634             }
635             else
636             {
637                 Connector.ReturnMessages.Add("Executing Database Request -  Unable to get the Database - Authentication Issue");
638                 Connector.hasError = true;  //throws exception
639                 return false;
640             }
641 
642         }
643         catch (Exception ex)
644         {
645             if (ex.GetType() == typeof(WebException))
646             {
647                 WebException webex;
648                 webex = (WebException)ex;
649                 if (webex.Response != null)
650                 {
651                     Stream stream = webex.Response.GetResponseStream();
652                     using (StreamReader reader = new StreamReader(stream))
653                     {
654                         string errorResp = reader.ReadToEnd();
655                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
656                         Connector.ReturnMessages.Add("Unable to Execute the Database Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
657                         Connector.hasError = true;  //throws exception
658                         return false;
659                     }
660 
661                 }
662                 else
663                 {
664                     Connector.ReturnMessages.Add("Unable to Execute the Database Request : " + Common.GetErrorInfo(ex));
665                     Connector.hasError = true;  //throws exception
666                     return false;
667                 }
668             }
669             else
670             {
671                 Connector.ReturnMessages.Add("Unable to Execute the Database Request : " + Common.GetErrorInfo(ex));
672                 Connector.hasError = true;  //throws exception
673                 return false;
674             }
675         }
676     }
