678     protected internal bool ExecuteDocumentRequest(string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj)
679     {
680         HttpWebRequest request = null;
681 
682         try
683         {
684             if (!isInitialized)
685             {
686                 Connector.ReturnMessages.Add("Unable to execute the Document Request - Not Initialized/Connected To Server");
687                 Connector.hasError = true;
688                 return false;
689             }
690 
691            // ServicePointManager.Expect100Continue = false;
692 
693             //create an HTTP request
694 
695             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Document");
696 
697             request.CookieContainer = this.AuthenticationCookie;
698             //  request.KeepAlive = True
699             request.AllowAutoRedirect = true;
700 
701             request.Method = WebRequestMethods.Http.Post;   //"POST"
702             request.ContentType = "application/jpi; charset=utf-8"; //application/json
703                                                                     // Set the request stream
704             string result;
705 
706             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
707             if (!AddIdentityHeader(ref request))
708             {
709                 Connector.ReturnMessages.Add("Unable to Execute Document Request : Identity Header could not be added!");
710                 Connector.hasError = true;  //throws error
711                 return false;
712             }
713 
714             //test with encryption
715             StringBuilder sb = new StringBuilder();
716 
717             sb.AppendLine("UserName: " + Connection.UserName);
718             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
719             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
720             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
721             sb.AppendLine("UniversalID: " + Unid);
722             sb.AppendLine("SearchField: " + searchField);
723             sb.AppendLine("SearchValue: " + searchValue);
724             sb.AppendLine("Formula: " + formula);
725             sb.AppendLine("Connecting via XPagesAPI");
726 
727             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
728             string myString = Encoding.UTF8.GetString(b);
729             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
730             if (EncodedEncryptedContent.Initialize())
731             {
732                 Stream requestStream = null;
733                 try
734                 {
735                     requestStream = request.GetRequestStream();
736                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
737                     {
738                         writer.Write(EncodedEncryptedContent.EncodedContent);
739                     }
740                 }
741                 catch (Exception e)
742                 {
743                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
744                     Connector.hasError = true;  //throws exception
745                     return false;
746                 }
747                 finally
748                 {
749                     //if (requestStream != null) {
750                     //    requestStream.Dispose();
751                     //}
752                 }
753             }
754             else
755             {
756                 Connector.ReturnMessages.Add("Executing Document Request - Unable to encode/encrypt the content of the request!");
757                 Connector.hasError = true; //throws exception
758                 return false;
759             }
760 
761             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
762 
763             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
764             {
765                 Stream responseStream = null;
766                 try
767                 {
768                     responseStream = request.GetResponse().GetResponseStream();
769                     using (StreamReader reader = new StreamReader(responseStream, true))
770                     {
771                         result = reader.ReadToEnd();
772                     }
773                 }
774                 catch (Exception e)
775                 {
776                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
777                     Connector.hasError = true;  //throws exception
778                     return false;
779                 }
780                 finally
781                 {
782                     //if (responseStream != null) {
783                     //    responseStream.Dispose();
784                     //}
785                 }
786 
787                 if (result != null)
788                 {
789                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
790                     if (DecodeDecryptedContent.Initialize())
791                     {
792                         ArrayList arList = new ArrayList();
793 
794                         string[] ar = null;
795                         bool isError = false;
796 
797                         string message = "";
798                         string details = "";
799                         string value = "";
800                         string unid = "";
801                         string noteId = "";
802                         string form = "";
803                         string size = "";
804                         string url = "";
805                         string created = "";
806                         string modified = "";
807 
808                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
809                         arList.AddRange(ar);
810 
811                         foreach (string str in arList)
812                         {
813                             //YES/NO
814                             if (str.StartsWith("Error: "))
815                             {
816                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
817                                 {
818                                     isError = true;
819                                 }
820                             }
821                             if (str.StartsWith("Message: "))
822                             {
823                                 message = str.Replace("Message: ", "");
824                             }
825                             if (str.StartsWith("Details: "))
826                             {
827                                 details = str.Replace("Details: ", "");
828                             }
829 
830                             if (str.StartsWith("NoteID: "))
831                             {
832                                 value = str.Replace("NoteID: ", "");
833                                 if (!string.IsNullOrEmpty(value))
834                                 {
835                                     noteId = value;
836                                 }
837                             }
838                             if (str.StartsWith("Unid: "))
839                             {
840                                 value = str.Replace("Unid: ", "");
841                                 if (!string.IsNullOrEmpty(value))
842                                 {
843                                     unid = value;
844                                 }
845                             }
846                             if (str.StartsWith("Form: "))
847                             {
848                                 value = str.Replace("Form: ", "");
849                                 if (!string.IsNullOrEmpty(value))
850                                 {
851                                     form = value;
852                                 }
853                             }
854 
855                             if (str.StartsWith("Size: "))
856                             {
857                                 value = str.Replace("Size: ", "");
858                                 if (!string.IsNullOrEmpty(value))
859                                 {
860                                     size = value;
861                                 }
862                             }
863 
864                             if (str.StartsWith("URL: "))
865                             {
866                                 value = str.Replace("URL: ", "");
867                                 if (!string.IsNullOrEmpty(value))
868                                 {
869                                     url = value;
870                                 }
871                             }
872 
873 
874                             if (str.StartsWith("Created: "))
875                             {
876                                 value = str.Replace("Created: ", "");
877                                 if (!string.IsNullOrEmpty(value))
878                                 {
879                                     created = value;
880                                 }
881                             }
882 
883 
884                             if (str.StartsWith("Modified: "))
885                             {
886                                 value = str.Replace("Modified: ", "");
887                                 if (!string.IsNullOrEmpty(value))
888                                 {
889                                     modified = value;
890                                 }
891                             }
892                         }
893 
894                         if (isError)
895                         {
896                             Connector.ReturnMessages.Add(message);
897                             Connector.ReturnMessages.Add(details);
898                             Connector.hasError = true; //throws exception
899                             return false;
900                         }
901                         else
902                         {
903 
904                             // add doc to list
905                             if (dbObj.Documents == null)
906                             {
907                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
908                             }
909                             if (!string.IsNullOrEmpty(unid))
910                             {
911                                 //docObj = new DocumentObject(dbObj, unid);
912                                 docObj.UniversalID = unid;
913                                 docObj.NoteID = noteId;
914                                 docObj.Size = size;
915                                 docObj.Url = url;
916                                 docObj.Form = form;
917                                 docObj.DateCreated = created;
918                                 docObj.DateModified = modified;
919                                 if (dbObj.Documents.ContainsKey(unid))
920                                 {
921                                     dbObj.Documents.Remove(unid);
922                                 }
923                                 dbObj.Documents.Add(unid, docObj);
924                                 Connector.ReturnMessages.Add(message);
925                                 Connector.ReturnMessages.Add(details);
926                                 Connector.hasError = false; //SUCCESS
927                             }
928                             else
929                             {
930                                 // unid not found??
931                                 Connector.ReturnMessages.Add(message);
932                                 Connector.ReturnMessages.Add(details);
933                                 Connector.ReturnMessages.Add("Universal ID not found!");
934                                 Connector.hasError = true; // throws exception
935                             }
936 
937                         }
938 
939                     }
940                     else
941                     {
942                         Connector.ReturnMessages.Add("Executing Document Request - Unable to decode/decrypt the content of the response!");
943                         Connector.hasError = true;  //throws exception
944                         return false;
945                     }
946                 }
947                 else
948                 {
949                     //no response from server
950                     Connector.ReturnMessages.Add("Executing Document Request -  Unable to get response from XPages!");
951                     Connector.hasError = true;  //throws exception
952                     return false;
953 
954                 }
955                 return true;
956             }
957             else
958             {
959                 Connector.ReturnMessages.Add("Executing Document Request -  Unable to get the Document - Authentication Issue");
960                 Connector.hasError = true;  //throws exception
961                 return false;
962             }
963 
964         }
965         catch (Exception ex)
966         {
967             if (ex.GetType() == typeof(WebException))
968             {
969                 WebException webex;
970                 webex = (WebException)ex;
971                 if (webex.Response != null)
972                 {
973                     Stream stream = webex.Response.GetResponseStream();
974                     using (StreamReader reader = new StreamReader(stream))
975                     {
976                         string errorResp = reader.ReadToEnd();
977                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
978                         Connector.ReturnMessages.Add("Unable to Execute the Document Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
979                         Connector.hasError = true;  //throws exception
980                         return false;
981                     }
982 
983                 }
984                 else
985                 {
986                     Connector.ReturnMessages.Add("Unable to Execute the Document Request : " + Common.GetErrorInfo(ex));
987                     Connector.hasError = true;  //throws exception
988                     return false;
989                 }
990             }
991             else
992             {
993                 Connector.ReturnMessages.Add("Unable to Execute the Document Request : " + Common.GetErrorInfo(ex));
994                 Connector.hasError = true;  //throws exception
995                 return false;
996             }
997         }
998     }
