1000     protected internal bool ExecuteDocumentFilesRequest(string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj)
1001     {
1002         HttpWebRequest request = null;
1003 
1004         try
1005         {
1006             if (!isInitialized)
1007             {
1008                 Connector.ReturnMessages.Add("Unable to execute the DocumentFiles Request - Not Initialized/Connected To Server");
1009                 Connector.hasError = true;
1010                 return false;
1011             }
1012 
1013            // ServicePointManager.Expect100Continue = false;
1014 
1015             //create an HTTP request
1016 
1017             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Files");
1018 
1019             request.CookieContainer = this.AuthenticationCookie;
1020             //  request.KeepAlive = True
1021             request.AllowAutoRedirect = true;
1022 
1023             request.Method = WebRequestMethods.Http.Post;   //"POST"
1024             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1025                                                                     // Set the request stream
1026             string result;
1027 
1028             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1029             if (!AddIdentityHeader(ref request))
1030             {
1031                 Connector.ReturnMessages.Add("Unable to Execute DocumentFiles Request : Identity Header could not be added!");
1032                 Connector.hasError = true;  //throws error
1033                 return false;
1034             }
1035 
1036             //test with encryption
1037             StringBuilder sb = new StringBuilder();
1038 
1039             sb.AppendLine("UserName: " + Connection.UserName);
1040             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
1041             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
1042             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
1043             sb.AppendLine("UniversalID: " + Unid);
1044             sb.AppendLine("SearchField: " + searchField);
1045             sb.AppendLine("SearchValue: " + searchValue);
1046             sb.AppendLine("Formula: " + formula);
1047             sb.AppendLine("Connecting via XPagesAPI");
1048 
1049             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1050             string myString = Encoding.UTF8.GetString(b);
1051             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1052             if (EncodedEncryptedContent.Initialize())
1053             {
1054                 Stream requestStream = null;
1055                 try
1056                 {
1057                     requestStream = request.GetRequestStream();
1058                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
1059                     {
1060                         writer.Write(EncodedEncryptedContent.EncodedContent);
1061                     }
1062                 }
1063                 catch (Exception e)
1064                 {
1065                     Connector.ReturnMessages.Add("Unable to execute the DocumentFiles Request : " + Common.GetErrorInfo(e));
1066                     Connector.hasError = true;  //throws exception
1067                     return false;
1068                 }
1069                 finally
1070                 {
1071                     //if (requestStream != null) {
1072                     //    requestStream.Dispose();
1073                     //}
1074                 }
1075             }
1076             else
1077             {
1078                 Connector.ReturnMessages.Add("Executing DocumentFiles Request - Unable to encode/encrypt the content of the request!");
1079                 Connector.hasError = true; //throws exception
1080                 return false;
1081             }
1082 
1083             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1084 
1085             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
1086             {
1087                 Stream responseStream = null;
1088                 try
1089                 {
1090                     responseStream = request.GetResponse().GetResponseStream();
1091                     using (StreamReader reader = new StreamReader(responseStream, true))
1092                     {
1093                         result = reader.ReadToEnd();
1094                     }
1095                 }
1096                 catch (Exception e)
1097                 {
1098                     Connector.ReturnMessages.Add("Unable to execute the DocumentFiles Request : " + Common.GetErrorInfo(e));
1099                     Connector.hasError = true;  //throws exception
1100                     return false;
1101                 }
1102                 finally
1103                 {
1104                     //if (responseStream != null) {
1105                     //    responseStream.Dispose();
1106                     //}
1107                 }
1108 
1109                 if (result != null)
1110                 {
1111                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1112                     if (DecodeDecryptedContent.Initialize())
1113                     {
1114                         ArrayList arList = new ArrayList();
1115 
1116                         string[] ar = null;
1117                         bool isError = false;
1118 
1119                         string message = "";
1120                         string details = "";
1121                         string value = "";
1122                         string unid = "";
1123                         string noteId = "";
1124                         string form = "";
1125                         string size = "";
1126                         string url = "";
1127                         string created = "";
1128                         string modified = "";
1129                         List<string> fList = new List<String>();
1130 
1131 
1132                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1133                         arList.AddRange(ar);
1134 
1135                         foreach (string str in arList)
1136                         {
1137                             //YES/NO
1138                             if (str.StartsWith("Error: "))
1139                             {
1140                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
1141                                 {
1142                                     isError = true;
1143                                 }
1144                             }
1145                             if (str.StartsWith("Message: "))
1146                             {
1147                                 message = str.Replace("Message: ", "");
1148                             }
1149                             if (str.StartsWith("Details: "))
1150                             {
1151                                 details = str.Replace("Details: ", "");
1152                             }
1153 
1154                             if (str.StartsWith("NoteID: "))
1155                             {
1156                                 value = str.Replace("NoteID: ", "");
1157                                 if (!string.IsNullOrEmpty(value))
1158                                 {
1159                                     noteId = value;
1160                                 }
1161                             }
1162                             if (str.StartsWith("Unid: "))
1163                             {
1164                                 value = str.Replace("Unid: ", "");
1165                                 if (!string.IsNullOrEmpty(value))
1166                                 {
1167                                     unid = value;
1168                                 }
1169                             }
1170                             if (str.StartsWith("Form: "))
1171                             {
1172                                 value = str.Replace("Form: ", "");
1173                                 if (!string.IsNullOrEmpty(value))
1174                                 {
1175                                     form = value;
1176                                 }
1177                             }
1178 
