1193                                     url = value;
1194                                 }
1195                             }
1196 
1197 
1198                             if (str.StartsWith("Created: "))
1199                             {
1200                                 value = str.Replace("Created: ", "");
1201                                 if (!string.IsNullOrEmpty(value))
1202                                 {
1203                                     created = value;
1204                                 }
1205                             }
1206 
