1209                             {
1210                                 value = str.Replace("Modified: ", "");
1211                                 if (!string.IsNullOrEmpty(value))
1212                                 {
1213                                     modified = value;
1214                                 }
1215                             }
1216 
1217                             if (str.StartsWith("FileObject: "))
1218                             {
1219                                 value = str.Replace("FileObject: ", "");
1220                                 if (!string.IsNullOrEmpty(value))
1221                                 {
1222                                     // files = value;
1223                                     fList.Add(value);
1224                                 }
1225                             }
1226                         }
1227 
1228                         if (isError)
1229                         {
1230                             Connector.ReturnMessages.Add(message);
1231                             Connector.ReturnMessages.Add(details);
1232                             Connector.hasError = true; //throws exception
1233                             return false;
1234                         }
1235                         else
1236                         {
1237 
