1239                             if (dbObj.Documents == null)
1240                             {
1241                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
1242                             }
1243                             if (!string.IsNullOrEmpty(unid))
1244                             {
1245                                 //docObj = new DocumentObject(dbObj, unid);
1246                                 docObj.UniversalID = unid;
1247                                 docObj.NoteID = noteId;
1248                                 docObj.Size = size;
1249                                 docObj.Url = url;
1250                                 docObj.Form = form;
1251                                 docObj.DateCreated = created;
1252                                 docObj.DateModified = modified;
1253                                 //create file objects
1254                                 SortedDictionary<string, FileObject> files = new SortedDictionary<string, FileObject>();
1255                                 FileObject fObj = null;
1256                                 foreach (string str in fList)
