1294                         Connector.hasError = true;  //throws exception
1295                         return false;
1296                     }
1297                 }
1298                 else
1299                 {
1300                     //no response from server
1301                     Connector.ReturnMessages.Add("Executing DocumentFiles Request -  Unable to get response from XPages!");
1302                     Connector.hasError = true;  //throws exception
1303                     return false;
1304 
