1436             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
1437             {
1438                 Stream responseStream = null;
1439                 try
1440                 {
1441                     responseStream = request.GetResponse().GetResponseStream();
1442                     using (StreamReader reader = new StreamReader(responseStream, true))
1443                     {
1444                         result = reader.ReadToEnd();
1445                     }
1446                 }
1447                 catch (Exception e)
1448                 {
1449                     Connector.ReturnMessages.Add("Unable to execute the All Documents Request : " + Common.GetErrorInfo(e));
1450                     Connector.hasError = true;  //throws exception
1451                     return false;
1452                 }
1453                 finally
1454                 {
1455                     //if (responseStream != null) {
1456                     //    responseStream.Dispose();
1457                     //}
1458                 }
1459 
1460                 if (result != null)
1461                 {
1462                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1463                     if (DecodeDecryptedContent.Initialize())
1464                     {
1465                         ArrayList arList = new ArrayList();
1466 
1467                         string[] ar = null;
1468                         bool isError = false;
1469 
1470                         string message = "";
1471                         string details = "";
1472                         string value = "";
1473                         string[] sep = { "||" };
1474                         string[] NoteIds = { };
1475                         string[] Unids = { };
1476                         string[] Forms = { };
1477                         string[] Sizes = { };
1478                         string[] URLs = { };
1479                         string[] Created = { };
1480                         string[] Modified = { };
1481 
1482                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1483                         arList.AddRange(ar);
1484                         foreach (string str in arList)
1485                         {
1486                             //YES/NO
1487                             if (str.StartsWith("Error: "))
1488                             {
1489                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
1490                                 {
1491                                     isError = true;
1492                                 }
1493                             }
1494                             if (str.StartsWith("Message: "))
1495                             {
1496                                 message = str.Replace("Message: ", "");
1497                             }
1498                             if (str.StartsWith("Details: "))
1499                             {
1500                                 details = str.Replace("Details: ", "");
1501                             }
1502 
1503                             if (str.StartsWith("Unids: "))
1504                             {
1505                                 value = str.Replace("Unids: ", "");
1506                                 if (!string.IsNullOrEmpty(value))
1507                                 {
1508                                     Unids = value.Split(sep, StringSplitOptions.None);
1509                                 }
1510                             }
1511 
1512                             if (str.StartsWith("NoteIDs: "))
1513                             {
1514                                 value = str.Replace("NoteIDs: ", "");
1515                                 if (!string.IsNullOrEmpty(value))
1516                                 {
1517                                     NoteIds = value.Split(sep, StringSplitOptions.None);
1518                                 }
1519                             }
1520 
1521                             if (str.StartsWith("Forms: "))
1522                             {
1523                                 value = str.Replace("Forms: ", "");
1524                                 if (!string.IsNullOrEmpty(value))
1525                                 {
1526                                     Forms = value.Split(sep, StringSplitOptions.None);
1527                                 }
1528                             }
1529 
1530                             if (str.StartsWith("Sizes: "))
1531                             {
1532                                 value = str.Replace("Sizes: ", "");
1533                                 if (!string.IsNullOrEmpty(value))
1534                                 {
1535                                     Sizes = value.Split(sep, StringSplitOptions.None);
1536                                 }
1537                             }
1538 
1539                             if (str.StartsWith("URLs: "))
1540                             {
1541                                 value = str.Replace("URLs: ", "");
1542                                 if (!string.IsNullOrEmpty(value))
1543                                 {
1544                                     URLs = value.Split(sep, StringSplitOptions.None);
1545                                 }
1546                             }
1547 
1548 
1549                             if (str.StartsWith("Created: "))
1550                             {
1551                                 value = str.Replace("Created: ", "");
1552                                 if (!string.IsNullOrEmpty(value))
1553                                 {
1554                                     Created = value.Split(sep, StringSplitOptions.None);
1555                                 }
1556                             }
1557 
1558 
1559                             if (str.StartsWith("Modified: "))
1560                             {
1561                                 value = str.Replace("Modified: ", "");
1562                                 if (!string.IsNullOrEmpty(value))
1563                                 {
1564                                     Modified = value.Split(sep, StringSplitOptions.None);
1565                                 }
1566                             }
1567                         }
1568 
1569                         //loop through unids, notesids, forms, sizes, urls and create document objects
1570                         // check if all have the same number of items!
1571                         int count = 0;
1572                         if (Unids != null && Unids.Length > 0)
1573                         {
1574                             count = Unids.Length;
1575                             if (NoteIds != null && NoteIds.Length > 0)
1576                             {
1577                                 if (count != NoteIds.Length)
1578                                 {
1579                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> NoteIds");
1580                                     Connector.hasError = true;  //throws exception
1581                                     return false;
1582                                 }
1583                                 else
1584                                 {
1585                                     // form can be empty!!
1586                                     //if (Forms != null && Forms.Length > 0) {
1587                                     //    if (count != Forms.Length) {
1588                                     //        Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Forms");
1589                                     //        Connector.hasError = true;  //throws exception
1590                                     //        return false;
1591                                     //    } else {
1592                                     if (Sizes != null && Sizes.Length > 0)
1593                                     {
1594                                         if (count != Sizes.Length)
1595                                         {
1596                                             Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Sizes");
1597                                             Connector.hasError = true;  //throws exception
1598                                             return false;
1599                                         }
1600                                         else
1601                                         {
1602                                             if (URLs != null && URLs.Length > 0)
1603                                             {
1604                                                 if (count != URLs.Length)
1605                                                 {
1606                                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> URLs");
1607                                                     Connector.hasError = true;  //throws exception
1608                                                     return false;
1609                                                 }
1610                                             }
1611                                         }
1612                                     }
1613                                     //   }
1614                                     // }
1615                                 }
1616                             }
1617                             // loop through and create doc obj
1618                             DocumentObject docObj = null;
1619                             if (dbObj.Documents == null)
1620                             {
1621                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
1622                             }
1623                             for (int i = 0; i < Unids.Length; i++)
1624                             {
1625                                 docObj = new DocumentObject(dbObj, Unids[i]);
1626                                 if (NoteIds.Length - 1 >= i)
1627                                 {
1628                                     docObj.NoteID = NoteIds[i];
1629                                 }
1630                                 if (Forms.Length - 1 >= i)
1631                                 {
1632                                     docObj.Form = Forms[i];
1633                                 }
1634                                 if (Sizes.Length - 1 >= i)
1635                                 {
1636                                     docObj.Size = Sizes[i];
1637                                 }
1638                                 if (URLs.Length - 1 >= i)
1639                                 {
1640                                     docObj.Url = URLs[i];
1641                                 }
1642                                 if (Created.Length - 1 >= i)
1643                                 {
1644                                     docObj.DateCreated = Created[i];
1645                                 }
1646                                 if (Modified.Length - 1 >= i)
1647                                 {
1648                                     docObj.DateModified = Modified[i];
1649                                 }
1650                                 if (dbObj.Documents.ContainsKey(Unids[i]))
1651                                 {
1652                                     dbObj.Documents.Remove(Unids[i]);
1653                                 }
1654                                 docObj.IsInitialized = true;
1655                                 dbObj.Documents.Add(Unids[i], docObj);
1656                             }
1657                         }
1658 
1659 
1660 
1661                         if (isError)
1662                         {
1663                             Connector.ReturnMessages.Add(message);
1664                             Connector.ReturnMessages.Add(details);
1665                             Connector.hasError = true; //throws exception
1666                             return false;
1667                         }
1668                         else
1669                         {
1670                             Connector.ReturnMessages.Add(message);
1671                             Connector.ReturnMessages.Add(details);
1672                             Connector.hasError = false; //SUCCESS
1673                         }
1674 
1675                     }
1676                     else
1677                     {
1678                         Connector.ReturnMessages.Add("Executing All Documents Request - Unable to decode/decrypt the content of the response!");
1679                         Connector.hasError = true;  //throws exception
1680                         return false;
1681                     }
1682                 }
1683                 else
1684                 {
1685                     //no response from server
1686                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get response from XPages!");
1687                     Connector.hasError = true;  //throws exception
1688                     return false;
1689 
1690                 }
1691                 return true;
1692             }
1693             else
1694             {
1695                 Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Document - Authentication Issue");
1696                 Connector.hasError = true;  //throws exception
1697                 return false;
1698             }
1699 
1700         }
1701         catch (Exception ex)
1702         {
1703             if (ex.GetType() == typeof(WebException))
1704             {
1705                 WebException webex;
1706                 webex = (WebException)ex;
1707                 if (webex.Response != null)
1708                 {
1709                     Stream stream = webex.Response.GetResponseStream();
1710                     using (StreamReader reader = new StreamReader(stream))
1711                     {
1712                         string errorResp = reader.ReadToEnd();
1713                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1714                         Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1715                         Connector.hasError = true;  //throws exception
1716                         return false;
1717                     }
1718 
1719                 }
1720                 else
1721                 {
1722                     Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1723                     Connector.hasError = true;  //throws exception
1724                     return false;
1725                 }
1726             }
1727             else
1728             {
1729                 Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1730                 Connector.hasError = true;  //throws exception
1731                 return false;
1732             }
1733         }
1734     }
