1736     protected internal bool ExecuteAllDocumentsFilesRequest(string WebServiceURL, string searchField, string searchValue, string formula, string unids, DatabaseObject dbObj)
1737     {
1738         HttpWebRequest request = null;
1739 
1740         try
1741         {
1742             if (!isInitialized)
1743             {
1744                 Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request - Not Initialized/Connected To Server");
1745                 Connector.hasError = true;
1746                 return false;
1747             }
1748 
1749         //    ServicePointManager.Expect100Continue = false;
1750 
1751             //create an HTTP request
1752 
1753             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFiles");
1754             request.Timeout = 300000; // set timeout to 5minutes
1755             request.CookieContainer = this.AuthenticationCookie;
1756             //  request.KeepAlive = True
1757             request.AllowAutoRedirect = true;
1758 
1759             request.Method = WebRequestMethods.Http.Post;   //"POST"
1760             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1761                                                                     // Set the request stream
1762             string result;
1763 
1764             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1765             if (!AddIdentityHeader(ref request))
1766             {
1767                 Connector.ReturnMessages.Add("Unable to Execute AllDocumentsFiles Request : Identity Header could not be added!");
1768                 Connector.hasError = true;  //throws error
1769                 return false;
1770             }
1771 
1772             //test with encryption
1773             StringBuilder sb = new StringBuilder();
1774 
1775             sb.AppendLine("UserName: " + Connection.UserName);
1776             sb.AppendLine("ServerName: " + dbObj.ServerName);
1777             sb.AppendLine("FilePath: " + dbObj.FilePath);
1778             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
1779             sb.AppendLine("SearchField: " + searchField);
1780             sb.AppendLine("SearchValue: " + searchValue);
1781             sb.AppendLine("Formula: " + formula);
1782             sb.AppendLine("Unids: " + unids);
1783             sb.AppendLine("Connecting via XPagesAPI");
1784 
1785             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1786             string myString = Encoding.UTF8.GetString(b);
1787             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1788             if (EncodedEncryptedContent.Initialize())
1789             {
1790                 Stream requestStream = null;
1791                 try
1792                 {
1793                     requestStream = request.GetRequestStream();
1794                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
1795                     {
1796                         writer.Write(EncodedEncryptedContent.EncodedContent);
1797                     }
1798                 }
1799                 catch (Exception e)
1800                 {
1801                     Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(e));
1802                     Connector.hasError = true;  //throws exception
1803                     return false;
1804                 }
1805                 finally
1806                 {
1807                     //if (requestStream != null) {
1808                     //    requestStream.Dispose();
1809                     //}
1810                 }
1811             }
1812             else
1813             {
1814                 Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request - Unable to encode/encrypt the content of the request!");
1815                 Connector.hasError = true; //throws exception
1816                 return false;
1817             }
1818 
1819             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1820 
1821             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
1822             {
1823                 Stream responseStream = null;
1824                 try
1825                 {
1826                     responseStream = request.GetResponse().GetResponseStream();
1827                     using (StreamReader reader = new StreamReader(responseStream, true))
1828                     {
1829                         result = reader.ReadToEnd();
1830                     }
1831                 }
1832                 catch (Exception e)
1833                 {
1834                     Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(e));
1835                     Connector.hasError = true;  //throws exception
1836                     return false;
1837                 }
1838                 finally
1839                 {
1840                     //if (responseStream != null) {
1841                     //    responseStream.Dispose();
1842                     //}
1843                 }
1844 
1845                 if (result != null)
1846                 {
1847                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1848                     if (DecodeDecryptedContent.Initialize())
1849                     {
1850                         ArrayList arList = new ArrayList();
1851 
1852                         string[] ar = null;
1853                         bool isError = false;
1854 
