2107                 }
2108                 return true;
2109             }
2110             else
2111             {
2112                 Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get the Document - Authentication Issue");
2113                 Connector.hasError = true;  //throws exception
2114                 return false;
2115             }
2116 
2117         }
2118         catch (Exception ex)
2119         {
2120             if (ex.GetType() == typeof(WebException))
2121             {
2122                 WebException webex;
2123                 webex = (WebException)ex;
2124                 if (webex.Response != null)
2125                 {
2126                     Stream stream = webex.Response.GetResponseStream();
2127                     using (StreamReader reader = new StreamReader(stream))
2128                     {
2129                         string errorResp = reader.ReadToEnd();
2130                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2131                         Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2132                         Connector.hasError = true;  //throws exception
2133                         return false;
2134                     }
2135 
2136                 }
2137                 else
2138                 {
2139                     Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
2140                     Connector.hasError = true;  //throws exception
2141                     return false;
2142                 }
2143             }
2144             else
2145             {
2146                 Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
2147                 Connector.hasError = true;  //throws exception
2148                 return false;
2149             }
