2153     protected internal bool ExecuteFieldsRequest(string WebServiceURL, DocumentObject docObj, string fields)
2154     {
2155         HttpWebRequest request = null;
2156 
2157         try
2158         {
2159             if (!isInitialized)
2160             {
2161                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
2162                 Connector.hasError = true;
2163                 return false;
2164             }
2165 
2166            // ServicePointManager.Expect100Continue = false;
2167 
2168             //create an HTTP request
2169 
2170             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Fields");
2171 
2172             request.CookieContainer = this.AuthenticationCookie;
2173             //  request.KeepAlive = True
2174             request.AllowAutoRedirect = true;
2175 
2176             request.Method = WebRequestMethods.Http.Post;   //"POST"
2177             request.ContentType = "application/jpi; charset=utf-8"; //application/json
2178                                                                     // Set the request stream
2179             string result;
2180 
2181             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
2182             if (!AddIdentityHeader(ref request))
2183             {
2184                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
2185                 Connector.hasError = true;  //throws error
2186                 return false;
2187             }
2188 
2189             //test with encryption
2190             StringBuilder sb = new StringBuilder();
2191 
2192             sb.AppendLine("UserName: " + Connection.UserName);
2193             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
2194             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
2195             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
2196             sb.AppendLine("UniversalID: " + docObj.UniversalID);
2197             sb.AppendLine("Fields: " + fields);
2198             sb.AppendLine("Connecting via XPagesAPI");
2199 
2200             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
2201             string myString = Encoding.UTF8.GetString(b);
2202             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
2203             if (EncodedEncryptedContent.Initialize())
2204             {
2205                 Stream requestStream = null;
2206                 try
2207                 {
2208                     requestStream = request.GetRequestStream();
2209                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8))
2210                     {
2211                         writer.Write(EncodedEncryptedContent.EncodedContent);
2212                     }
2213                 }
2214                 catch (Exception e)
2215                 {
2216                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2217                     Connector.hasError = true;  //throws exception
2218                     return false;
2219                 }
2220                 finally
2221                 {
2222                     //if (requestStream != null) {
2223                     //    requestStream.Dispose();
2224                     //}
2225                 }
2226             }
2227             else
2228             {
2229                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
2230                 Connector.hasError = true; //throws exception
2231                 return false;
2232             }
2233 
2234             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
2235 
2236             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently))
2237             {
2238 
2239                 Stream responseStream = null;
2240                 try
2241                 {
2242                     responseStream = request.GetResponse().GetResponseStream();
2243                     using (StreamReader reader = new StreamReader(responseStream, true))
2244                     {
2245                         result = reader.ReadToEnd();
2246                     }
2247                 }
2248                 catch (Exception e)
2249                 {
2250                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2251                     Connector.hasError = true;  //throws exception
2252                     return false;
2253                 }
2254                 finally
2255                 {
2256                     //if (responseStream != null) {
2257                     //    responseStream.Dispose();
2258                     //}
2259                 }
2260 
2261                 if (result != null)
2262                 {
2263                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2264                     if (DecodeDecryptedContent.Initialize())
2265                     {
2266                         ArrayList arList = new ArrayList();
2267 
2268                         string[] ar = null;
2269                         bool isError = false;
2270 
2271                         string message = "";
2272                         string details = "";
2273                         string value = "";
2274                         string[] fieldnames = null;
2275                         string[] fieldvalues = null;
2276                         string[] fieldtypes = null;
2277 
2278                         string[] sep = { "||" };
2279 
2280                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2281                         arList.AddRange(ar);
2282                         foreach (string str in arList)
2283                         {
2284                             //YES/NO
2285                             if (str.StartsWith("Error: "))
2286                             {
2287                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase))
2288                                 {
2289                                     isError = true;
2290                                 }
2291                             }
2292                             if (str.StartsWith("Message: "))
2293                             {
2294                                 message = str.Replace("Message: ", "");
2295                             }
2296                             if (str.StartsWith("Details: "))
2297                             {
2298                                 details = str.Replace("Details: ", "");
2299                             }
2300 
2301                             if (str.StartsWith("FieldNames: "))
2302                             {
2303                                 value = str.Replace("FieldNames: ", "");
2304                                 if (!string.IsNullOrEmpty(value))
2305                                 {
2306                                     fieldnames = value.Split(sep, StringSplitOptions.None);
2307                                 }
2308                             }
2309 
2310                             if (str.StartsWith("FieldValues: "))
2311                             {
2312                                 value = str.Replace("FieldValues: ", "");
2313                                 if (!string.IsNullOrEmpty(value))
2314                                 {
2315                                     fieldvalues = value.Split(sep, StringSplitOptions.None);
2316                                 }
2317                             }
2318 
2319                             if (str.StartsWith("FieldTypes: "))
2320                             {
2321                                 value = str.Replace("FieldTypes: ", "");
2322                                 if (!string.IsNullOrEmpty(value))
2323                                 {
2324                                     fieldtypes = value.Split(sep, StringSplitOptions.None);
2325                                 }
2326                             }
2327 
2328                         }
2329                         FieldObject fObj = null;
2330                         if (docObj.Fields == null)
2331                         {
2332                             docObj.Fields = new SortedDictionary<string, FieldObject>();
2333                         }
2334                         for (int i = 0; i < fieldnames.Length; i++)
2335                         {
2336                             fObj = new FieldObject(fieldnames[i]);
2337                             if (fieldvalues.Length - 1 >= i)
2338                             {
2339                                 fObj.Value = fieldvalues[i];
2340                             }
2341                             if (fieldtypes.Length - 1 >= i)
2342                             {
2343                                 fObj.Type = fieldtypes[i];
2344                             }
2345 
2346                             if (docObj.Fields.ContainsKey(fObj.Name))
2347                             {
2348                                 docObj.Fields.Remove(fObj.Name);
2349                             }
2350                             docObj.Fields.Add(fObj.Name, fObj);
2351 
2352                         }
2353 
2354                         if (isError)
2355                         {
2356                             Connector.ReturnMessages.Add(message);
2357                             Connector.ReturnMessages.Add(details);
2358                             Connector.hasError = true; //throws exception
2359                             return false;
2360                         }
2361                         else
2362                         {
2363                             Connector.ReturnMessages.Add(message);
2364                             Connector.ReturnMessages.Add(details);
2365                             Connector.hasError = false; //SUCCESS
2366                         }
2367 
2368                     }
2369                     else
2370                     {
2371                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
2372                         Connector.hasError = true;  //throws exception
2373                         return false;
2374                     }
2375                 }
2376                 else
2377                 {
2378                     //no response from server
2379                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
2380                     Connector.hasError = true;  //throws exception
2381                     return false;
2382 
2383                 }
2384                 return true;
2385             }
2386             else
2387             {
2388                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
2389                 Connector.hasError = true;  //throws exception
2390                 return false;
2391             }
2392 
2393         }
2394         catch (Exception ex)
2395         {
2396             if (ex.GetType() == typeof(WebException))
2397             {
2398                 WebException webex;
2399                 webex = (WebException)ex;
2400                 if (webex.Response != null)
2401                 {
2402                     Stream stream = webex.Response.GetResponseStream();
2403                     using (StreamReader reader = new StreamReader(stream))
2404                     {
2405                         string errorResp = reader.ReadToEnd();
2406                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2407                         Connector.ReturnMessages.Add("Unable to Execute the Fields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2408                         Connector.hasError = true;  //throws exception
2409                         return false;
2410                     }
2411 
2412                 }
2413                 else
2414                 {
2415                     Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2416                     Connector.hasError = true;  //throws exception
2417                     return false;
2418                 }
2419             }
2420             else
2421             {
2422                 Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2423                 Connector.hasError = true;  //throws exception
2424                 return false;
2425             }
2426         }
2427     }
