2765                                 if (dbObj.Documents.ContainsKey(Unids[i]))
2766                                 {
2767                                     dbObj.Documents.Remove(Unids[i]);
2768                                 }
2769                                 docObj.IsInitialized = true;
2770                                 dbObj.Documents.Add(Unids[i], docObj);
2771                             }
2772                         }
2773 
