2775 
2776                         if (isError)
2777                         {
2778                             Connector.ReturnMessages.Add(message);
2779                             Connector.ReturnMessages.Add(details);
2780                             Connector.hasError = true; //throws exception
2781                             return false;
2782                         }
2783                         else
2784                         {
2785                             Connector.ReturnMessages.Add(message);
2786                             Connector.ReturnMessages.Add(details);
2787                             Connector.hasError = false; //SUCCESS
2788                         }
2789 
2790                     }
2791                     else
2792                     {
2793                         Connector.ReturnMessages.Add("Executing All Files Request - Unable to decode/decrypt the content of the response!");
2794                         Connector.hasError = true;  //throws exception
2795                         return false;
2796                     }
2797                 }
2798                 else
2799                 {
2800                     //no response from server
2801                     Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get response from XPages!");
2802                     Connector.hasError = true;  //throws exception
2803                     return false;
2804 
2805                 }
2806                 return true;
2807             }
2808             else
2809             {
2810                 Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get the Document - Authentication Issue");
2811                 Connector.hasError = true;  //throws exception
2812                 return false;
2813             }
2814 
2815         }
2816         catch (Exception ex)
2817         {
2818             if (ex.GetType() == typeof(WebException))
2819             {
2820                 WebException webex;
2821                 webex = (WebException)ex;
2822                 if (webex.Response != null)
2823                 {
2824                     Stream stream = webex.Response.GetResponseStream();
2825                     using (StreamReader reader = new StreamReader(stream))
