3204     protected internal bool ExecuteGetFileRequest(string FileURL, string LocalFilePath)
3205     {
3206         HttpWebRequest request = null;  
3207         try
3208         {
3209 
3210             if (!isInitialized)
3211             {
3212                 Connector.ReturnMessages.Add("Unable to execute the Get File Request - Not Initialized/Connected To Server");
3213                 Connector.hasError = true;
3214                 return false;
3215             }
3216 
3217             request = request = (HttpWebRequest)WebRequest.Create(FileURL);
3218         
3219             request.CookieContainer = AuthenticationCookie;
3220             request.AllowAutoRedirect = true;
3221             request.Method = WebRequestMethods.Http.Get;
3222             const int BUFFER_SIZE = 16 * 1024;
3223            byte[] buffer = new byte[BUFFER_SIZE - 1];
3224             int bytesRead;
3225 
3226             using (var outputFileStream = File.Create(LocalFilePath, BUFFER_SIZE))
3227             {
3228                 using (var response2 = request.GetResponse())
3229                 {
3230                     using (var responseStream = response2.GetResponseStream())
3231                     {
3232                         while ((bytesRead = responseStream.Read(buffer, 0, buffer.Length)) > 0)
3233                         {
3234                             outputFileStream.Write(buffer, 0, bytesRead);
3235                         }
3236                     }
3237                 }
3238             }
3239             return true;
3240         }
3241         catch (Exception ex)
3242         {
3243             if (ex.GetType() == typeof(WebException))
3244             {
3245                 WebException webex;
3246                 webex = (WebException)ex;
3247                 if (webex.Response != null)
3248                 {
3249                     Stream stream = webex.Response.GetResponseStream();
3250                     using (StreamReader reader = new StreamReader(stream))
3251                     {
3252                         string errorResp = reader.ReadToEnd();
3253                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
3254                         Connector.ReturnMessages.Add("Unable to Execute the Get File Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
3255                         Connector.hasError = true;  //throws exception
3256                         return false;
3257                     }
3258 
3259                 }
3260                 else
3261                 {
3262                     Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
3263                     Connector.hasError = true;  //throws exception
3264                     return false;
3265                 }
3266             }
3267             else
3268             {
3269                 Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
3270                 Connector.hasError = true;  //throws exception
3271                 return false;
3272             }
3273         }
