29     private bool AddIdentityHeader(ref HttpWebRequest request) {
30         try {
31             if (request != null) {
32                 string XPIdentity = "JPI$XP@ges!C0nn3nt0r|Id3nTity@Request";
33                 if (!string.IsNullOrEmpty(Connection.XPIdentity)) {
34                     XPIdentity = Connection.XPIdentity;
35                 }
36                 //  Const XPIdentity As String = "JPI$XP@ges!C0nn3nt0r|Id3nTity@Request"
37 
38                 Encryptor EncodedEncryptedContent = new Encryptor(XPIdentity, true, ref Connection);
39                 if (EncodedEncryptedContent.Initialize()) {
40                     request.Headers.Add("Identity", EncodedEncryptedContent.EncodedContent);
41                     return true;
42                 }
43             } else {
44                 //request is nothing ' error added in calling function
45             }
46             return false;
47         } catch (Exception ex) {
48             Connector.ReturnMessages.Add("Unable to add Identity Header to XPages Request");
49             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
50             Connector.HasError = true; //throws exception
51 
52             return false;
53         }
54     }
55 
56     #endregion Private Methods
57 
58     #region Protected Internal Methods
59 
60     protected internal bool Initialize() {
61         HttpWebRequest request = null;
62 
63         // use the XPagesAPI ReturnMessages & hasError
64         try {
65             //ServicePointManager.Expect100Continue = false;
66             ServicePointManager.Expect100Continue = true;
67             //  ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls;
68             ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12 | SecurityProtocolType.Tls11 | SecurityProtocolType.Tls;
69             ServicePointManager.DefaultConnectionLimit = 9999;
70 
71             // Cookie container to store authentication cookie
72             CookieContainer cookies = new CookieContainer();
73 
74             //create an HTTP request
75             //Login - check for "/names.nsf?Login"
76             string serverURL = "";
77             serverURL = Connection.ServerURL;
78             if (!serverURL.Contains("/names.nsf?Login")) {
79                 serverURL = serverURL + "/names.nsf?Login";
80             }
81 
82             request = (HttpWebRequest)WebRequest.Create(serverURL);
83 
84             // Prepare HTTP request
85             request.Method = WebRequestMethods.Http.Post;
86             // "POST"
87             request.AllowAutoRedirect = false;
88             request.ContentType = "application/x-www-form-urlencoded";
89             request.CookieContainer = cookies;
90             //  request.UserAgent = "Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/54.0.2840.99 Safari/537.36"
91             // Prepare POST body
92             string post = "Username=" + Uri.EscapeDataString(Connection.UserName) + "&Password=" + Uri.EscapeDataString(Connection.Password);
93             byte[] bytes = Encoding.ASCII.GetBytes(post);
94 
95             // Write data to request
96             request.ContentLength = bytes.Length;
97             Stream streamOut = request.GetRequestStream();
98             streamOut.Write(bytes, 0, bytes.Length);
99             streamOut.Close();
100 
101             //' Get response
102             HttpWebResponse response = (HttpWebResponse)request.GetResponse();
103 
104             // Check if we are authenticated properly
105             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
106                 foreach (Cookie cooky in response.Cookies) {
107                     cookies.Add(cooky);
108                 }
109 
110                 this.AuthenticationCookie = cookies;
111 
112                 if (response.Cookies != null && response.Cookies.Count > 0) {
113                     Connector.ReturnMessages.Add(Connection.UserName + " Successfully Authenticated On Domino Server " + Connection.ServerURL);
114                     Connector.HasError = false;
115                 } else {
116                     Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
117                     Connector.HasError = true;
118                     //thows exception
119                 }
120             } else {
121                 Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
122                 Connector.HasError = true;
123                 //thows exception
124             }
125             isInitialized = true;
126             return true;
127         } catch (Exception ex) {
128             Connector.ReturnMessages.Add(Connection.UserName + " was unable to authenticate to the domino server " + Connection.ServerURL);
129             Connector.ReturnMessages.Add(Common.GetErrorInfo(ex));
130             Connector.HasError = true;
131             //throws exception
132             isInitialized = false;
133             return false;
134         }
135     }
136 
137     protected internal bool ExecuteSessionRequest(string WebServiceURL) {
138         HttpWebRequest request = null;
139 
140         try {
141             if (!isInitialized) {
142                 Connector.ReturnMessages.Add("Unable to execute the Session Request - Not Initialized/Connected To Server");
143                 Connector.HasError = true;
144                 return false;
145             }
146 
147             //ServicePointManager.Expect100Continue = false;
148 
149             //create an HTTP request
150             // WebURL use this without ?CheckOut or ?CheckIn
151 
152             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Session");
153             // ?CheckOut
154 
155             request.CookieContainer = this.AuthenticationCookie;
156             //  request.KeepAlive = True
157             request.AllowAutoRedirect = true;
158 
159             request.Method = WebRequestMethods.Http.Post;           //"POST"
160             request.ContentType = "application/jpi; charset=utf-8";         //application/json
161                                                                             // Set the request stream
162             string result;
163 
164             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
165             if (!AddIdentityHeader(ref request)) {
166                 Connector.ReturnMessages.Add("Unable to Execute Session Request : Identity Header could not be added!");
167                 Connector.HasError = true;
168                 return false;
169             }
170 
171             //test with encryption
172             StringBuilder sb = new StringBuilder();
173 
174             // User Name
175             sb.AppendLine("UserName : " + Connection.UserName);
176             sb.AppendLine("Connecting via XPagesAPI");
177 
178             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
179             string myString = Encoding.UTF8.GetString(b);
180             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
181             if (EncodedEncryptedContent.Initialize()) {
182                 Stream requestStream = null;
183                 try {
184                     requestStream = request.GetRequestStream();
185                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
186                         writer.Write(EncodedEncryptedContent.EncodedContent);
187                     }
188                 } catch (Exception e) {
189                     Connector.ReturnMessages.Add("Unable to execute the Session Request : " + Common.GetErrorInfo(e));
190                     Connector.HasError = true;  //throws exception
191                     return false;
192                 } finally {
193                     //if (requestStream != null) {
194                     //    requestStream.Dispose();
195                     //}
196                 }
197             } else {
198                 Connector.ReturnMessages.Add("Executing Session Request - Unable to encode/encrypt the content of the request!");
199                 Connector.HasError = true; //throws exception
200                 return false;
201             }
202 
203             HttpWebResponse response = (HttpWebResponse)request.GetResponse();
204             //can give webexception error 500
205 
206             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
207                 Stream responseStream = null;
208                 try {
209                     responseStream = request.GetResponse().GetResponseStream();
210                     using (StreamReader reader = new StreamReader(responseStream, true)) {
211                         result = reader.ReadToEnd();
212                     }
213                 } catch (Exception e) {
214                     Connector.ReturnMessages.Add("Unable to execute the Session Request : " + Common.GetErrorInfo(e));
215                     Connector.HasError = true;  //throws exception
216                     return false;
217                 } finally {
218                     //if (responseStream != null) {
219                     //    responseStream.Dispose();
220                     //}
221                 }
222 
223                 if (result != null) {
224                     //check if the page returned is not just the login page - we need to get encoded & encrypted content here - check if start with <!
225                     if (result.StartsWith("<!")) {
226                         //possible wrong password !
227                         Connector.ReturnMessages.Add("Executing Session Request -  Unable to get the session - Authentication/Password Issue");
228                         Connector.HasError = true;
229                         //throws exception
230                         return false;
231                     }
232 
233                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
234                     if (DecodeDecryptedContent.Initialize()) {
235                         ArrayList arList = new ArrayList();
236 
237                         string[] ar = null;
238                         bool isError = false;
239 
240                         string message = "";
241                         string details = "";
242 
243                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
244                         arList.AddRange(ar);
245                         foreach (string str in arList) {
246                             //YES/NO
247                             if (str.StartsWith("Error: ")) {
248                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
249                                     isError = true;
250                                 }
251                             }
252                             if (str.StartsWith("Message: ")) {
253                                 message = str.Replace("Message: ", "");
254                             }
255                             if (str.StartsWith("Details: ")) {
256                                 details = str.Replace("Details: ", "");
257                             }
258                         }
259                         if (isError) {
260                             Connector.ReturnMessages.Add(message);
261                             Connector.ReturnMessages.Add(details);
262                             Connector.HasError = true;
263                             //throws exception
264                             return false;
265                         } else {
266                             Connector.ReturnMessages.Add(message);
267                             Connector.ReturnMessages.Add(details);
268                             Connector.HasError = false;
269                             //SUCCESS
270                         }
271                     } else {
272                         Connector.ReturnMessages.Add("Executing Session Request - Unable to decode/decrypt the content of the response!");
273                         Connector.HasError = true;
274                         //throws exception
275                         return false;
276                     }
277                 } else {
278                     //no response from server
279                     Connector.ReturnMessages.Add("Executing Session Request -  Unable to get response from XPages!");
280                     Connector.HasError = true;
281                     //throws exception
282                     return false;
283                 }
284                 return true;
285             } else {
286                 Connector.ReturnMessages.Add("Executing Session Request -  Unable to get the session - Authentication Issue");
287                 Connector.HasError = true;
288                 //throws exception
289                 return false;
290             }
291         } catch (Exception ex) {
292             if (ex.GetType() == typeof(WebException)) {
293                 WebException webex;
294                 webex = (WebException)ex;
295                 if (webex.Response != null) {
296                     Stream stream = webex.Response.GetResponseStream();
297                     using (StreamReader reader = new StreamReader(stream)) {
298                         string errorResp = reader.ReadToEnd();
299                         // errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\r\\n\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\r\\n", Environment.NewLine);
300                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
301                         Connector.ReturnMessages.Add("Unable to Execute the Session Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
302                         Connector.HasError = true;
303                         //throws exception
304                         return false;
305                     }
306                 } else {
307                     Connector.ReturnMessages.Add("Unable to Execute the Session Request : " + Common.GetErrorInfo(ex));
308                     Connector.HasError = true;
309                     //throws exception
310                     return false;
311                 }
312             } else {
313                 Connector.ReturnMessages.Add("Unable to Execute the Session Request : " + Common.GetErrorInfo(ex));
314                 Connector.HasError = true;
315                 //throws exception
316                 return false;
317             }
318         }
319     }
320 
321     protected internal bool ExecuteDatabaseRequest(string WebServiceURL, string ServerName, string dbFilePath, string dbReplicationId, DatabaseObject dbObj) {
322         HttpWebRequest request = null;
323 
324         try {
325             if (!isInitialized) {
326                 Connector.ReturnMessages.Add("Unable to execute the Database Request - Not Initialized/Connected To Server");
327                 Connector.HasError = true;
328                 return false;
329             }
330 
331             //  ServicePointManager.Expect100Continue = false;
332 
333             //create an HTTP request
334 
335             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Database");
336 
337             request.CookieContainer = this.AuthenticationCookie;
338             //  request.KeepAlive = True
339             request.AllowAutoRedirect = true;
340 
341             request.Method = WebRequestMethods.Http.Post;   //"POST"
342             request.ContentType = "application/jpi; charset=utf-8"; //application/json
343                                                                     // Set the request stream
344             string result;
345 
346             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
347             if (!AddIdentityHeader(ref request)) {
348                 Connector.ReturnMessages.Add("Unable to Execute Database Request : Identity Header could not be added!");
349                 Connector.HasError = true;  //throws error
350                 return false;
351             }
352 
353             //test with encryption
354             StringBuilder sb = new StringBuilder();
355 
356             sb.AppendLine("UserName: " + Connection.UserName);
357             sb.AppendLine("ServerName: " + ServerName);
358             sb.AppendLine("FilePath: " + dbFilePath);
359             sb.AppendLine("ReplicationID: " + dbReplicationId);
360 
361             sb.AppendLine("Connecting via XPagesAPI");
362 
363             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
364             string myString = Encoding.UTF8.GetString(b);
365             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
366             if (EncodedEncryptedContent.Initialize()) {
367                 Stream requestStream = null;
368                 try {
369                     requestStream = request.GetRequestStream();
370                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
371                         writer.Write(EncodedEncryptedContent.EncodedContent);
372                     }
373                 } catch (Exception e) {
374                     Connector.ReturnMessages.Add("Unable to execute the Database Request : " + Common.GetErrorInfo(e));
375                     Connector.HasError = true;  //throws exception
376                     return false;
377                 } finally {
378                     //if (requestStream != null) {
379                     //    requestStream.Dispose();
380                     //}
381                 }
382             } else {
383                 Connector.ReturnMessages.Add("Executing Database Request - Unable to encode/encrypt the content of the request!");
384                 Connector.HasError = true; //throws exception
385                 return false;
386             }
387 
388             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
389 
390             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
391                 Stream responseStream = null;
392                 try {
393                     responseStream = request.GetResponse().GetResponseStream();
394                     using (StreamReader reader = new StreamReader(responseStream, true)) {
395                         result = reader.ReadToEnd();
396                     }
397                 } catch (Exception e) {
398                     Connector.ReturnMessages.Add("Unable to execute the Database Request : " + Common.GetErrorInfo(e));
399                     Connector.HasError = true;  //throws exception
400                     return false;
401                 } finally {
402                     //if (responseStream != null) {
403                     //    responseStream.Dispose();
404                     //}
405                 }
406 
407                 if (result != null) {
408                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
409                     if (DecodeDecryptedContent.Initialize()) {
410                         ArrayList arList = new ArrayList();
411 
412                         string[] ar = null;
413                         bool isError = false;
414 
415                         string message = "";
416                         string details = "";
417                         string value = "";
418 
419                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
420                         arList.AddRange(ar);
421                         foreach (string str in arList) {
422                             //YES/NO
423                             if (str.StartsWith("Error: ")) {
424                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
425                                     isError = true;
426                                 }
427                             }
428                             if (str.StartsWith("Message: ")) {
429                                 message = str.Replace("Message: ", "");
430                             }
431                             if (str.StartsWith("Details: ")) {
432                                 details = str.Replace("Details: ", "");
433                             }
434                             if (str.StartsWith("ServerName: ")) {
435                                 value = str.Replace("ServerName: ", "");
436                                 if (!string.IsNullOrEmpty(value)) {
437                                     dbObj.ServerName = value;
438                                 }
439                             }
440                             if (str.StartsWith("FilePath: ")) {
441                                 value = str.Replace("FilePath: ", "");
442                                 if (!string.IsNullOrEmpty(value)) {
443                                     dbObj.FilePath = value;
444                                 }
445                             }
446                             if (str.StartsWith("ReplicationID: ")) {
447                                 value = str.Replace("ReplicationID: ", "");
448                                 if (!string.IsNullOrEmpty(value)) {
449                                     dbObj.ReplicationID = value;
450                                 }
451                             }
452 
453                             if (str.StartsWith("Title: ")) {
454                                 value = str.Replace("Title: ", "");
455                                 if (!string.IsNullOrEmpty(value)) {
456                                     dbObj.Title = value;
457                                 }
458                             }
459 
460                             if (str.StartsWith("Template: ")) {
461                                 value = str.Replace("Template: ", "");
462                                 if (!string.IsNullOrEmpty(value)) {
463                                     dbObj.Template = value;
464                                 }
465                             }
466 
467                             if (str.StartsWith("Size: ")) {
468                                 value = str.Replace("Size: ", "");
469                                 if (!string.IsNullOrEmpty(value)) {
470                                     dbObj.Size = value;
471                                 }
472                             }
473 
474                             if (str.StartsWith("URL: ")) {
475                                 value = str.Replace("URL: ", "");
476                                 if (!string.IsNullOrEmpty(value)) {
477                                     dbObj.Url = value;
478                                 }
479                             }
480                         }
481 
482                         if (isError) {
483                             Connector.ReturnMessages.Add(message);
484                             Connector.ReturnMessages.Add(details);
485                             Connector.HasError = true; //throws exception
486                             return false;
487                         } else {
488                             Connector.ReturnMessages.Add(message);
489                             Connector.ReturnMessages.Add(details);
490                             Connector.HasError = false; //SUCCESS
491                         }
492                     } else {
493                         Connector.ReturnMessages.Add("Executing Database Request - Unable to decode/decrypt the content of the response!");
494                         Connector.HasError = true;  //throws exception
495                         return false;
496                     }
497                 } else {
498                     //no response from server
499                     Connector.ReturnMessages.Add("Executing Database Request -  Unable to get response from XPages!");
500                     Connector.HasError = true;  //throws exception
501                     return false;
502                 }
503                 return true;
504             } else {
505                 Connector.ReturnMessages.Add("Executing Database Request -  Unable to get the Database - Authentication Issue");
506                 Connector.HasError = true;  //throws exception
507                 return false;
508             }
509         } catch (Exception ex) {
510             if (ex.GetType() == typeof(WebException)) {
511                 WebException webex;
512                 webex = (WebException)ex;
513                 if (webex.Response != null) {
514                     Stream stream = webex.Response.GetResponseStream();
515                     using (StreamReader reader = new StreamReader(stream)) {
516                         string errorResp = reader.ReadToEnd();
517                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
518                         Connector.ReturnMessages.Add("Unable to Execute the Database Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
519                         Connector.HasError = true;  //throws exception
520                         return false;
521                     }
522                 } else {
523                     Connector.ReturnMessages.Add("Unable to Execute the Database Request : " + Common.GetErrorInfo(ex));
524                     Connector.HasError = true;  //throws exception
525                     return false;
526                 }
527             } else {
528                 Connector.ReturnMessages.Add("Unable to Execute the Database Request : " + Common.GetErrorInfo(ex));
529                 Connector.HasError = true;  //throws exception
530                 return false;
531             }
532         }
533     }
